INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1732, 'Gerente de Saúde, Segurança e Meio Ambiente', 'Gerencia, planeja e implementa políticas de saúde, segurança e meio ambiente e acompanha procedimentos de inspeções em locais, instalações e equipamentos para determinar fatores e riscos de acidentes. Administra auditorias ambientais no atendimento as conformidades legais e participa no planejamento de campanhas de segurança do trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/gerente-de-saude-seguranca-e-meio-ambiente/');
