INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (19, 'Advogado', 'Atua na defesa dos direitos ou interesses de indivíduos, empresas e instituições em qualquer instância, avalia os casos, provas orais e documentais, recomenda procedimentos e encaminha processos para justiça. Realiza pesquisas jurídicas, elabora peças iniciais, contestações, petições e recursos, acompanha e comparece em audiências.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/advogado/');
