INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (29, 'Advogado Imobiliário', 'Responsável pela assessoria jurídica na aquisição de fazendas, residências, apartamentos, novos terrenos e compra de ativos em diversas áreas, adequando convenções condominiais às disposições da lei.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/advogado-imobiliario/');
