INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (35, 'Agente de Aeroporto', 'Trabalha em aeroportos com serviços de check-in e retaguarda operacional, embarque e desembarque de passageiros, serviço de atendimento especial a passageiros VIP ou que requeira atenção especial (menores desacompanhados, idosos, gestantes, etc), setor de bagagens etc.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/agente-de-aeroporto/');
