INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (40, 'Ajudante de Caldeiraria', 'Auxilia na fabricação e reparo de caldeiras, tanques e outros recipientes de ferro ou aço, por meio da leitura e interpretação de desenhos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-caldeiraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (41, 'Ajudante de Carga e Descarga', 'Prepara cargas e descargas de mercadorias, movimenta mercadorias em transportes, coleta e entrega encomendas. Recebe e solicita informações, autorizações e orientações de transporte, embarque e desembarque de mercadorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-carga-e-descarga/');
