INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (46, 'Ajudante de Estamparia', 'Auxilia no acabamento, tingimento e estamparia de tecidos, fibras soltas e fios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-estamparia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (47, 'Ajudante de Fundição', 'Auxilia no preparo de panela de vazamento de metal líquido na transformação de peças, de acordo com o modelo piloto. Controla temperaturas adequadas para obter materiais de boa qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-fundicao/');
