INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (50, 'Ajudante de Lubrificação', 'Auxilia na lubrificação de máquinas e equipamentos, avalia condições de funcionamento e realiza ajustes e regulagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-lubrificacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (51, 'Ajudante de Marceneiro', 'Auxilia na confecção, restauração e acabamento de produtos de madeira e derivados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-marceneiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (52, 'Ajudante de Metalúrgico', 'Realiza cortes de alumínio, manusea lixadeira, faz medidas e atua com policortes e medições. Realiza leitura e interpretação de desenhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-metalurgico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (53, 'Ajudante de Motorista', 'Prepara, entrega e manuseia cargas e descargas de mercadorias. Repara embalagens danificadas e controla a qualidade dos serviços prestados. Opera equipamentos de carga e descarga, além de realizar atividades de limpeza e conservação nos locais de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-motorista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (54, 'Ajudante de Obras', 'Auxilia pedreiros, carpinteiros e operadores de equipamentos pesados. Carrega e descarrega materiais de construção, prepara canteiros de obras e limpa áreas de trabalho. Faz pequenas manutenções nos equipamentos, limpa máquinas e ferramentas, verifica condições de uso e repara eventuais defeitos mecânicos nas mesmas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (55, 'Ajudante de Off-set', 'Auxilia no abastecimento, ajuste e manutenção de máquina de impressão off-set.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-off-set/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (56, 'Ajudante de Pizzaiolo', 'Auxilia na montagem e preparo de massas e recheios para pizzas doces e salgadas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-pizzaiolo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (57, 'Ajudante de Serralheiro', 'Auxilia nas rotinas de corte, acabamento, forja e solda de estruturas, armações, portas, entre outros materiais de ferro ou alumínio.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-serralheiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (59, 'Ajudante de Vidraceiro', 'Auxilia na montagem, instalação e manutenção de vidros, vitrais e espelhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-de-vidraceiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (60, 'Ajudante Geral', 'Auxilia no planejamento, controle e programação da produção, monitora estoque de materiais e acompanha ordens de fabricação. Realiza carga e descarga de mercadorias e serviços de manutenção e limpeza.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-geral/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (61, 'Ajudante Operacional', 'Descarrega cargas, acompanha e confere os volumes, apontando eventuais divergências entre documento de transporte e quantidades descarregadas. Efetua carregamento de mercadorias em caminhões, auxilia na coleta, separação e manuseio das mercadorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajudante-operacional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (62, 'Ajustador Mecânico', 'Confecção de ferramentas, peças e dispositivos especiais. Presta assistência técnica e executa reparos de peças e serviços em geral de ajustagem. Opera máquinas de eletro-erosão, lapidação e brunimento. Efetua medições e traçagem. Afia ferramentas de corte. Executa manutenção preventiva e corretiva de máquinas operatrizes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ajustador-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (63, 'Alfaiate', 'Projeta e modela confecções de roupas sob encomenda, desenvolve peças-piloto e costura roupas em tecidos, couros ou peles.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/alfaiate/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (64, 'Alinhador', 'Elabora e realiza planos de manutenção em sistemas
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (65, 'Almoxarife', 'Realiza o recebimento, movimentação e estocagem de matérias-primas e produtos. Recebe e confere as notas fiscais de entrada dos materiais adquiridos, verifica quantidade, descrição e as condições gerais dos materiais e embalagens.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/almoxarife/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (66, 'Alpinista Industrial', 'Realiza serviços em altura na limpeza e pintura de edifícios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/alpinista-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (67, 'Amarrador', 'Realiza amarração de veículos e cargas para garantir a segurança do transporte.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/amarrador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (68, 'Analista Administrativo', 'Analisa controles e métodos, elabora relatórios de acompanhamento da área administrativa, participa do planejamento, organização e controle de fluxos de trabalhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-administrativo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (69, 'Analista Administrativo de Obras', 'Faz relatórios periódicos para manter o engenheiro responsável pela obra informado sobre seu andamento e principais eventos ocorridos. Avalia a viabilidade das ações da empresa na realização de obras, como aluguel, aquisição de equipamentos, local para contratação de colaboradores e demais estruturas necessárias. Apoio as atividades de gerenciamento de pessoas, verifica as condições do ambiente de trabalho e possíveis instalações para estadia e refeições para os colaboradores que irão trabalhar nas obras da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-administrativo-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (70, 'Analista Ambiental', 'Administra processos para obtenção de licenças e autorizações ambientais municipal, estadual e federal. Elabora diagnósticos, pareceres e relatórios de controle para os órgãos ambientais e realiza o atendimento aos agentes de fiscalização. Ministra treinamentos e desenvolve os programas de educação ambiental.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-ambiental/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (71, 'Analista Comercial', 'Analisa estratégias e atividades de concorrentes e elabora projeções de vendas e relatórios de tendências mercadológicas para subsidiar informações à gerência na tomada de decisão. Desenvolve orçamentos e presta suporte à equipe de vendas no esclarecimento de dúvidas sobre produtos e serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (72, 'Analista Contábil', 'Realiza análise, classificação e conciliação contábeis e financeiras, desenvolve obrigações acessórias e apura impostos diretos e indiretos. Elabora balanços e balancetes e relatórios contábeis, por meio de análises de lançamentos e despesas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (73, 'Analista de Almoxarifado', 'Atua com expedição e recebimento, controle, manutenção, limpeza, organização de máquinas, ferramentas e estoque. Acompanha a carga e descarga, faz interpretação de nota fiscal e conferência de recebimento junto à ordem de compra.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-almoxarifado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (74, 'Analista de Apuração de Resultados', 'Analisa dados sobre preços, volume de vendas e faturamento, a fim de elaborar relatórios no reporte de informações às tomadas de decisões para os negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-apuracao-de-resultados/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (75, 'Analista de Arquitetura', 'Acompanha o desenvolvimento de projetos para identificar as necessidades dos clientes, levanta medidas, define materiais, elabora orçamentos e fiscaliza obras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-arquitetura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (76, 'Analista de Assistência Técnica', 'Acompanha as atividades de instalação, reparo e troca de peças de equipamentos ou máquinas mecânicas, elétricas e eletrônicas. Elabora relatórios técnicos sobre níveis de serviços, a fim de identificar oportunidades de melhorias e redução de custos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-assistencia-tecnica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (77, 'Analista de Assuntos Regulatórios', 'Prepara, analisa e renova a documentação legal da empresa para registro de produtos na ANVISA e outros órgãos reguladores, elaborando dossiês referentes à registro, alterações e pós-registro para cumprir as exigências. Responde exigências formuladas pelas agências reguladoras. Altera registro de produtos elaborando documentos necessários, para adequar ou melhorar o produto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-assuntos-regulatorios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (78, 'Analista de Atendimento', 'Realiza atendimento ao cliente, análise de resultado e relatórios, relacionamento com os parceiros participantes da campanha. Recebe, confere e edita planilhas, faz a atualização de mailing, apoio e monitoramento da operação. Cria e administra a escalas de trabalho, folgas e férias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-atendimento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (79, 'Analista de Ativo Fixo', 'Mantém controle de todo o ativo imobilizado da Empresa, recebe as aquisições de bens e determina a sua contabilização, classificação e a respectiva numeração, conforme controles diversos. Efetua a inclusão das aquisições e as respectivas baixas necessárias. Elabora o fechamento mensal de controle do ativo fixo. Realiza o inventário físico do ativo imobilizado.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (80, 'Analista de Auditoria', 'Realiza auditorias internas e externas e mapeia processos para identificar riscos, irregularidades e divergências e recomendar soluções, assegurando a correta aplicação das normas da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-auditoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (81, 'Analista de Automação', 'Desenvolve projetos de automação industrial, levanta requisitos técnicos e funcionais e acompanha a instalação, construção e modificação de máquinas, equipamentos e dispositivo de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-automacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (82, 'Analista de Back Office', 'Analisa, confere e oferece o tratamento adequado a todas as ocorrências geradas pelo cliente e que não foram resolvidas pela equipe de SAC. Acompanha os pedidos não finalizados garantindo a execução dos prazos pré - estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-back-office/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (83, 'Analista de Banco de Dados', 'Administra banco de dados, envolvendo manutenção de estrutura e implementação de novos processos de software, métodos de acesso e dimensionamento de hardware e mantém segurança conforme políticas da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-banco-de-dados/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (84, 'Analista de Benefícios', 'Administra todo o processo de movimentação de benefícios, como convênio médico, odontológico, vale transporte e refeição, entre outros, envolvendo controle de pedidos, lançamento de dados em sistema e elaboração de normas e políticas de benefícios. Análise de parcerias e negociação com fornecedores, na busca por melhores taxas e práticas de mercado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-beneficios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (85, 'Analista de Business Intelligence', 'Analisa e levanta requisitos, define modelagem e modelos de dados a serem tratados e transformados e estuda a melhor ferramenta para extração de dados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-business-intelligence/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (86, 'Analista de Cadastro', 'Realiza cadastro de novos produtos, atualiza cadastros, faz acompanhamento e ativação de novos fornecedores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-cadastro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (88, 'Analista de Campo', 'Faz análises de parâmetros analíticos e de processo. Elabora relatórios diários, de inspeção e de performance. Identifica oportunidades de melhorias e suporte de vendas. Acompanha resultados com iniciativa para ajuste dos parâmetros de processo e analítico e faz controle de estoque de produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-campo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (89, 'Analista de Captação de Recursos', 'Identifica no mercado novas oportunidade de captação de recursos com pessoas físicas e jurídicas para projetos culturais, sociais, socioeducativos etc. Planeja campanhas de captação e desenvolve o relacionamento com doadores, a fim de ampliar a receita e fidelizar parcerias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-captacao-de-recursos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (90, 'Analista de Cargos e Salários', 'Atua com implantação, manutenção e desenvolvimento de toda a política de cargos e salários. Elabora descrição de cargos, realiza análises e estudos relacionados a estrutura de cargos e pesquisa remuneração. Analisa as estatísticas de informações salariais, propõe políticas de cargos e remunerações e efetua a manutenção de tabelas salariais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-cargos-e-salarios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (91, 'Analista de Cobrança', 'Analisa operações de cobrança, acompanha o processo de liquidação, localiza pagamentos efetuados, providencia baixa no sistema e identifica pendências. Atualiza o histórico de clientes e confere movimento de extratos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (92, 'Analista de Comércio Exterior', 'Planeja e acompanha as atividades de importação e exportação, analisa e controla o fluxo de documentos das operações, regimes alfandegários e cambial. Determina o meio de transporte mais adequado, analisa tendências de negócios, identifica oportunidade de novos mercados e participa no fechamento de contratos de compra e venda internacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-comercio-exterior/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (93, 'Analista de Compliance', 'Presta suporte no planejamento dos projetos estratégicos da organização em questões relacionadas a compliance. Implanta as normas e procedimentos que respaldem a estrutura de gestão de riscos. Revisa os contratos de TI, define e implanta a gestão de licenças de softwares. Realiza a gestão de conformidade com regulamentações, monitoramento e reporte de desvios de SLA. Audita periodicamente a conformidade dos processos às regulamentações internas e externas. Atuarem parceria com a área de processos no tratamento das questões de compliance dos processos implantados, desde o desenho até as eventuais manutenções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-compliance/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (94, 'Analista de Compras', 'Realiza compras de materiais, suprimentos. Negocia com os fornecedores, visando as melhores condições de preços, qualidade, prazos de pagamento e entrega. Desenvolve e acompanha relatórios para análise da performance de fornecedores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (95, 'Analista de Comunicação', 'Elabora planejamento estratégico, propostas, relatórios, produtos e serviços de comunicação em conjunto com a coordenação de comunicação. Atende à imprensa e produz conteúdo textual e visual para internet, intranet e demais canais de comunicação. Seleciona e distribui clippings sobre temas de interesse. Estabelece relacionamento com a mídia de interesse. Monitora a imagem institucional e acompanha temas ambientais na mídia, prepara conteúdos especiais para publicações. Identifica fornecedores e prestadores de serviços de comunicação e elabora termos de referência e documentos necessários para a contratação eventual. Supervisiona o trabalho de consultores e fornecedores de produtos e serviços de comunicação. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-comunicacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (96, 'Analista de Comunicação e Marketing', 'Desenvolve e implementa ações de Marketing, incluindo pesquisas de mercado, campanhas publicitárias e promocionais. Desenvolve ações de endomarketing e comunicação interna. Elabora notícias sobre acontecimentos para divulgação no site da empresa e redes sociais. Acompanha processos de produção gráfica de pesquisas publicitárias. Organiza eventos e presta assistência ao setor comercial.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-comunicacao-e-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (97, 'Analista de Comunicação Interna', 'Desenvolve comunicados internos, criar peças de comunicação e diagrama textos em comunicados, e-mail marketing e newsletter. Busca e desenvolve pautas para o público interno e externo, valida as informações e redige textos jornalísticos. Edita imagens e vídeos, atualiza conteúdo em mídias sociais, auxilia no desenvolvimento de campanhas de comunicação interna e realiza o planejamento de eventos institucionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-comunicacao-interna/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (98, 'Analista de Contas', 'Prospecção de clientes, suporte a equipe, promove ações de relacionamento com os clientes, informa os clientes sobre os processos em andamento, agenda visitas, controle, conferência de documentos, cadastro, elaboração de relatórios, gráficos comparativos, digitação, tabulação de dados, etc.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-contas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (99, 'Analista de Contas a Pagar', 'Faz recebimento e conferência de documentos quanto a legislação fiscal, tributária e procedimentos internos da empresa. Fazer análise de lançamentos (validação e correção). Fazer envio de documentos a tesouraria e lançamento no sistema dos documentos entregues pelo departamento pessoal. Responsável pela retenção de impostos (IR, PIS, COFINS, INSS e ISS) atuando na sua identificação, classificação e controle.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-contas-a-pagar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (100, 'Analista de Contas a Pagar e Receber', 'Controla o pagamento dos compromissos financeiros da empresa e liquidação de títulos, verifica vencimentos, emite borderôs e realiza cobrança de contas em atraso. Acompanha fluxo de caixa e elabora relatórios da posição financeira da organização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-contas-a-pagar-e-receber/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (101, 'Analista de Contas a Receber', 'Acompanha a liquidação de títulos e vencimentos, realiza baixa em sistema e cobrança de débitos em aberto. Realiza conciliação de retornos e pagamentos, confecciona borderôs, emite extratos bancários e realiza negociação e recuperação junto aos inadimplentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-contas-a-receber/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (102, 'Analista de Contas Médicas', 'Controla e participa dos trabalhos de análise e conciliação de contas, confere saldos apresentados, localiza e emenda os possíveis erros, para assegurar a correção das operações contábeis. Analisa e audita todas as guias médicas existentes, visando minimizar o impacto das glosas médicas no faturamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-contas-medicas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (103, 'Analista de Conteúdo', 'Atua na elaboração de releases e produção de conteúdo, alimenta site e gerencia  redes sociais. Analisa, redige e modifica o conteúdo a ser publicado no site. Atualiza textos e imagens. Apoia projetos e desenhos que envolvam estrutura do banco de dados e das consultas no site.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-conteudo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (104, 'Analista de Contratos', 'Recebe, analisa e controla a documentação para elaboração de contrato. Confecciona, elabora e revisa contratos. Atua com discussão de cláusulas com jurídico, controle de prazos e assinaturas internas nos instrumentos jurídico. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-contratos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (105, 'Analista de Control Desk', 'Realiza a análise e apresentação de relatórios estatísticos de tráfego de ligações, faz acompanhamento de resultados das filiais, report diário e semanal e acompanhamento de incidentes sistêmicos. Absenteísmo. Controle dos problemas técnicos na central, apoio aos gerentes, coordenação e supervisor das operações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-control-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (106, 'Analista de Controladoria', 'Analisa resultados financeiros, elabora balanços e balancetes, acompanha o desempenho econômico e participa no planejamento estratégico para identificar desvios e propor soluções. Define o controle orçamentário e contábil adequado à estratégia dos negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-controladoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (107, 'Analista de Controle de Qualidade', 'Atua em diversas áreas ligadas ao controle de qualidade dentro de uma indústria ou laboratório. Fornece suporte no desenvolvimento, na produção e no acompanhamento de produtos e processos. Realiza inspeções, preenche relatórios, identifica produtos ou serviços que não estão dentro do padrão exigido, solicita aprovação de produtos e promove treinamento de funcionários.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (108, 'Analista de Controles Internos', 'Elabora, revisa e aprova políticas e normativas internas, analisa riscos e controles para garantir segurança e confiabilidade e mapeia fluxo de atividades, a fim de atender o cumprimento dos processos operacionais, evitar a ocorrência de erros ou irregularidades e alcançar objetivos e metas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-controles-internos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (109, 'Analista de Credenciamento', 'Analisa propostas de credenciamento e extensões, negocia reajustes das redes credenciadas, realizar manutenção de cadastro em sistema e realiza visitas técnicas a novos credenciados sob supervisão e direcionamento do líder.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-credenciamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (110, 'Analista de Crédito', 'Realiza o levantamento e análise de informações econômico-financeiras de clientes para definir limites de crédito, conforme padrões e políticas da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-credito/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (111, 'Analista de Crédito e Cobrança', 'Acompanha com os bancos as aprovações de limite de crédito e liberação da condição de pagamento, aprovação bancária, emissão de contratos, envio de nota fiscal ao banco e coleta de documentos para análises. Envio de cobranças para pessoa física e jurídica, solicita baixa bancária, sustação de protesto e emissão de carta de anuência.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-credito-e-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (112, 'Analista de Crédito Imobiliário', 'Analisa documentos e cadastra processos para crédito imobiliário, preenche formulários, elabora pasta de financiamento e emite contrato.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-credito-imobiliario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (113, 'Analista de Criação', 'Elabora campanhas de comunicação on e offline para clientes internos. Desenvole material de comunicação institucional da entidade. Atualiza e realiza a manutenção de convites eletrônicos e e-mails marketing. criação de materiais promocionais, impressos, banners etc. Atua em projetos de desenvolvimento de aplicativos para dispositivos móveis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-criacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (114, 'Analista de CRM', 'Desenvolve estudos de mercado, concorrência, comportamento de clientes e segmentação de públicos, analisa indicadores de performance e métricas de satisfação, a fim de sugerir melhorias nos processos de relacionamento, desenvolver ações de marketing direto e identificar oportunidades de novos negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-crm/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (115, 'Analista de Customer Service', 'Analisa os pedidos de vendas, acompanha disponibilidade de estoque, faturamento e expedição de mercadoria e monitora entrega. Administra indicadores de nível de serviço e identifica falhas operacionais, a fim de melhorar os processos de atendimento ao cliente', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-customer-service/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (116, 'Analista de Custos', 'Realiza a elaboração e acompanhamento de orçamentos, analisando e controlando estoques e administra inventários variáveis ou fixos. Atua na análise e controle de custos, elaborando projetos de eliminação de perdas com programas de redução de gastos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-custos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (117, 'Analista de DBM', 'Realiza a gestão do DBM da companhia junto ao fornecedor contratado, acompanhando de perto as melhorias e as alterações. Entende as regras de negócio da empresa e replica os conhecimentos na estrutura do DBM (Database Marketing). Implementa e acompanha métricas de negócios e realizar melhorias nas bases de dados e nos processos da área de DBM. Atua com a criação e gestão de mailings extraídos da base para suporte nas ações mercadológicas. Elabora apresentações sobre conclusões das análises descritivas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-dbm/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (118, 'Analista de Departamento Pessoal', 'Realiza controle e processamento folha de pagamento, encargos sociais, cálculo de férias, admissões e demissões, prepara documentos para homologação, garantindo o cumprimento dos procedimentos legais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-departamento-pessoal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (119, 'Analista de Desembaraço Aduaneiro', 'Realiza o trâmite de desembaraço, controla os processos de drawback e analisa documentos e classificação fiscal de mercadorias enviadas pelo importador, para liberação e conclusão da conferência aduaneira. Presta suporte quanto a legislação e melhor procedimento na área de desembaraço e acompanha despachantes na resolução de problema junto a Receita Federal e outros órgãos governamentais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-desembaraco-aduaneiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (120, 'Analista de Desenvolvimento Analítico', 'Atua com análises qualitativas e quantitativas de extratos e substâncias de origem vegetal e marinha e compostos sintéticos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-desenvolvimento-analitico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (121, 'Analista de Documentação', 'Cria os processos de documentação, procedimentos, elabora o layout da documentação, adequa a documentação ao layout estabelecido,  revisa os documentos, classifica e organiza os documentos. Publica os documentos no sistema e documenta as reuniões realizadas por meio da ata.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (122, 'Analista de E-commerce', 'Desenvolve conteúdos para sites e realiza o cadastramento, publicação, alteração e exclusão de produtos na loja virtual. Efetua configuração e manutenção do sistema, realiza a edição do visual, cria campanhas online, hotsites, e-mail marketing, banners para sites, publicação e acompanhamento de campanhas de links buscadores / compradores de preço.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-e-commerce/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (123, 'Analista de Endomarketing', 'Realiza a gestão de eventos, abastece informações no site da empresa, elabora brindes para datas comemorativas, desenvolve campanhas motivacionais e comunicação interna. Elabora ações/campanhas para melhoria de resultados. Busca soluções de comunicação e interatividade. Elabora a comunicação visual e textual dos eventos. Atua com definição de layout e textos dos materiais. Solicita orçamento de acordo com as necessidades de compra, interage com o superior imediato para aprovações, acompanha a produção dos materiais e seleciona as melhores opções de compras dentro da verba destinada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-endomarketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (124, 'Analista de Engenharia', 'Participa no desenvolvimento de projetos, determinando especificações, desenhos, técnicas de execução, recursos necessários e demais requisitos, para possibilitar a construção, montagem, funcionamento e manutenção dentro dos padrões técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-engenharia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (125, 'Analista de Engenharia de Produto', 'Participa no desenvolvimento de projetos de produtos, determinando especificações, desenhos, técnicas de execução, recursos necessários e demais requisitos, para possibilitar a construção, montagem e funcionamento dentro dos padrões técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-engenharia-de-produto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (126, 'Analista de Estoque', 'Planeja o adequado armazenamento dos produtos e disposição das mercadorias, para preservar sua integridade e segurança e facilitar identificação, localização e manuseio. Acompanha o serviço de transporte e entrega dos produtos e participa na definição de rotas, assegurando a entrega dos pedidos dentro do prazo programado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-estoque/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (127, 'Analista de Estratégia de Mercado', 'Atua com criação, consolidação e análise de bases de dados de gestão comercial e indicadores de mercado. Extrai e manipula dados e relatórios de venda e comissões. Realiza estudos, mapeamentos e propostas de ocupação do mercado brasileiro. Faz concepção e produção de materiais de apresentação de projetos e iniciativas. Conduz participação no planejamento e execução de projetos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-estrategia-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (128, 'Analista de Eventos', 'Organiza eventos, realiza orçamento, define e avalia fornecedores e acompanha montagem e desmontagem da logística, para assegurar a qualidade dos serviços prestados e satisfação do cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-eventos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (129, 'Analista de Expatriados', 'Realiza o processo operacional de expatriados da empresa, envolvendo desde a análise salarial até férias e desligamento, acompanha e assessora nos assuntos relacionados à imigração, obtenção de vistos e documentos para garantir o cumprimento da legislação. Presta suporte nos procedimentos de acomodação e adaptação no Brasil, orienta quanto às obrigações fiscais e promove a integração do expatriado com a empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-expatriados/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (130, 'Analista de Expedição', 'Realiza o cadastro de entrada e saída de materiais no sistema, emite notas fiscais, acompanha a separação do pedido dos clientes e controla o transporte e o custo de fretes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-expedicao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (131, 'Analista de Facilities', 'Administra contratos terceirizados de serviços de portaria, segurança, limpeza e conservação, jardinagem e manutenção predial e acompanha custos e despesas previstos em contrato.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-facilities/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (132, 'Analista de Farmacovigilância', 'Acompanha o desempenho dos medicamentos que já estão no mercado e as ações que são realizadas e compartilhada pela vigilância sanitária dos estados, municípios e pela Anvisa. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-farmacovigilancia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (133, 'Analista de Faturamento', 'Emite e confere notas fiscais para realizar faturamento de vendas de produtos ou serviços e garantir a conformidade das condições contratuais definidas. Analisa e calcula impostos e alíquotas de notas fiscais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-faturamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (134, 'Analista de Folha de Pagamento', 'Atua com toda a rotina da folha de pagamento e rescisão de contrato, com cálculos de folha de pagamento, encargos e obrigações acessórias, cálculos e homologação de rescisão, admissão e atendimento aos colaboradores. Recebe pessoas da área trabalhista para fiscalização, lança os pagamentos no sistema e realiza cálculo de encargos sociais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-folha-de-pagamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (135, 'Analista de Franquias', 'Avalia e prospecta novos franqueados e estuda potencial de mercado, público e concorrência. Elabora relatórios de procedimentos padrões a serem seguidos pelos franqueados, conforme normas da empresa. Acompanha performance dos negócios para subsidiar informações à gestão nas tomadas de decisões.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-franquias/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (136, 'Analista de Frota', 'Controla a frota de veículos da empresa, agenda manutenção preventiva e corretiva, acompanha pagamento de multas, licenciamento e documentação e elabora relatório de quilometragem, gastos, perdas e custo com combustível.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-frota/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (137, 'Analista de Fusões e Aquisições', 'Realiza o levantamento e avaliação do perfil das empresas, estuda o mercado econômico, pesquisa potenciais investidores e elabora a modelagem financeira, a fim de identificar viabilidade e concretizar os negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-fusoes-e-aquisicoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (138, 'Analista de Garantia', 'Analisa processos de solicitação de garantia de veículos e de peças, requisita autorizações para execução de serviços e acompanha indicadores de serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-garantia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (139, 'Analista de Garantia da Qualidade', 'Realiza auditoria e análise de garantia da qualidade dos processos de produção, produtos e serviços, revisa procedimentos operacionais, elabora instruções de trabalho e participa na aplicação de medidas corretivas de não-conformidades, para garantir a aplicação dos padrões da empresa e boas práticas de fabricação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-garantia-da-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (140, 'Analista de Geoprocessamento', 'Interpreta documentação técnica, elabora e especifica técnica de componentes e requisitos de soluções de geoprocessamento. Responsável por processos de geocodificação manual e elaboração de processo. Faz edição, ajuste, configuração e montagem de bases cartográficas, elabora relatórios dinâmicos, elabora testes de soluções Web de geoprocessamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-geoprocessamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (141, 'Analista de Gestão de Incidentes', 'Presta suporte aos processos de missão crítica de TI (tecnologia da informação) e monitora links e servidores, acompanhando o incidente até seu tratamento e encerramento para garantir a disponibilidade de aplicações e serviços e integridade dos dados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-gestao-de-incidentes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (142, 'Analista de Governança de TI', 'Analisa capacidade dos ambientes de tecnologia da informação, identifica, planeja e implementa mudanças nos recursos e mapeia processos e procedimentos, a fim de garantir o controle das práticas de governança, otimização e segurança dos recursos e alinhamento aos negócios da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-governanca-de-ti/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (143, 'Analista de Help Desk', 'Atende chamados via service desk, visualiza e analisa as solicitações, executa procedimentos necessários para resoluções dos problemas. Instala softwares de upgrade e realiza modificações para melhoria no desempenho dos equipamentos. Controla o estoque de peças de reposição para equipamentos, garante que haja recursos disponíveis para manutenções, substituições e reparos, possibilitando pleno funcionamento das áreas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-help-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (144, 'Analista de Implantação', 'Implanta soluções, configurações e parametrizações em sistema de gestão e automação de processos de atendimento e faz treinamento de usuários no ambiente do cliente. Elabora relatório com indicadores de implantação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-implantacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (145, 'Analista de Incorporação', 'Analisa e valida projetos imobiliários junto com equipes de arquitetura, paisagismo, decoração e comercial e realiza o trâmite legal da prefeitura e órgãos públicos na aprovação de alvará de construção. Acompanha o desenvolvimento de ações de marketing e montagem de estande de vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-incorporacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (146, 'Analista de Indicadores', 'Atua com composição e atualização de indicadores da área, faz estudo de mercado comparando com a concorrência, controle de KPI&#039;s. Responsável pela melhoria de ferramentas de análises, buscando agilidade na obtenção de resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-indicadores/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (147, 'Analista de Informação', 'Faz mapeamento, desenho e processamento de conteúdos de web, sites e sistemas e dimensiona requisitos e funcionalidade para desenvolver soluções de informação e facilitar o acesso aos dados
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (148, 'Analista de Informações Gerenciais', 'Extrai, manipula e analisa informações extraídas de bancos de dados. Gera e elabora relatórios, indicadores operacionais e de resultados. Analisa mensalmente os relatórios gerados, mapeia as necessidades das áreas de apoio e elaborar propostas de relatórios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-informacoes-gerenciais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (149, 'Analista de Infraestrutura', 'Presta suporte e manutenção de servidores e data center, planeja capacidade e desenvolve projetos de melhorias dos serviços corporativos. Realiza criação de usuários e manutenção de grupos para concessão de privilégios com permissão de acesso. Implanta servidores, incluindo configuração de clusters para alta disponibilidade com acompanhamento e cumprimento das políticas dos recursos computacionais. Elabora documentação de procedimentos operacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-infraestrutura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (150, 'Analista de Inteligência Comercial', 'Analisa os negócios da empresa, projeções de vendas e indicadores comerciais. Acompanha concorrência, estuda tendências e mapeia potencial de mercado, com objetivo de elaborar relatórios e reportar informações à diretoria na tomada de decisões.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-inteligencia-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (151, 'Analista de Inteligência de Mercado', 'Realiza mapeamento, segmentação e estudo de mercado e concorrência e analisa perfil de clientes para identificar riscos e oportunidades de negócios, desenvolver cenários comerciais e alcançar os resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-inteligencia-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (152, 'Analista de Inventário', 'Realiza inventários cíclicos e rotativos, acompanha transações de entradas, saídas, ajustes e transferência de mercadorias e concilia fluxo de devolução e descarte, a fim de identificar diferenças de estoque e propor melhorias nos procedimentos da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-inventario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (153, 'Analista de Investimentos', 'Realiza análises de demonstrações financeiras, estuda projeções econômicas de clientes e mapeia mercado de fundo, a fim de identificar oportunidades de negócios e apresentar opções de aplicação de investimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-investimentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (154, 'Analista de Laboratório', 'Coleta, recebe e distribui material biológico de pacientes. Realiza exames conforme solicitações vindas do médico. Faz, checa, calibra e faz manutenção corretiva dos equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-laboratorio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (155, 'Analista de Legalização', 'Realiza o processo de abertura, alteração e encerramento de empresas no âmbito de órgãos públicos e entidade de classe, emite certidão negativa de débito, alvará e licença de funcionamento e elabora contrato social e distratos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-legalizacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (156, 'Analista de Licitação', 'Elabora e acompanha processos de licitação, desenvolve planilha de custos e propostas técnicas, pesquisa editais, cadastra a empresa em órgãos públicos e participa e negocia em pregões públicos ou eletrônicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-licitacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (157, 'Analista de Logística', 'Analisa a compra de matérias-primas, máquinas e materiais da empresa e desenvolve estratégias para armazenagem e distribuição de produtos. Acompanha indicadores de desempenho operacional e de transporte e elabora planilhas de controle.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-logistica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (158, 'Analista de Manutenção', 'Acompanha os processos de manutenção elétrica, mecânica e civil, conforme as programações corretivas e preventivas. Analisa chamados técnicos, elabora inspeções e procedimentos, controla indicadores de serviços e identifica oportunidades de melhorias nos programas de manutenção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (159, 'Analista de Marcas e Patentes', 'Elabora e controla toda a documentação dos processos de registro de produtos, desenvolvendo petições e relatórios técnicos sobre componentes, indicações, contraindicações, apresentação, entre outras informações para montar pastas dos produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-marcas-e-patentes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (160, 'Analista de Marketing', 'Elabora estratégia no desenvolvimento de materiais promocionais e campanhas em mídia online e off line. Realiza pesquisas de mercado e analisa comportamento do consumidor para atender as necessidades dos clientes e criar novos produtos ou serviços. Presta suporte na organização de feiras, eventos e exposições.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (179, 'Analista de Pesquisa de Mercado', 'Planeja e elabora questionário para pesquisa quantitativa ou qualitativa, define metodologias e amostras. Analisa resultados, consolida informações, estuda tendências e concorrência e acompanha reuniões de briefing com clientes ou diretoria, a fim de apresentar propostas de melhorias e desenvolver estratégias de negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-pesquisa-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (161, 'Analista de Marketing Digital', 'Desenvolve ações de SEO e SEM para geração visitas e leads e planeja campanhas de e-mail marketing. Desenvolve ações sociais de mídia e ações de relacionamento com clientes e parceiros. Acompanha o desenvolvimento de conteúdo sobre o mercado de e-commerce e envio de newsletter para base de prospects. Acompanha novas ferramentas de web, como aplicativos, mobile marketing e e-commerce.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-marketing-digital/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (162, 'Analista de Marketing Online', 'Desenvolve campanhas de Search Marketing através dos principais canais digitais de busca e comparadores de preço, criar e analisa relatórios diários do desempenho da área para identificar tendências, problemas e oportunidades. Faz a manutenção e otimização diária das campanhas de Search Marketing através dos sistemas dos respectivos canais. Realiza análise e controle dos KPIS da área de acordo com os Budgets estabelecidos pela empresa. Utiliza ferramentas que auxiliam no planejamento do search marketing (Google AdPlanner, Google Insights, Google Keyword Tools, Google Roda Mágica, Google Linha do Tempo).', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-marketing-online/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (163, 'Analista de Melhoria Contínua', 'Avalia, propõe melhorias e otimiza os processos a fim de diminuir os custos e gerar produtos com uma qualidade ainda melhor.  Propõe novas ideias, define se as soluções apresentadas são viáveis e devem ser implementadas. Estuda e analisa o comportamento atual do processo, identificação de novas oportunidades de projeto ou remodelagem dos processos de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-melhoria-continua/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (164, 'Analista de Mercado', 'Realiza análises de mercado, fundamenta e sugere ações para incremento de volume e participação de mercado. Verifica hábitos de consumo, presta apoio ao gerente do projeto no levantamento de dados para cálculo de rentabilidade, planejamento de vendas e portfólio de produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (165, 'Analista de Merchandising', 'Desenvolve estratégias de visual e merchandising de lojas, realiza o levantamento técnico de soluções de exposição e comunicação visual, atua na fiscalização da execução de projetos de merchandising, garante a padronização das lojas. Acompanha promoção e eventos, realiza formação de custos e propostas focados para campanhas, tarefas e mensalistas, elaboração de relatórios, treinamento e desenvolvimento de equipe.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-merchandising/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (166, 'Analista de Mídia', 'Cria campanhas de links patrocinados em redação de anúncios, segmentação e categorização. Checa, melhora e categoriza peças enviados por clientes. Analisa e monitora as métricas de criação e acompanhamento de relatórios gerais, de campanha monitorando os resultados e avaliando melhoras. Otimiza Campanhas, cria e analisa relatórios detalhados para descobrir oportunidades de melhora das métricas. Avalia campanhas e implementa otimizações. Gera propostas de melhorias, assessora e presta suporte a clientes. Monitora campanhas de performance.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-midia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (168, 'Analista de Mídias Sociais', 'Elabora textos para as redes sociais, dissemina o conteúdo em comunidades e blogs relevantes, interage com participantes nos diferentes pontos de contato, mantém contato com os principais multiplicadores em redes sociais (líderes em comunidades e blogueiros), acompanha a presença nas redes sociais (quantitativa e qualitativa), identifica o crescimento da presença online, gera relatórios de desempenho de campanha e garante o fluxo de informações entre agência, cliente e redes sociais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-midias-sociais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (169, 'Analista de MIS', 'Realiza a extração de relatórios, criação, manutenção e gestão de banco de dados e desenvolve estudos e orienta as tendências operacionais, por meio da mensuração de resultados e comparativos para tomada de decisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-mis/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (170, 'Analista de Monitoramento', 'Monitora desempenho de sistemas, sites e componentes da infraestrutura, a fim de detectar desvios e evitar indisponibilidade no ambiente, registra ocorrência de falhas e acompanha resolução de incidentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-monitoramento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (171, 'Analista de Negócios', 'Analisa demanda e necessidades dos clientes, levanta requisitos, define escopo e avalia os processos de negócios. Estuda tendências, concorrência e mercado na busca das melhores oportunidades de negócio alinhado a estratégia da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-negocios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (172, 'Analista de Negócios de TI', 'Planeja e gerencia a implementação de projetos de sistemas de informação e aplicativos, envolvendo a compreensão do negócio do cliente, mapeamento de suas necessidades, especificação da solução e análise de tendências tecnológicas para identificar a melhor oportunidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-negocios-de-ti/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (173, 'Analista de Operações', 'Realiza estudo, diagnóstico e revisão do fluxo de atividades administrativas e de logística. Acompanha indicadores para identificar oportunidades de melhoria no processo operacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-operacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (174, 'Analista de Outsourcing', 'Analisa processos para terceirização, de acordo com as necessidades da empresa, realiza pesquisa de mercado na identificação de opções de fornecimento de produtos e serviços e acompanha realização das atividades e resultados, a fim de conquistar melhor desempenho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-outsourcing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (175, 'Analista de Ouvidoria', 'Atende, resolve e acompanha as reclamações dos clientes, postadas pelos diversos canais da empresa. Identifica e sugere mudanças nos fluxos operacionais da companhia para evitar reclamações futuras. Busca soluções e analisa os casos recepcionados pelos canais mais críticos, realiza estudos analíticos, conforme histórico de reclamações. Identifica através das análises de reclamações o que gerou a insatisfação dos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-ouvidoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (176, 'Analista de Patrimônio', 'Realiza o cadastro, classificação, identificação e inventário de bens patrimoniais da empresa, controla baixas e transferências e calcula depreciação para atualizar em sistema o controle de ativo imobilizado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-patrimonio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (177, 'Analista de PCM', 'Programa e controla os planos de manutenção preventiva e corretiva, analisa falhas em máquinas e equipamentos de produção, desenvolve novos fornecedores e analisa necessidade de compra de peças para reposição.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-pcm/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (178, 'Analista de PCP', 'Atua com informe de resultados através de relatórios e otimização de inventários. Elabora e acompanha cronogramas de fabricação. Faz análise crítica de pedido e o manuseio do estoque.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-pcp/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (217, 'Analista de Service Desk', 'Administra solicitações, problemas e mudanças, analisa chamados, realiza testes e identifica a solução de problemas. Instala e presta assistência aos sistemas operacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-service-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (180, 'Analista de Planejamento', 'Realiza o levantando e análise de informações sobre os aspectos econômicos e financeiros, a fim de contribuir na elaboração de planos de ação para alcance dos objetivos da empresa. Gera indicadores para melhorar performance dos resultados e elabora relatórios gerenciais para dar suporte na tomada de decisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-planejamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (181, 'Analista de Planejamento Estratégico', 'Analisa falhas de processos, criar banco de dados de processos, confecciona planos de ação e procedimentos e manuais. Compila as entrevistas criando uma bases de dados. Mapeia e redesenha processos. Alinha os processos de estratégia, verifica não conformidade nos processos redesenhados. Automatiza e otimiza processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-planejamento-estrategico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (182, 'Analista de Planejamento Financeiro', 'Elabora os relatórios gerenciais para acompanhamento de desempenho de áreas de negócio, dando suporte as áreas não-financeiras, orçamento, forecast e análises de cenário para dar suporte a tomada de decisão. Realiza as análises de viabilidade financeira de projetos, auxilia no fechamento contábil, construção de modelos financeiros em Excel e análise de demonstrações contábeis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-planejamento-financeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (183, 'Analista de Pós-venda', 'Realiza contato e visitas aos clientes, verifica a qualidade dos produtos ou serviços contratados, identifica suas necessidades e acompanha reclamações e dúvidas até a solução, com objetivo de estreitar o relacionamento e desenvolver ações de melhorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-pos-venda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (184, 'Analista de Prevenção de Perdas', 'Analisa as atividades de prevenção de perdas de estoque de lojas, acompanha o cumprimento às normas de segurança e procedimentos administrativo, controla inventário e indicadores de performance operacional e elabora relatórios para reportar incidentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-prevencao-de-perdas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (185, 'Analista de Pricing', 'Realiza análise e formação de preços de mercadoria e serviços para aumentar a receita e garantir uma melhor margem sobre os custos diretos ou indiretos de seus produtos e serviços. Monitora o mercado e a concorrência em relação aos preços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-pricing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (186, 'Analista de Processos', 'Analisa e define métodos e processos de trabalho, estuda tempos e parâmetros, mapeia fluxo das atividades e identifica falhas, a fim estabelecer estratégias para solução de problemas e propor melhoria contínua dos processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-processos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (187, 'Analista de Processos e Qualidade', 'Realiza estruturação da documentação do sistema de gestão para adequação as normas de Qualidade, desenha processos e fluxogramas para implantação. Padroniza as atividades e medição de performance, elabora documentos para padronização de processos corporativos, elabora planilhas e relatórios e apresenta análises de resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-processos-e-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (188, 'Analista de Processos Gerenciais', 'Atua com análise, avalia o mercado e define as estratégias de gerenciamento e a administração dos recursos materiais e financeiros das empresas. Analisa o patrimônio empresarial e os funcionários, lida com orçamentos e investimentos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (189, 'Analista de Processos Industriais', 'Planeja, implementa e otimiza processos industriais, acompanhando fluxo e índices de performance para identificar oportunidades de melhorias e eliminar falhas no alcance dos resultados de volume, qualidade e custo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-processos-industriais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (190, 'Analista de Produção', 'Realiza o gerenciamento das metas contratadas e indicadores de produção e produtividade. Elabora relatórios gerenciais, implementa projetos de melhoria de Índice Rendimento e de Produtividade de Industrializados, participar dos comitês operacionais. Faz a análise de consumos e desvios de consumos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (191, 'Analista de Produto', 'Acompanha o ciclo de vida dos produtos, comportamento da concorrência e tendências de mercado, viabilidade de lançamentos, analisando resultados para identificar novas oportunidades, melhorias e modificações nos produtos existentes. Participa no lançamento de novos produtos, campanhas de promoções de vendas e formas de divulgação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-produto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (192, 'Analista de Projetos', 'Elabora planos de execução, comunicação e acompanhamento do projeto. Garante que todas as informações e acontecimentos serão registrados e estarão em um local de fácil consulta. Elabora e acompanha cronograma do projeto. Elabora indicadores e garante as entregas de pacotes de serviço e atividades a serem desempenhadas para a execução do projeto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-projetos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (193, 'Analista de Publicidade e Propaganda', 'Conduz reuniões com os fornecedores e os prestadores de serviços. Faz a manipulação de informação junto à plataforma eletrônica, mantém o acompanhamento de entrada de material e faz a conferência de qualidade. Faz a avaliação de contrato de serviço de novo fornecedor da empresa. Mantém o acompanhamento e quando necessário fazer o apontamento a ordem de pagamento ao que é relativo ao setor. Atua com a gestão de campanha publicitária, marketing digital pessoal e impressa. Manter o acompanhamento de reunião de trabalho para a realização de nova parceria. Faz a atualização de preço e de produto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-publicidade-e-propaganda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (194, 'Analista de QSMS', 'Atua com qualidade e sistemas integrados (segurança, meio ambiente e saúde). Elabora documentos do sistema integrado de gestão. Desenvolve e implanta projetos para melhorar a qualidade da produção e do ambiente de trabalho, assim como a segurança e a saúde dos trabalhadores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-qsms/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (195, 'Analista de Qualidade', 'Elabora e implementa modelos e ferramentas de controle da qualidade, com foco na adoção de políticas e técnicas de melhoria contínua. Realiza mapeamento de processos, instruções de trabalho, análise e controle de não-conformidades e acompanha auditoria interna e externa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (196, 'Analista de Recrutamento e Seleção', 'Realiza todo o processo de recrutamento e seleção. Analisa padrões para aproveitamento de candidatos, seleciona fontes de recrutamento. Analisa juntamente com cargos e salários, níveis adequados de remuneração para contratação.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (197, 'Analista de Recursos Humanos', 'Realiza análise da área de Recursos Humanos, recruta e seleciona novos colaboradores, levanta necessidades de treinamento e avalia desempenho de pessoal. Desenvolve planos de carreiras e dissemina cultura organizacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-recursos-humanos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (292, 'Animador de Festas', 'Realiza animação em festas infantis, interage com o público e desenvolve atividades recreativas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/animador-de-festas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (198, 'Analista de Redes', 'Realiza serviços nos equipamentos da rede local, como instalação e configuração de computadores e ativos de rede. Presta suporte à área de desenvolvimento de aplicações, registra ocorrências, orienta usuários, acompanha e elimina falhas. Negocia a contratação de serviços e produtos, acompanhando contratos de manutenção de programas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-redes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (199, 'Analista de Redes Sociais', 'Planeja ações em plataformas digitais, realiza produção de conteúdos, monitora redes sociais e pesquisa público-alvo. Desenvolve relatórios e análises para o departamento de marketing. Elabora o plano de campanhas de investimento em links patrocinados e em campanhas publicitárias online. Gerencia campanhas e marcas em redes sociais, como Facebook, Twitter, Youtube, Linkedin, entre outros. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-redes-sociais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (200, 'Analista de Relacionamento', 'Presta suporte a equipe, promove ações de relacionamento com os clientes, agenda visitas e realiza as atividades de organização e controle de documentos, cadastro, elaboração de relatórios, gráficos comparativos, digitação e tabulação de dados.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (201, 'Analista de Relações Institucionais', 'Desenvolve o relacionamento com instituições, articulando estratégias para estabelecer parcerias em programas de sustentabilidade e responsabilidade social. Identifica e avalia oportunidades de parcerias, a fim de atender os objetivos da organização e consolidar imagem no mercado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-relacoes-institucionais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (202, 'Analista de Relações Internacionais', 'Analisa mercados e o cenário mundial, identifica oportunidades de negócios e orienta clientes em investimentos no exterior. Promove o relacionamento diplomático entre empresas e governos de diversos países nos processos de exportação e importação e participa na negociação de acordos bilaterais ou multinacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-relacoes-internacionais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (203, 'Analista de Relações Trabalhistas', 'Acompanha as fiscalizações diretas e indiretas e de processos judiciais, participação em audiências.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (204, 'Analista de Relatórios', 'Atua com macro (VBA), tabela dinâmica, gráfico, funções de pesquisa e referência (CONT.SE, Procv, Proch, deslocamento, endereço, SE, importação de dados externos e entre outros). Elabora relatórios analíticos, gerenciais e painéis gerenciais / operacionais, extração e análise de base de dados. Emite relatórios solicitados pelo gerência, coordenação e supervisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-relatorios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (205, 'Analista de Remuneração', 'Elabora e atualiza tabelas salariais, com base na política definida pela organização, envolvendo estudos e simulações de alterações salariais e seus impactos nos custos de pessoal. Orienta e acompanha os processos de definição da remuneração nos casos de admissão, promoção e transferência de funcionários. Monitora a competitividade da empresa em relação ao mercado, por meio de elaboração e participação de pesquisas salariais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-remuneracao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (206, 'Analista de Requisitos', 'Realiza o levantamento de requisitos e especificação de projetos de TI, desenvolvendo soluções para processos, mapeamento e análise de negócio. Elabora a documentação técnica de especificação de requisitos de softwares e status report para gestão de projetos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-requisitos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (207, 'Analista de Responsabilidade Social', 'Elabora e acompanha projetos e programas sociais e realiza levantamento de necessidades. Administra o orçamento e cronograma, faz parcerias com instituições, capta recursos e realiza eventos institucionais. Levanta informações para indicadores de responsabilidade social e presta suporte às campanhas internas beneficentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-responsabilidade-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (208, 'Analista de Retenção', 'Realiza atendimento receptivo e ativo aos clientes, esclarece dúvidas, faz fidelização e mantém relacionamento afim de fazer o cliente não desistir do produto/serviço.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-retencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (209, 'Analista de Risco', 'Faz análises de maior complexidade referente a levantamentos de dados, preparação de análises econômico financeiras de clientes para definição de limites para crédito em operações. Elabora relatórios do setor, análise de crédito e consulta a restrições.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-risco/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (210, 'Analista de SAC', 'Recepciona ligações de clientes, aponta ocorrências, busca informações ou soluções de problemas junto às áreas competentes e retorna ao cliente. Analisa as respostas recebidas e interage com o Coordenador quando necessário. Efetua Follow-up das reclamações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-sac/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (211, 'Analista de Segurança da Informação', 'Administra ambientes computacionais e participa na definição da arquitetura tecnológica para segurança da informação. Analisa sistemas, levanta vulnerabilidades, mapeia riscos e implementa solução para a segurança de ambientes e dispositivos informatizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-seguranca-da-informacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (212, 'Analista de Segurança do Trabalho', 'Realiza a fiscalização dos processos para cumprir as normas de higiene e segurança, inspeciona ambientes organizacionais, observando locais de periculosidade e desenvolve políticas de segurança do trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-seguranca-do-trabalho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (213, 'Analista de Segurança Patrimonial', 'Analisa e planeja as atividades de segurança patrimonial, identifica não-conformidades no processo, levanta riscos e desenvolve plano de ação para solucionar vulnerabilidade. Controla tráfego de pessoas e veículos dentro das dependências da empresa e acompanha registro de ocorrências.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-seguranca-patrimonial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (214, 'Analista de Seguros', 'Estuda propostas de seguros e pedidos de cobertura, realiza cálculo de prêmios, identifica riscos e estabelece prazos de vigência.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-seguros/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (215, 'Analista de SEM', 'Desenvolve estratégias de anúncios de links patrocinados, realizando ajustes necessários em campanhas para melhor performance e monitora métricas por meio de ferramentas de web analytics.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-sem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (216, 'Analista de SEO', 'Implementa estratégias de SEO, aumenta o tráfego do site e realiza melhorias nos sites a partir da estratégia de SEO. Define estratégias de SEO e palavras chaves. Realiza relacionamento e troca de links com outros sites. Elabora páginas do site com estratégia de SEO e produz textos com estratégias de SEO. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-seo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (293, 'Animador Digital', 'Desenvolve ilustrações e animações em ambiente virtual e realiza criação, manipulação e modelagem de cenários e objetos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/animador-digital/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (218, 'Analista de Sinistro', 'Analisa processos de sinistros, verifica dados cadastrais do segurado, compara cláusulas e taxas de apólices para calcular os valores de indenização. Controla sinistros pendentes e encerra os já quitados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-sinistro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (219, 'Analista de Sistema de Gestão Integrada', 'Realiza o mapeamento e integração de processos, procedimentos e práticas organizacionais e identifica atividades críticas relacionadas a qualidade, ambiente, segurança ocupacional e da informação, entre outros, a fim de revisar e implementar políticas unificadas, aperfeiçoar os métodos internos e atingir os objetivos e metas da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-sistema-de-gestao-integrada/');
