INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (235, 'Analista de Subscrição', 'Analisa cobertura de seguros ou resseguros, avaliando risco inclusos e excluídos, precificação de prêmio, prevenção de sinistro e termos de contrato, a fim de fornecer coberturas de acordo com a necessidade do consumidor.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-subscricao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (236, 'Analista de Suporte Mainframe', 'Atua com configuração e customização de hardware e software relacionados a armazenamento da plataforma mainframe. Controla a infraestrutura de rede, Switch Brocade, placa OSA, análise de traces TCPIP e SNA, depuração de problemas em comunicação TCP / IP e SNA / APPN, atualização e manutenção de softwares e ITIL.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-suporte-mainframe/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (237, 'Analista de Suporte Middleware', 'Atua com implantação e sustentação de plataforma Oracle Middleware, Oracle Web Center, Oracle Weblogic, Oracle SOA Suíte, Oracle BPM, OIM, OAM entre outros. Responde pela administração de sistemas operacionais e banco de dados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-suporte-middleware/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (238, 'Analista de Suporte Técnico', 'Realiza seleção, implantação e manutenção de hardwares e software básico e de apoio, define controle de acesso aos recursos, participa na documentação de rotina e acompanha o desempenho dos recursos técnicos instalados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-de-suporte-tecnico/');
