INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (288, 'Analista SAP', 'Faz análise, configuração e desenvolvimento de sistema informatizado abrangendo as várias áreas de negócio da empresa, conforme métodos previamente estabelecidos. Busca soluções informatizadas que visem à melhoria de processo. Documenta conforme governança da área de TI. Orienta usuários para melhor operação dos sistemas. Atua sobre incidentes, visando a estabilidade da operação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-sap/');
