INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (290, 'Analista Técnico', 'Faz atendimento de campo e remoto a clientes, realizando testes em sistemas desenvolvidos pela empresa e serviços de manutenção. Realiza a manutenção preventiva e corretiva de equipamentos. Atua no diagnóstico e troca de equipamentos com problemas, envolvendo a instalação e reinstalação de sistema operacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/analista-tecnico/');
