INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (295, 'Apontador de Obras', 'Realiza levantamentos e registros da mão de obra no canteiro, distribui ordens de serviço e acompanha medição. Fiscaliza a frequência de ponto e controla entrada e saída de materiais em estoque.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/apontador-de-obras/');
