INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (314, 'Aprendiz na área Financeira', 'Ajuda nas rotinas do departamento financeiro, controle de contas a pagar e receber, emissão de notas fiscais e boleto, elaboração de planilhas e controle de arquivos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/aprendiz-na-area-financeira/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (315, 'Aprendiz na área Jurídica', 'Ajuda o assistente jurídico com o controle de arquivos e processos, auxilia na documentação para audiência e diligência em fóruns.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (316, 'Aprendiz na área Mecânica', 'Ajuda nas rotinas da área, envolvendo o atendimento ao cliente, consertos mecânicos ou elétricos, faz a troca de óleo e ajuda na limpeza de motores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/aprendiz-na-area-mecanica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (317, 'Aprendiz na área Operacional', 'Ajuda nas rotinas do departamento de operações, fazendo atendimento telefônico, acompanhamento de relatórios operacionais, elaboração de planilhas e controle de arquivos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/aprendiz-na-area-operacional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (318, 'Apresentador de Programa de Televisão', 'Apresenta programas de entrevistas, entretenimento e variedades, em emissoras de televisão aberta ou fechada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/apresentador-de-programa-de-televisao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (319, 'Aquarista', 'Manejo e manutenção de peixes e aquários, atendimento e montagem de aquários. Venda de aquários, equipamentos, medicamentos, rações e de material solicitado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/aquarista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (320, 'Armador', 'Realiza a montagem de armações e estruturas de concreto e aço em obras, aplica pilares, vigas e lajes e confere material a ser utilizado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/armador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (321, 'Armazenista', 'Atua com armazenagem, separação, controle e conferências de mercadorias e pedidos, além de preencher informações em sistema. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/armazenista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (345, 'Assessor de Investimentos', 'Prospecta clientes para investimento na bolsa de valores, prestando assessoria no mercado de capitais. Identifica perfil de investidor, estuda viabilidade, elabora estratégia de investimentos e auxilia na tomada de decisões.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assessor-de-investimentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (322, 'Arqueólogo', 'Realiza estudos e pesquisas sociais, econômicas e políticas de acordo com vestígios de materiais de antigas civilizações. Participa da gestão territorial e socioambiental e coordena pesquisa de campo, envolvendo escavações e análise de cavidades, mapas e fotos aéreas para identificar sítios arqueológicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arqueologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (323, 'Arquiteto', 'Elabora, projeta e organiza espaços internos e externos, como edifícios, pontes, praças e demais obras, de acordo com a estética, conforto e funcionalidade. Desenvolve a planta, determina os materiais que serão utilizados e realiza estudos de viabilidade financeira, econômica e ambiental.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (325, 'Arquiteto Coordenador', 'Coordena equipe e projetos de Arquitetura, acompanha o desenvolvimento de obras, envolvendo a especificação de materiais e equipamentos, fiscalização de cronograma e estudo de viabilidade, para cumprimento da qualidade e prazo estabelecido.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-coordenador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (326, 'Arquiteto de Edificações', 'Desenvolve projeto de edifícios comerciais, industriais, institucionais e residenciais, define materiais e acabamento, fiscaliza obras e realiza estudo de viabilidade financeira, econômica e ambiental.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-de-edificacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (327, 'Arquiteto de Informação', 'Estrutura grupos de informações relacionadas para organização de websites, intranets e demais portais digitais, a fim de prestar suporte à usabilidade e facilitar o acesso ao conteúdo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-de-informacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (328, 'Arquiteto de Interiores', 'Desenvolve planilhas orçamentárias, realiza a montagem de caderno de mobiliários, negociação com fornecedores. Elabora, pesquisa e busca decoração para o projeto. Realiza o layout e finalização dos decorados, acompanha as obras, faz compras e executa a produção e montagem do decorado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-de-interiores/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (329, 'Arquiteto de Sistemas', 'Desenvolve o design estrutural de grupos de informações relacionadas ou, pela organização e rotulação de Web sites, Intranets, comunidades online e software para dar suporte à usabilidade e facilidade de obtenção de informações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-de-sistemas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (330, 'Arquiteto de Soluções', 'Desenvolve o design estrutural de grupos de informações relacionadas ou, pela organização e rotulação de Web sites, Intranets, comunidades online e software para dar suporte à usabilidade e facilidade de obtenção de informações.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (331, 'Arquiteto Especificador', 'Realiza visitas técnicas, elabora especificação de produtos e desenvolve orçamentos para obras, escritórios de arquitetura e engenharia e demais clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-especificador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (332, 'Arquiteto Java', 'Atua com arquitetura e design estrutural de projetos Java, verifica dados  relacionados a organização de Web Sites, Intranets, comunidades online e software para dar suporte à usabilidade e facilidade no acesso às informações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-java/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (333, 'Arquiteto Paisagista', 'Desenvolve projeto de paisagismo em espaços naturais e urbanos, analisa as necessidades de acordo com sua funcionalidade, topografia e preservação do meio ambiente. Contribui para o planejamento, estética e implantação de espaços públicos e privados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-paisagista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (334, 'Arquiteto Urbanista', 'Realiza o planejamento físico e financeiro de obras e elabora projetos de edifícios e cidades, envolvendo ruas, parques e bairros. Orienta o processo de aprovação de projetos nos órgãos responsáveis e desenvolve projetos de transportes e saneamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquiteto-urbanista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (335, 'Arquiteto Vendedor', 'Faz vendas de projetos mobiliário, faz todo acompanhamento de entregas e pós-vendas. Prospecção de novos negócios ligados a arquitetura.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (336, 'Arquivista', 'Organiza documentos de arquivos institucionais e pessoais, avalia o grau de importância para decisão do tempo de arquivamento adequado e realiza estudo de sistemas de arranjo dos documentos, identificando, codificando e padronizando.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arquivista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (337, 'Arrematador', 'Faz manualmente ou em máquina o arremate de peças de vestuário, prega botões, colchetes e enfeites e realiza bordados e customização.                       ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arrematador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (338, 'Arte-finalista', 'Finaliza tecnicamente uma peça de design ou publicidade, como paginação, desenvolvimento de desenhos e pós-produção de imagem, a fim de fechar o arquivo para impressão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/arte-finalista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (339, 'Artesão', 'Confecciona produtos artesanais por meio de processo manual ou com auxílio de ferramentas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/artesao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (340, 'Articulador Social', 'Desenvolve e implanta plano de captação de recursos no âmbito corporativo, com parcerias e convênios com o setor público e privado, voluntariado e ações de sustentabilidade locais. Planeja e organiza os trabalhos voluntários. Apoia a instituição na captação de parcerias financeiras e não-financeiras (operacionais e técnicas).', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/articulador-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (341, 'Ascensorista', 'Opera elevadores no transporte de pessoas e cargas. Observa o limite de peso respectivo, zelando pela segurança das pessoas e do patrimônio de edifícios de apartamentos, edifícios comerciais e outros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ascensorista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (342, 'Assessor Científico', 'Atua em laboratório de análises clínicas, presta assessoria científica, acompanha o andamento de exames laboratoriais, correlaciona resultados e assina laudos, atualiza documentação técnico-científica, pesquisa e elabora relatórios sobre o mercado concorrente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assessor-cientifico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (343, 'Assessor de Comunicação', 'Elabora e monitora o planejamento de marketing da instituição (publicidade e propaganda) marketing de relacionamento com clientes, imprensa e fornecedores. Interage com a mídia interna e externa para reforçar a imagem do produto da entidade. Classifica os releases por região e por temas. Organiza o clipping e produz o relatório de desempenho de imagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assessor-de-comunicacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (344, 'Assessor de Imprensa', 'Assessora as atividades de comunicação, trata informações institucionais e projeta a imagem da empresa para o público interno e externo, de acordo com as estratégias e políticas de negócios. Busca espaços em veículos de comunicação para divulgar informações sobre empresas, produtos, serviços ou pessoas públicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assessor-de-imprensa/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (346, 'Assessor de Marketing', 'Presta suporte nas ações de comunicação interna e externa, na realização de eventos, campanhas promocionais e no desenvolvimento de projetos diversos voltados à satisfação dos clientes e de melhoria da imagem da empresa. Realiza análise do mercado para identificar oportunidades de negócios e acompanha as ações dos concorrentes para orientação das campanhas de divulgação. Atua com promoção do relacionamento com os clientes da empresa desde a pré-venda, prospecção até a sua fidelização. Estabelece indicadores de desempenho para a gerência. Apoia a realização de eventos internos, reunião de motivação de colaboradores, confraternizações da empresa, suporte as reuniões de formação e qualificação. Confecciona apresentações da empresa e da área, criação de banners, convites, bem como vídeos e álbuns com registros dos eventos. Efetua a manutenção dos conteúdos do site da empresa. Acompanha os pedidos de orçamento. Desenvolve projetos de sinalização visual da empresa, redesign da intranet, papelaria e design de brindes personalizados. Gerencia verbas de fornecedores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assessor-de-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (347, 'Assessor Jurídico', 'Presta assessoria jurídica nas áreas comercial, cível, tributária, trabalhista, entre outras, tanto nos aspectos preventivos quanto na administração do contencioso, sugerindo medidas a tomar para resguardar os interesses e dar segurança jurídica aos atos e decisões da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assessor-juridico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (348, 'Assistente Administrativo', 'Presta assistência nos processos da área administrativa, auxiliando na organização de arquivos, controle de entrada e saída de correspondências, emissão e lançamento de nota fiscal e elaboração de planilhas e relatórios gerenciais. Realiza atividades de apoio às áreas financeira e de recursos humanos. Recepcionista de clínica e telefonista.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-administrativo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (349, 'Assistente Administrativo de Manutenção', 'Acompanha tarefas de manutenção, consertos e reparos dos bens móveis da empresa. Relata sobre as necessidades de manutenção de cada tipo de bem. Solicita o material necessário para os reparos e consertos. Atua em conformidade com as determinações superiores e do mestre de manutenção e ajuda nos reparos dos bens imóveis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-administrativo-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (350, 'Assistente Comercial', 'Acompanha os pedidos de vendas, presta apoio no desenvolvimento de propostas comerciais e elabora relatórios sobre tendências de mercado, demanda e concorrentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (351, 'Assistente Contábil', 'Realiza classificação, conciliação e lançamento contábil e participa na apuração de impostos e preenchimento de guias de recolhimento junto aos órgãos do governo. Controla escrituração de livros fiscais e auxilia na elaboração de balancetes e demonstrativos de contabilidade.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (352, 'Assistente de Alimentos e Bebidas', 'Auxilia na cotação e avaliação de fornecedores, participa da elaboração de cardápios, organiza equipe na realização de eventos e acompanha reuniões para alinhamento do cronograma.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-alimentos-e-bebidas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (353, 'Assistente de Almoxarifado', 'Controla entrada e saída de materiais entregues ou retirados do estoque, confere as notas fiscais com os pedidos e organiza a estocagem de materiais, preservando sua integridade física e condições de uso.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-almoxarifado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (354, 'Assistente de Arquitetura', 'Auxilia nas atividades de elaboração e interpretação de desenhos técnicos, acompanha projetos de Arquitetura e pesquisa novos materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-arquitetura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (355, 'Assistente de Arquivo', 'Auxilia no arquivo de documentos, classifica e indexa de acordo com critérios e realiza manutenção de arquivos para facilitar sua localização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-arquivo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (356, 'Assistente de Arte', 'Cria e desenvolve peças gráficas como cartazes, revistas e folders e finaliza a arte no tratamento e editoração de imagens. Realiza o processo de concepção de ideias a partir do briefing.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-arte/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (357, 'Assistente de Assuntos Regulatórios', 'Auxílio nas atividades de regularização da empresa perante órgãos da vigilância sanitária, elaboração de mapas mensal e trimestral, acompanhamento de DOU, DOE, site da ANVISA, (atualizações referente a legislação da vigilância sanitária), pesquisa de normas e legislações internacionais, assistência na elaboração dos processos e trâmites necessários para protocolos e processos na ANVISA e na elaboração de processos de registro de notificação de produtos, controle de prazos de revalidação de produtos junto a ANVISA, montagem de documentos necessários em dossiê.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-assuntos-regulatorios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (358, 'Assistente de Atendimento', 'Atua com atendimento ao cliente, acompanha junto ao fornecedor a posição dos pedidos e solicitações dos clientes. Comunica e negocia com os clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-atendimento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (359, 'Assistente de Atendimento Publicitário', 'Realiza interface entre a agência e o cliente, participa de reuniões, elabora briefing para criação de campanhas publicitárias e negocia prazos para entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-atendimento-publicitario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (360, 'Assistente de Ativo Fixo', 'Recebe as aquisições de bens e determina a sua contabilização, classificação e a respectiva numeração, conforme controles diversos. Efetua a inclusão das aquisições e as respectivas baixas necessárias. Acompanha o fechamento mensal de controle do ativo fixo. Ajuda na realiza do inventário físico do ativo imobilizado.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (361, 'Assistente de Auditoria', 'Faz análise e cumprimento de normas, elabora relatórios com pareceres, recomendações, demonstrativos, descrições dos procedimentos praticados e não conformidades. Controla testes quantitativos e qualitativos, faz a identificação de riscos e fraudes, realiza inventários, acompanha e elabora normas e procedimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-auditoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (362, 'Assistente de Auditoria Contábil', 'Presta auxílio nas atividades de auditoria para acompanhamento dos resultados, participa da prestação de serviços de auditoria, consultoria contábil, tributária e societária. Audita os procedimentos envolvidos, identificando eventuais falhas e avaliando a eficácia dos controles.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-auditoria-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (363, 'Assistente de Back Office', 'Verifica e analisa as reclamações recebidas através do SAC, analisa as reclamações dos problemas, contata as áreas responsáveis e providencia as ações e documentos necessários para atendimento e solução da dificuldade do usuário.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-back-office/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (683, 'Auxiliar de Veterinário', 'Auxiliar o veterinário nos procedimentos, preparar e manusear animais e materiais para procedimentos veterinários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-de-veterinario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (364, 'Assistente de Benefícios', 'Auxilia na implantação e administração de benefícios, como assistência médica e odontológica, vale refeição, alimentação e transporte, entre outros. Analisa necessidades e procedimentos para a concessão e negociação de contratos de benefícios, assegurando os melhores custos e qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-beneficios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (365, 'Assistente de Cadastro', 'Realiza cadastro, cancelamento e reativações de clientes, fornecedores e itens novos. Mantém em dia a rotina administrativa, cumprindo prazos e regras de movimentações cadastrais. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-cadastro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (366, 'Assistente de Câmbio', 'Realiza processamento na compra e venda de moeda estrangeira e crédito em cartão pré-pago e analisa e confere documentos para fechamento de câmbio.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-cambio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (367, 'Assistente de Cargos e Salários', 'Auxilia na descrição e classificação dos cargos de uma empresa e realiza pesquisas salariais, para fornecer informações na determinação de níveis salariais, avaliações de desempenho e sistemas de promoções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-cargos-e-salarios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (368, 'Assistente de Cobrança', 'Faz negociações com clientes inadimplentes, cálculo de juros, emissão de boletos, carta de anuência, baixa e liquidação de títulos e devolução de mercadorias. Elabora relatórios referentes a cobranças efetuadas e atrasos existentes, facilitando o acompanhamento e a tomada de decisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (369, 'Assistente de Comércio Exterior', 'Controla as atividades de importação e exportação da empresa, realiza a cotação de preços com fornecedores, negocia fretes e acompanha fluxos de documentos e regimes alfandegários e cambial.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-comercio-exterior/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (370, 'Assistente de Compliance', 'Presta suporte administrativo no desenvolvimento das atividades da área de Compliance, auxilia na descrição de procedimentos, por meio de sistema, bem como efetuando controles de normatizações editadas e das suas respectivas manutenções, bem como auxiliar na divulgação das mesmas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (371, 'Assistente de Compras', 'Realiza pesquisa de mercado para cotações de preços, analisa e emite pedidos, atende fornecedores e acompanha entrada de notas fiscais no almoxarifado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (372, 'Assistente de Comunicação', 'Auxilia no desenvolvimento de ações de comunicação interna e externa, elabora conteúdo para redes sociais, site, newsletter, entre outras mídias, cria informativos internos e presta apoio na assessoria de imprensa. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-comunicacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (373, 'Assistente de Comunicação e Marketing', 'Monitora a presença da rede e suas frentes de atuação na Web. Produz conteúdo de manutenção frequente para os canais oficiais da rede na Web - sites, hotsites, blogs e redes sociais. Orienta estratégias e ações para canais na Web das unidades de ensino e outras frentes de atuação da rede. Atende e orientar as unidades sobre dúvidas relacionadas a presença na Web - sites e redes sociais). Atender demandas operacionais que envolvam presença da Web da Rede La Salle e suas frentes de atuação, como criação de novos canais, aplicativos e ações promocionais. Prestar apoio no atendimento de agência digital e outros fornecedores de atuação na Web. Acompanhar indicadores e elaborar relatórios da presença na Web - relatórios analíticos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-comunicacao-e-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (374, 'Assistente de Condomínio', 'Auxilia na administração de condomínios, atendimento a síndicos, condôminos e zeladores, elabora cartas de advertências, circulares e atas e participa em assembleias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-condominio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (375, 'Assistente de Contas a Pagar', 'Realiza conciliação bancária, emissão de cheques e follow-up de contas a pagar. Elabora planilhas, participa de negociação com fornecedores. Organiza documentos e arquivos em geral.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-contas-a-pagar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (376, 'Assistente de Contas a Pagar e Receber', 'Faz lançamento e classificação de notas fiscais e pedidos de compras. Confere pedidos x notas fiscais, envia controles de cheques e ordens de pagamentos aos bancos. Efetua pedido de baixa de títulos quitados e conciliação de pagamentos de fornecedores. Emite relatório de títulos em aberto. Realiza integração contábil das notas e pedidos. Controla lista de ordens de pagamentos e cheques da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-contas-a-pagar-e-receber/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (377, 'Assistente de Contas a Receber', 'Envia as faturas para os clientes e para a cobrança bancária. Negocia com clientes a quitação de débitos, envolvendo aspectos como prazos, abatimentos, juros e restituição de mercadorias. Contata clientes para solução de pendências relacionadas com o faturamento. Prepara relatórios com a posição das faturas em aberto e dos recebimentos efetuados. Calcula juros e outros encargos financeiros sobre pagamentos de faturas em atraso.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-contas-a-receber/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (378, 'Assistente de Contas Médicas', 'Realiza o lançamento de informações de prontuários e auxilia na análise de contas médicas, glosas e folha de pagamento de credenciados para assegurar o correto faturamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-contas-medicas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (379, 'Assistente de Conteúdo Web', 'Realiza atividades de apoio na criação visual das páginas do site. Efetua revisões constantes, aplicando testes de funcionamento nas criações de sites, com a finalidade de corrigir eventuais problemas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (380, 'Assistente de Contratos', 'Planeja a gestão administrativa dos contratos de longo prazo, desenvolve estratégias de mercado, define, análise e cumpre metas. Faz emissão de documentos de compra. Responsável por contrato de mão-de-obra especializada, dar apoio à fiscalização, acompanhamento de cronogramas financeiro. Supervisiona serviços executados, medições de pagamento junto as empresas contratadas e elabora relatórios de obras diários e semanais. Faz controle de EPIs, recebe e controla documentação administrativa e de SMS.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-contratos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (381, 'Assistente de Control Desk', 'Realiza a análise e apresentação de relatórios estatísticos de tráfego de ligações, faz acompanhamento de resultados das filiais, report diário e semanal e acompanhamento de incidentes sistêmicos. Atua com métricas em relação ao desempenho da operação, escala de trabalho, extração de relatórios em tempo real, com defasagem de horas, extração de relatórios e históricos, cálculos de pagamentos de chamadas, controle do nível de serviço, absenteísmo, mudança de horário da operação, controle dos problemas técnicos na central, apoio aos gerentes, coordenação e supervisor das operações de recuperação de crédito e outros controles da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-control-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (382, 'Assistente de Controladoria', 'Acompanha o planejamento orçamentário da empresa, controla indicadores de desempenho, receitas e despesas, a fim de definir formas de controle contábil e financeiro adequados à estratégia do negócio.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-controladoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (383, 'Assistente de Controle de Qualidade', 'Faz controle de qualidade e inspeção dos materiais recebidos, definindo especificações dos produtos e materiais. Acompanha as etapas do processo de produção, conforme a especificação do produto, faz avaliação das mercadorias não conformes, elabora relatórios e auxilia em atividades inerentes ao controle de qualidade. Monitora a qualidade da produção na empresa, verifica os procedimentos operacionais de higiene e boas práticas de fabricação, elabora os laudos de análise fornecidos aos clientes conforme especificação técnica interna.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-controle-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (384, 'Assistente de Controles Internos', 'Auxilia na elaboração de relatórios gerenciais e mapeamento de processos e fluxogramas administrativos e financeiros, para conferir o cumprimento dos processos operacionais e evitar a ocorrência de erros, desperdícios ou irregularidades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-controles-internos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (385, 'Assistente de Credenciamento', 'Presta assistência no credenciamento, descredenciamento ou extensão de procedimentos, negocia e prospecta novos estabelecimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-credenciamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (386, 'Assistente de Crédito e Cobrança', 'Realiza cadastro de clientes, analisa limite de crédito e pesquisa pendências em órgãos de proteção. Controla pagamentos, acompanha faturas em andamento e faz cobrança de valores em aberto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-credito-e-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (387, 'Assistente de Crédito Imobiliário', 'Avalia o risco pela concessão de crédito e emite parecer sobre as propostas de vendas mediante análise da capacidade financeira dos clientes. Respeita os procedimentos de análise e a política de crédito da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-credito-imobiliario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (388, 'Assistente de Criação', 'Cria e finaliza peças publicitárias, como catálogos, folhetos, logotipos, entre outros, de acordo com briefing, auxilia na concepção de ideias e desenvolvimento de identidade visual, adequando às necessidades dos clientes e do mercado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-criacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (389, 'Assistente de CRM', 'Atua com padronização, limpeza e manutenção do mailing, presta suporte no desenvolvimento do projeto de relacionamento (sistema de CRM). Cria mailings e atua com a distribuição, controle de protocolos e reenvios. Realiza impressão de etiquetas diversas e manuseio de convites. Realiza atendimento ao RSVP de eventos via telefone e e-mail. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-crm/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (390, 'Assistente de Customer Service', 'Atualiza cadastros, acompanha pedidos de vendas, como prazos de pagamentos, preços e descontos dos produtos e desenvolve relacionamento com clientes, a fim de entender suas necessidades, esclarecer dúvidas e solucionar reclamações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-customer-service/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (391, 'Assistente de Custos', 'Elabora planilhas de custos, faz apontamento de horas, controle de custos, conciliação das contas do estoque e emissão de relatórios contábeis. Acompanha os processos de orçamentos, analisando e controlando estoques, além de administrar inventários variáveis ou fixos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-custos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (392, 'Assistente de Departamento Pessoal', 'Presta suporte no preparo e cálculo da folha de pagamento, rescisões, férias e recolhimento de contribuições. Realiza manutenção do cadastro de funcionários, controla benefícios e acompanha fluxo de ponto eletrônico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-departamento-pessoal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (393, 'Assistente de Desembaraço Aduaneiro', 'Controla documentos de importação, elabora o registro de DI (Declaração de Importação) e LI (Licença de Importação) e acompanha a liberação de cargas na alfândega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-desembaraco-aduaneiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (394, 'Assistente de Design', 'Atua com desenvolvimento de produtos, criação de estampas, fichas técnicas, criação de catálogos e materiais promocionais. Realiza pesquisas de moda e de mercado para o desenvolvimento de novos produtos. Cria, altera e adapta artes para materiais impressos, cartuchos e/ou etiquetas. Cria novas facas para estojos promocionais, gerencia arquivos digitais de embalagens, faz análise e diagramação de imagens, códigos de barras, textos ou cores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-design/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (395, 'Assistente de Design Gráfico', 'Auxilia na criação de materiais gráficos de comunicação visual, como banners, catálogos, panfletos, dentre outros, desenvolve logotipos e define diagramação, na seleção de letras para textos e tratamento de imagens.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-design-grafico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (396, 'Assistente de Diretoria', 'Organiza e controla compromissos diários da Diretoria, faz a elaboração de relatórios, reservas de passagens e hotéis para viagem, controle de compromissos particulares e organização de arquivos. Implementa as providências para cumprimento dos compromissos da Diretoria Executiva. Responde pela qualidade do fluxo de informações e do arquivamento de documentos, otimizando o tempo no desempenho da gestão da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-diretoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (397, 'Assistente de Documentação', 'Realiza catalogação, indexação e arquivamento de documentos e controla arquivo para organizar e facilitar acesso às informações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-documentacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (398, 'Assistente de E-commerce', 'Atua com o atendimento operacional do site e-commerce, pedidos, inclusão de produtos e atualização do site. Desenvolve as campanhas de marketing, mídias sociais e mala direta. Pesquisa e desenvolve o conteúdo. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-e-commerce/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (399, 'Assistente de Endomarketing', 'Auxilia no desenvolvimento de conteúdos para comunicação interna, participa na organização de eventos da empresa e elabora briefing de campanhas motivacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-endomarketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (400, 'Assistente de Engenharia', 'Auxilia o engenheiro na coordenação dos trabalhos fazendo levantamento do material em projetos, medições de empreiteiros e recebimentos de serviços. Elabora propostas técnicas e comerciais, a fim de definir o preço técnico e relatórios de acompanhamento dos projetos contratados pela engenharia. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-engenharia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (401, 'Assistente de Estilo', 'Presta apoio no desenvolvimento de peças de vestuário, auxilia na escolha de modelos, tecidos e aviamentos e realiza pesquisa de tendências para planejamento de coleções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-estilo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (402, 'Assistente de Estoque', 'Recepciona, confere e armazena materiais no estoque. Faz os lançamentos da movimentação de entrada e saída e os distribui para expedição. Emite relatórios para acompanhamento e execução das tarefas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-estoque/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (403, 'Assistente de Eventos', 'Presta assistência na organização de eventos, acompanha a montagem e desmontagem, realiza cotação e negociação com fornecedores e controla os processos desde a entrada do projeto até a finalização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-eventos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (404, 'Assistente de Expedição', 'Cadastra a entrada e saída de produtos e materiais no sistema, realiza lançamento de notas fiscais, manutenção do sistema de estoque e registro de fretes e libera mercadorias de acordo com entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-expedicao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (405, 'Assistente de Facilities', 'Auxilia no acompanhamento dos serviços de manutenção predial, recepção, limpeza, telefonia, segurança patrimonial e compras. Elabora contratos de prestação de serviços e relatórios com informações sobre gastos e lucros e realiza cotação de materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-facilities/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (406, 'Assistente de Faturamento', 'Emite, classifica e confere notas ficais e boletos de cobrança, calcula impostos e retenções e controla pedidos faturados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-faturamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (407, 'Assistente de Faturamento Hospitalar', 'Analisa faturamento de contas médicas, como materiais, medicamentos, exames e honorários, confere contas de pacientes, acompanha recursos de glosas, realiza fechamento de guias e emite notas fiscais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-faturamento-hospitalar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (408, 'Assistente de Fotógrafo', 'Ajuda o fotógrafo em estúdio e ambiente externo na montagem de equipamentos. Realiza edição de fotografias, diagramação para impressão e atende clientes para agendamentos de ensaios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-fotografo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (409, 'Assistente de Franquias', 'Atende clientes e prospecta novos franqueados. Acompanha o processo de abertura de franquias, presta suporte no treinamento comercial e controla o planejamento para implantação da loja, a fim de garantir a padronização e atender o prazo estabelecido.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-franquias/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (410, 'Assistente de Frota', 'Realiza monitoramento e rastreamento de veículos, pagamento de fretes e atendimento ao cliente, a fim de controlar o custo total das entregas. Verifica a necessidade de manutenção preventiva dos veículos e auxilia na administração de combustíveis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-frota/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (411, 'Assistente de Garantia da Qualidade', 'Auxilia na realização de auditorias internas de produtos, serviços e processos de fabricação e analisa e revisa procedimentos operacionais, para garantir a aplicação dos padrões de qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-garantia-da-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (412, 'Assistente de Governança', 'Acompanha e controla as atividades de limpeza e conservação dos apartamentos e áreas públicas do hotel. Participa do processo de compra e realiza contato com fornecedores. Elabora escalas de trabalho e férias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-governanca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (413, 'Assistente de Implantação', 'Instala e repara em estabelecimentos comerciais, industriais, residenciais, linhas e aparelhos de telecomunicações, equipamentos de comutação e telefonia, de transmissão e telefonia e de energia em telefonia. Repara aparelhos em laboratório. Controla resultados de funcionamento de linhas, aparelhos, redes de cabos e equipamentos instalados, testando, analisando indicadores de desempenho e registrando informações técnicas e operacionais das atividades realizadas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (414, 'Assistente de Informática', 'Atende chamados de suporte dos usuários da rede, de computadores, envolvendo a montagem, reparos e configurações de equipamentos e na utilização do hardware, softwares disponíveis e pontos de rede. Treina os usuários nos aplicativos disponíveis. Efetua a manutenção e conservação dos equipamentos. Cadastra acessos e utilização da rede, mediante formulário de inclusão de usuário de TI. Instala softwares de upgrade e faz adaptações e modificações para melhorar o desempenho dos equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-informatica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (415, 'Assistente de Infraestrutura', 'Realiza manutenção de infraestrutura de redes e computadores, instala sistemas e configura softwares e servidores e presta suporte técnico aos usuários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-infraestrutura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (416, 'Assistente de Inteligência de Mercado', 'Coleta, consolida e analisa informações sobre mercado e concorrentes, avalia o desempenho da empresa nos negócios, acompanha a elaboração do planejamento de marketing e reporta dados à equipe de vendas para desenvolvimento de ações estratégicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-inteligencia-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (417, 'Assistente de Inventário', 'Realiza inventário de estoque físico, controla entrada e saída de mercadorias e elabora relatórios sobre divergências, a fim de garantir a qualidade da cadeia logística.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-inventario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (418, 'Assistente de Legalização', 'Auxilia nos processos de abertura, alteração e encerramento de empresas, emissão de certidões e regularização fiscal e cadastral no âmbito dos órgãos públicos e elabora contratos sociais, procurações e documentos legais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-legalizacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (419, 'Assistente de Licitação', 'Acompanha processos licitatórios, busca e analisa editais, cadastra e renova a empresa em órgãos públicos e privados e controla homologações, a fim de viabilizar a participação em pregões eletrônicos e presenciais, organizando toda a documentação necessária.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (420, 'Assistente de Locação', 'Analisa documentos para locação de imóveis, desenvolve relacionamento com locadores e locatários e elabora contratos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-locacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (421, 'Assistente de Logística', 'Atua com gestão de estoque, recebimento, armazenagem e expedição. Realiza inventários, emite nota fiscal eletrônica, elabora planilhas eletrônicas e atende cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-logistica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (422, 'Assistente de Loja', 'Recebe e confere produtos e mercadorias, lança itens no estoque, recebe o pagamento dos clientes. Fecha caixa, lança contas a pagar e receber e organiza o malote. Executa depósito diários, atende telefone, recebe e encaminha e-mails e consulta cheques e/ou crédito de clientes junto ao setor ou sistema de cadastro para atendimento de pedidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (423, 'Assistente de Manutenção Predial', 'Ajuda na manutenção predial, preventiva e corretiva, com cabines primárias, manobras, geradores e no breaks. Realiza pequenos reparos elétricos, hidráulicos e de alvenaria.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (463, 'Assistente de Secretaria', 'Realiza rotinas administrativas, compras de passagens, aluguéis de carros, compras corporativas e controle de agenda. Envia e recebe correspondências, além de organizar arquivos e assessorar os demais colaboradores do departamento administrativo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-secretaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (424, 'Assistente de Marketing', 'Auxilia o gerente no acompanhamento do desempenho de vendas e fornecedores, presta suporte no contato com agência de propaganda, faz o controle de notas fiscais, apoio em negociações, implementação de campanhas publicitárias e de vendas. Elabora parceria com o gerente de marketing em campanhas e promoções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (425, 'Assistente de Marketing Digital', 'Atua na prospecção de clientes, através de mídias digitais, Facebook, sites, e-mail, marketing, google Adwords e Google Analytics. Realiza manutenção dos sites, atualização dos blogs e redes sociais. Cria e faz o acompanhamento de Google Adwords e elaboração de artes no Photoshop.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-marketing-digital/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (426, 'Assistente de Meio Ambiente', 'Auxilia na inspeção de normas ambientais adotadas pela empresa, acompanha prazos para licenciamento, organiza controle de resíduos e participa de projetos de saneamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-meio-ambiente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (427, 'Assistente de Merchandising', 'Auxilia na administração da carteira de clientes e executa o planejamento de vendas. Acompanha os orçamentos e levantamentos de custos para as ações de divulgação, comunicação e propaganda. Treina expositores, controla a distribuição e chegada de mostruários do departamento e acompanha novas tendências de mercado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-merchandising/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (428, 'Assistente de Mídia', 'Analisa e aprova novas lojas através de critérios de qualidade, atende as necessidades dos clientes que entram em contato por meio do fale conosco, controla e acompanha casos críticos de reclamação que devem ser tratados com prioridade. Planeja e desenvolve peças publicitárias para impulsionar a receita. Avalia e acompanha os resultados para planejar novas ações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-midia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (429, 'Assistente de Mídias Sociais', 'Atua com mídias sociais, interage nas mídias sociais, gera conteúdo relacionado a moda e redige redações. Gera relatórios e análises, cria e divulgar conteúdo, administra entrada e saída de usuários, identifica melhorias e oportunidades.  Alimenta diariamente, com conteúdos relevantes, o blog, facebook, twitter, instagram e flickr da empresa. Coloca fotos de produtos, trata imagens que serão utilizadas nas mídias e mantém contato da assessoria de imprensa.  Administra, gerar conteúdo, cria anúncios, interage com clientes e colaboradores. Realiza o planejamento e criação de conteúdo para redes sociais, como publicações, textos e mecânicas de ações para melhoria de desempenho, extração de dados, cruzamento e análises. Elabora insights e recomendações a partir dos dados analisados. Produz relatórios de métricas, monitoramento e relatórios pontuais. Analisa constantemente a arquitetura de navegação, estrutura de links, conteúdo de páginas do site. Recomenda melhorias visando ganho de posicionamento nos mecanismos de busca tomando como base informações extraídas dos sites concorrentes e de ferramentas de Web Analytics. Identifica oportunidades para a marca em redes sociais. Acompanha o desempenho do site e campanhas por meio de relatórios, inclusive do Google Adwords. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-midias-sociais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (430, 'Assistente de Modelagem', 'Auxilia no desenho de peças, de acordo com tabela de medidas, realiza provas e ajustes e acompanha processo de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-modelagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (431, 'Assistente de Monitoramento', 'Faz monitoramento de câmeras via sistema CFTV, resgata imagens gravadas pelo sistema de segurança e armazena em mídia externa e ativa. Desativa alarmes de segurança. Lança as informações em sistema e preenche planilhas com controle diário das informações lançadas em livros de ocorrências.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-monitoramento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (432, 'Assistente de Negócios', 'Participa na identificação de oportunidades de negócios, relacionamento com clientes e análise de mercado. Auxilia no desenvolvimento e implementação de ações de vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-negocios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (433, 'Assistente de Nutrição', 'Apoio aos nutricionistas e técnicos da área, atendimento aos clientes. Orienta sobre os produtos e organiza de relatórios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-nutricao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (434, 'Assistente de Orçamento', 'Auxilia no planejamento financeiro e orçamentário e elabora relatórios de despesas e cálculos, a fim de promover a eficiente utilização de recursos e contenção de custos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-orcamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (435, 'Assistente de Ouvidoria', 'Atua com análise das demandas de ouvidoria de forma imparcial. Busca subsídio necessário com as áreas para elaboração das respostas. Preenche relatórios de indicadores. Identifica melhorias nos processos internos. Garante a alta performance de qualidade para todos os atendimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-ouvidoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (436, 'Assistente de Patrimônio', 'Auxilia na classificação, identificação e inventário de bens patrimoniais da empresa, acompanhando divergências em estoque e mapa de movimentações para atualizar em sistema o controle de ativo imobilizado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-patrimonio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (437, 'Assistente de PCP', 'Atua com programação e controle de produção, alimenta planilhas e faz lançamentos de dados de relatórios. Obtém índice de produtividade e elabora gráficos de controle de qualidade e aparas. Lança no sistema informações dos produtos acabados e ajuda no planejamento da fábrica. Realiza acompanhamento da produção e faz análise da capacidade de produção. Atua com controle e definição de estoque de matéria-prima e insumos, define cronograma e prioridades de produção. Presta auxílio ao controle da produção interna do corte das peças, até a entrada do produto acabado na expedição e no estoque de produtos junto ao supervisor.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-pcp/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (438, 'Assistente de Pesquisa', 'Auxilia o analista nas pesquisas com fornecedores, contribui com a criação do briefing, verifica escopo, realiza cronograma, necessidades e orçamento. Aplica pesquisas de campo. Contribui com a elaboração de relatórios de pesquisa, análises e recomendações. Contribui na elaboração de relatórios analíticos contendo informações do consumidor, da marca e do mercado. Auxilia na edição e montagem de vídeos de pesquisa. Participa de reuniões com as áreas da empresa e fornecedores. Participa da elaboração do planejamento de marketing.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-pesquisa/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (439, 'Assistente de Pesquisa de Mercado', 'Busca informações sobre o mercado, prospecta novos clientes e acompanha as ações de vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-pesquisa-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (440, 'Assistente de Pesquisa e Desenvolvimento', 'Atua com pesquisa e desenvolvimento de produtos, análise de tendências do mercado, elaboração de projetos de novos produtos e de melhora de produtos já existentes. Desenvolve de novas embalagens que viabilizem a produção e manutenção da qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-pesquisa-e-desenvolvimento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (441, 'Assistente de Planejamento', 'Ajuda no desenvolvimento das atividades relacionadas a elaboração, manutenção e atualização do planejamento estratégico. Elabora orçamentos, auxilia no desenvolvimento de novos projetos, com objetivo de melhorar os processos operacionais e estratégicos da organização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-planejamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (442, 'Assistente de Pós-venda', 'Presta suporte ao cliente na pós-venda, analisa e soluciona reclamações e realiza contato para pesquisa de satisfação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-pos-venda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (443, 'Assistente de Prevenção de Perdas', 'Ajuda nas atividades de prevenção de perdas e na conservação da loja. Não permitir a entrada de pessoas fora dos horários permitidos e sem autorização. Verifica o respeito às normas de segurança e procedimentos administrativos. Prepara relatórios e faz o registro de ocorrências acontecidas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-prevencao-de-perdas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (444, 'Assistente de Pricing', 'Auxilia na análise de custo de mercadoria e produtos, envolvendo a aplicação de custos diretos e/ou descontos especiais, formação de preço de venda, impostos recuperáveis e não recuperáveis e participa na definição de política. Auxilia na elaboração de relatórios sobre pesquisa de mercado e desempenho de preços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-pricing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (445, 'Assistente de Processamento de Dados', 'Realiza implantação, conservação e alimentação do banco de dados e atualiza informações em sistema.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-processamento-de-dados/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (446, 'Assistente de Processos', 'Realiza diagnósticos para mapeamento de fluxograma, estruturação e descrição detalhada do processo. Elabora e divulga o procedimento relativo ao processo desenvolvido, identificando a oportunidade de melhoria e otimização do processo e da prática existente. Apoia o desenvolvimento do trabalho de melhoria contínua junto a área. Acompanha o desenvolvimento e a operacionalização da área de apoio e arquivo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-processos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (447, 'Assistente de Produção', 'Presta apoio nas operações de produção da empresa, controla programas de fabricação e auxilia na utilização eficaz de equipamentos, matérias-primas e pessoal.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (448, 'Assistente de Produção Editorial', 'Auxilia na elaboração, desenvolvimento e editoração das matérias selecionadas para composição de livros, jornais e revistas. Realiza diagramação, revisão e fechamento de arquivo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-producao-editorial/');
