INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (481, 'Assistente de Trade Marketing', 'Analisa informações de trade marketing do grupo e da concorrência. Planeja e organiza feiras, eventos de trade e campanhas promocionais. Acompanha a execução das atividades nos pontos de vendas, por telefone e pessoalmente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-trade-marketing/');
