INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (498, 'Assistente Técnico', 'Elabora desenhos técnicos, faz acompanhamento e leitura de projetos de estruturas e visitas técnicas e desenvolve relatórios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (499, 'Assistente Técnico de Campo', 'Realiza manutenção preventiva e auditoria das máquinas. Auxiliar na aplicação do treinamento técnico da equipe de manutenção do cliente. Presta atendimento remoto ao cliente com orientações referentes a problemas específicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-tecnico-de-campo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (500, 'Assistente Técnico de Edificações', 'Acompanha construção de obras, elabora planilha de quantidades e custos para controle orçamentário e controla pedidos de materiais e equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-tecnico-de-edificacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (501, 'Assistente Técnico de Vendas', 'Prospecta, identifica, visita e mantém relacionamento com clientes, oferece suporte técnico das especificações do produto, elabora propostas com definição de escopo técnico e condições comerciais para o fornecimento de soluções em automação e elabora relatórios comerciais periódicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-tecnico-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (502, 'Assistente Técnico em Eletrônica', 'Realiza atividades técnicas para a execução de projetos de engenharia nas áreas de geração, transmissão e distribuição de áudio e videoconferência, entre outros equipamentos eletrônicos. Auxilia na manutenção de equipamentos e suas aplicações, interpreta desenhos e técnicas de execução. Realiza manutenção e instalação nos equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-tecnico-em-eletronica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (503, 'Assistente Tributário', 'Auxilia no planejamento tributário, acompanha mudanças na legislação, apura tributos diretos e indiretos, elabora livros fiscais e entrega declarações mensais, semestrais e anuais, a fim de cumprir as obrigações fiscais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-tributario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (504, 'Assoalhador', 'Aplica assoalhos em edificações e residências, identifica dimensões das superfícies e escolhe o material.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assoalhador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (505, 'Atendente', 'Atua com atendimento ao cliente, recepciona, apresenta os produtos, realiza a organização de prateleiras, estocagem e organização do local. Faz a recepção de mercadorias e esclarecimento de dúvidas. Zela pelo bom atendimento, eficiência e produtividade. Cumpre as normas e procedimentos, realiza a precificação de produtos e reposição de mercadorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (506, 'Atendente de Assistência Técnica', 'Atende a demanda de solicitação de clientes, verifica a necessidade de agendar visitas para vistorias e assistência técnica, identifica qual serviço necessário, controla e informa quanto aos agendamentos de assistência técnica, direcionando-os conforme a necessidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-assistencia-tecnica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (507, 'Atendente de Câmbio', 'Atende clientes para a compra e venda de moeda estrangeira, venda de cartões internacionais e compra de cheques internacionais. Realiza negociação de taxas, emissão de boleto e cadastro de operação no sistema, arquiva dos boletos e realiza o fechamento diário do caixa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-cambio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (709, 'Bamburista', 'Opera máquina de mistura de matérias-primas na fabricação da borracha, acompanhando para transformar em massa uniforme.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/bamburista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (509, 'Atendente de E-commerce', 'Atende clientes da loja virtual por telefone, chat ou e-mail para auxílio na finalização de pedidos ou em dúvidas gerais. Cadastra produtos no site e participa na elaboração de estratégias comerciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-e-commerce/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (510, 'Atendente de Help Desk', 'Atendimento e baixa de chamados, auxilio no suporte e configuração de sistemas operacionais Windows e Linux, configurações de rede Windows e Linux, configurações e compartilhamentos de impressoras e scanner.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-help-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (511, 'Atendente de Marketing', 'Participa de reuniões de Briefing, apresentações, criação de relatórios e métricas. Atua na criação de propostas de ações promocionais inovadoras e apresentação de ROI.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (512, 'Atendente de Nutrição', 'Monta bandejas e carrinhos, conforme dieta prescrita a cada paciente e auxiliar na entrega para pacientes e familiares.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-nutricao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (513, 'Atendente de Pista', 'Recepciona clientes e tripulação no balcão de atendimento, embarque e desembarque, controla horários de pousos e decolagens e acompanha movimentação em solo, reboque e sinalização de aeronaves, de acordo com os Procedimentos Operacionais Padrão (POP).', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-pista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (514, 'Atendente de Pós-venda', 'Atua com atendimento ao cliente, esclarece dúvidas gerais de vendas da parte técnica, andamento e acompanhamento de pedidos. Zela pelo bom atendimento, eficiência e produtividade. Cumpre as normas e procedimentos, realiza a precificação de produtos e reposição de mercadorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-pos-venda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (515, 'Atendente de Restaurante', 'Presta atendimento ao cliente, entrega cardápios, anota pedidos e organiza salão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-restaurante/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (516, 'Atendente de SAC', 'Realiza ligações receptivas e ativas para prestação de informações e esclarecimento de dúvidas. Encaminha os problemas apresentados para às áreas responsáveis e acompanha pendências.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-sac/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (517, 'Atendente de Service Desk', 'Resolve problemas de hardware, software, redes dos usuários por telefone, e-mail ou chat. Responde às chamadas telefônicas, e-mails, mensagens instantâneas, e os tickets dos usuários. Atribui ordens de trabalho e incidentes para as equipes de apoio adequadas e acompanha até o fechamento. Responde e diagnostica os problemas através de conversas com os usuários, incluindo o reconhecimento do problema, os registros, a pesquisa, o isolamento, a resolução e os passos para acompanhamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-service-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (518, 'Atendente de Sinistro', 'Atende clientes na solução de dúvidas, abertura de sinistros junto a companhias seguradoras e agendamento de vistoria.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-sinistro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (519, 'Atendente de Suporte Técnico', 'Presta suporte técnico (telefônico e acesso remoto) aos usuários dos sistemas desenvolvidos pela empresa, além de efetuar visitas técnicas referentes a implantação dos sistemas, configuração e treinamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-suporte-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (520, 'Atendente de Turismo', 'Realiza atendimento ativo e receptivo de clientes para esclarecer dúvidas sobre pacotes turísticos nacionais e internacionais e vender reserva de passagens e hotel, aluguel de carros e seguro viagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendente-de-turismo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (521, 'Atendimento Publicitário', 'Prospecta e atende as necessidades de comunicação do produto ou serviço do cliente, mantém relacionamento entre agência e cliente, defende ideias de campanhas criadas pela agência junto ao cliente, elabora briefings para a criação e cumprimento de metas e faz follow up dos trabalhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atendimento-publicitario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (522, 'Ator', 'Interpreta e representa um personagem numa ação dramática, com base em textos, estímulos visuais, sonoros, dentre outros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ator/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (523, 'Atuário', 'Elabora cálculos, analisa riscos e determina prêmios a serem pagos em operações de seguros, previdência complementar, planos de saúde e títulos de capitalização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/atuario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (524, 'Auditor', 'Responde pelas atividades relacionadas à obtenção e à manutenção de certificados, participa de auditorias internas e externas e realiza a revisão e elaboração de normas e procedimentos dentro dos padrões preestabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (525, 'Auditor Contábil', 'Realiza auditoria operacional e contábil, baseando-se em programas específicos de auditoria. Verifica os livros contábeis, fiscais e comerciais apurando a veracidade entre os lançamentos e os documentos. Atua de forma integrada com os auditores externos, visando apoiá-los quando das análises das demonstrações financeiras e contábeis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (526, 'Auditor de Contas Médicas', 'Analisa contas médicas, como processos de internação, diagnóstico médico, exames e procedimentos realizados, conferência de materiais e medicamentos e recurso de glosas, a fim de identificar possíveis irregularidades ou inconformidades e aplicar ações corretivas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-de-contas-medicas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (527, 'Auditor de Estoque', 'Realiza auditoria de estoques, controla inventário e movimentação de materiais e analisa divergências ou conformidade no cumprimento dos procedimentos estabelecidos, a fim de contribuir para padronização dos processos e aplicar medidas preventivas e corretivas para manter a integridade financeira e física do patrimônio.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-de-estoque/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (528, 'Auditor de Loja', 'Verifica os livros contábeis, fiscais e auxiliares da empresa. Realiza auditorias na área de almoxarifado, verificando a movimentação de materiais em estoque para confronto dos dados físicos com os controles internos e a contabilidade. Faz as recomendações necessárias para melhor produtividade do trabalho e qualidade do produto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (529, 'Auditor de Processos', 'Realiza auditorias internas dos sistemas e procedimentos relacionados com a qualidade e processos, identificando não conformidades e recomendando ações corretivas. Realiza treinamentos e participa da integração apresentando a área da auditoria de processos de qualidade a novos colaboradores. Participa de reuniões para apresentação dos indicadores e monitoramento dos resultados das auditorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-de-processos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (530, 'Auditor de Qualidade', 'Inspeciona e dimensiona os produtos em fabricação. Preenche relatórios diários e efetua ocorrências referentes às inspeções realizadas. Faz o controle de produtos não conforme em todas as fases do processo produtivo. Controla prazos de aferição dos equipamentos de medição e ensaios conforme procedimentos documentados. Executa aferição e calibração dos equipamentos de medição e ensaios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (531, 'Auditor de Sistemas', 'Atua na elaboração de normas e procedimentos, desenho de processos e regras de negócios. Elabora os relatórios explicativos dos indicadores de qualidade nas auditorias efetuadas. Efetua mapeamento dos processos para identificação de oportunidades de melhorias, avaliação dos riscos e controles existentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-de-sistemas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (532, 'Auditor Externo', 'Realiza auditorias externas para obtenção e manutenção de certificados, documenta procedimentos de acordo com objetivos e metodologia e aplica recomendação de alterações nos controles e processos para atender os padrões estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-externo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (533, 'Auditor Financeiro', 'Realiza análise e conferência de balancetes, fiscaliza cumprimento de normas, com conferência de despesas e verificação de documentos comprobatórios. Elabora relatórios, providência e corrige os problemas apontados. Realiza reuniões com administradores e sobre problemas financeiros apontados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-financeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (534, 'Auditor Fiscal', 'Realiza auditoria fiscal, analisando escrituração de livro, apuração de imposto e cumprimento de obrigação acessória. Estuda e acompanha legislação fiscal e tributária.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-fiscal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (535, 'Auditor Interno', 'Elabora, executa e se responsabiliza por procedimentos técnicos, examina a integridade, adequação e eficácia dos controles internos e das informações físicas, contábeis, financeiras e operacionais da empresa evitando riscos de fraudes, erros, ineficiências e outras irregularidades. Ajuda a eliminar desperdícios, facilita tarefas, apoio à gestão e passa informações a gestão sobre atividades executadas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (536, 'Auditor Noturno', 'Confere procedimentos, documentos, contas dos hóspedes e pagamentos processados na recepção e elabora balancete do movimento de vendas, a fim de garantir a qualidade das informações para tomada de decisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-noturno/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (537, 'Auditor Trainee', 'Acompanha e desenvolve atividades na área de Auditoria, envolvendo a análise de documentos e processos organizacionais, a fim de capacitar para ocupação do cargo profissional planejado e aprimorar o conhecimento dos negócios, produtos ou serviços da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auditor-trainee/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (538, 'Auxiliar Administrativo', 'Auxilia diversas áreas de uma organização nas rotinas de digitação, arquivo de documentos, distribuição de correspondência e serviços externos. Elabora relatórios e planilhas de controle.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-administrativo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (539, 'Auxiliar Comercial', 'Auxilia no acompanhamento de pedidos de vendas, prazos de entrega e estoque de mercadorias e acompanha a elaboração de propostas comerciais. Levanta dados sobre o comportamento dos produtos da empresa e mercado e alimenta planilhas para projeções e comparativos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (540, 'Auxiliar Contábil', 'Realiza classificações e conciliações contábeis, registra lançamentos e auxilia na apuração de impostos e na elaboração de balancetes. Preenche guias de recolhimento, junto aos órgãos do governo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (541, 'Auxiliar de Acabamento Gráfico', 'Atua na organização, acabamento de produção e revisão de materiais gráficos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-de-acabamento-grafico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (542, 'Auxiliar de Açougue', 'Faz atendimento ao cliente e fornece orientação sobre carnes, adquirindo habilidade no preparo. Ajuda o açougueiro no corte de carne, separação e desossa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-de-acougue/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (543, 'Auxiliar de Almoxarifado', 'Envia e recebe materiais, separa e organiza mercadorias, auxilia na verificação e na embalagem de produtos prontos, conferência de produtos recebidos e anota os dados em planilhas, verifica o estoque e anota os produtos que estão em falta ou sem saída.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (544, 'Auxiliar de Arquitetura', 'Auxilia arquitetos no desenvolvimento de projetos, realiza a especificação de materiais, elabora relatórios, controla contratos e acompanha cronograma.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-de-arquitetura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (545, 'Auxiliar de Arquivo', 'Auxilia na atividades de arquivo, tratamento e recuperação de documentos e abertura e fechamento de pasta.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-de-arquivo/');
