INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (703, 'Azulejista', 'Faz revestimentos em paredes, pisos, muros e outras partes de construções com ladrilhos, pastilhas, cerâmicas ou material similar. Trabalha em obras novas ou em reformas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/azulejista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (704, 'Babá', 'Cuida de bebês e crianças, zelando pelo bem-estar, saúde, alimentação, higiene pessoal, educação, cultura, recreação e lazer. Acompanha horários dos medicamentos, alimentação, escola e banho. Realiza atividades que estimulam o desenvolvimento físico, emocional e motor da criança, viagens e consultas médicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/baba/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (705, 'Back Office', 'Confere e oferece o tratamento adequado a todas as ocorrências geradas pelo cliente e que não foram resolvidas pela equipe de SAC. Acompanha os pedidos não finalizados garantindo a execução dos prazos pré - estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/back-office/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (706, 'Back Office de Vendas', 'Presta suporte no atendimento aos clientes para solucionar problemas no faturamento e cobrança de serviços prestados e controla as ocorrências abertas da área de vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/back-office-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (707, 'Balanceiro', 'Realiza a pesagem de veículos, matérias-primas, produtos e mercadorias para processo de fabricação ou transporte.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/balanceiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (708, 'Balconista', 'Atende clientes em lojas, telefone e realiza coleta de pedidos e encomendas. Demonstra produtos e efetua vendas internas. Confere, armazena, vende, cobra e organiza mercadorias. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/balconista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (712, 'Berçarista', 'Ajuda na higiene, banhos e alimentação. Zela sempre pelo bem estar da criança. Acomoda as crianças e cuida da organização do local. Auxilia os professores e coordenadores nos cuidados básicos com as crianças da educação infantil.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/bercarista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (713, 'Bibliotecário', 'Analisa, sintetiza e controla a entrada e saída de livros, revistas, documentos, fotos, filmes e vídeos, organiza e monitora a permanência dos mesmos. Realiza atendimento aos usuários, esclarece dúvidas e orienta na localização de material de pesquisa, autores e temas. Insere dados em sistema, seleciona e adquire material bibliográfico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/bibliotecario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (715, 'Biologista', 'Faz procedimentos técnicos de análises clínicas em materiais biológicos, verifica a adequação do material coletado, solicita novas amostras quando necessário. Analisa consistência dos resultados dos exames e providencia a sua validação e liberação em sistema.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/biologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (716, 'Biólogo', 'Analisa as relações entre os seres e o meio ambiente.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (717, 'Biomédico', 'Pesquisa e realiza exames laboratoriais de doenças humanas e animais, suas causas e os meios de tratá-las. Identifica, classifica e estuda os microrganismos causadores de doenças e procura medicamentos e vacinas para combatê-las.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/biomedico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (718, 'Bioquímico', 'Estuda a química dos processos biológicos que ocorrem em todos os seres vivos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (719, 'Biotecnólogo', 'Estuda as reações químicas que ocorrem nos seres vivos, auxilia a medicina na fabricação de remédios, soros e exames laboratoriais. Analisa microorganismos, observando as reações destes corpos em contato com o meio ambiente ou com substâncias das mais variadas, para a produção de derivados de células cultivadas para a purificação do material genético. Desenvolve produtos a partir de suas pesquisas, nas indústrias das áreas médicas, de bebidas e alimentação e com uma maior preocupação com a ecologia.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/biotecnologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (720, 'Blaster', 'Realiza detonação de explosivos para desmonte de rochas em minas ou pedreiras, de acordo com plano de fogo estabelecido.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/blaster/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (721, 'Blogueiro', 'Administra e elabora textos para blogs e redes sociais. Mantém o relacionamento com clientes, administra e divulga a empresa e suas promoções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/blogueiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (722, 'Bloquista', 'Realiza processos de acabamento, intercalação, colagem, encadernação e blocagem de materiais gráficos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/bloquista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (723, 'Bombeiro Civil', 'Protege pessoas e patrimônios de riscos de incêndios e vazamentos, inspeciona e testa equipamentos de segurança. Realiza salvamentos terrestres, aquáticos e em altura e presta primeiros socorros. Treina equipes e brigadas em situações de emergência.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/bombeiro-civil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (724, 'Bombeiro Industrial', 'Atua com acompanhamento e liberação de serviços especiais (serviço em altura, solda e corte a quente, espaço confinado, recebimento de produtos químicos e liberação de resíduos industriais). Realiza inspeções nos equipamentos de prevenção e combate a incêndio registrando a rotina em relatório específico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/bombeiro-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (725, 'Booker', 'Seleciona modelos para catálogos, desfiles, comerciais, filmes, dentre outras atividades. Realiza contato direto com produtoras para seleção de casting.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/booker/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (726, 'Bordador', 'Confecciona bordados, acabamentos e conferência das peças têxteis. Programa, regula e prepara máquina para confecção de material, além de ajustar medidas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/bordador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (727, 'Borracheiro', 'Monta, desmonta e alinha pneus e rodas. Calibra e balanceia os pneus.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/borracheiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (728, 'Business Partner', 'Define e implementa estratégias de recursos humanos e viabiliza alterações de estrutura e movimentação de pessoal em clientes, por meio do mapeamento dos processos, análise de políticas e identificação de melhorias alinhados aos objetivos organizacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/business-partner/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (729, 'Cabeleireiro', 'Faz cortes, escovas, aplicação de cremes, químicas e tinturas. Realiza terapia capilar, aplicação de produtos químicos para ondular, alisar ou colorir cabelos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cabeleireiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (730, 'Cabista', 'Instalação e montagem de infraestrutura, passagem e organização do cabeamento metálico e óptico com acompanhamento do líder. Identifica cabos, abertura de cabos, faz certificação de pontos com equipamentos específicos e montar racks.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cabista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (731, 'Caixa de Banco', 'Realiza operações bancárias, venda de produtos e serviços, acompanha o fluxo de caixa, verifica os valores e documentos financeiros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/caixa-de-banco/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (732, 'Calceteiro', 'Faz pavimentação de leitos de estradas, ruas e calçadas, utilizando areia ou terra e recobre com paralelepípedos ou blocos de concreto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/calceteiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (733, 'Calculista', 'Elabora cálculos trabalhistas, como horas-extras, revisão salarial e aposentadoria. Analisa e confere verbas rescisórias, adicional de periculosidade ou insalubridade, comissão e atualização monetária.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/calculista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (734, 'Caldeireiro', 'Atua com montagens, soldagens e fabricação de componentes mecânicos, elaboração de vistorias de equipamentos mecânicos, inspeções visuais em componentes construídos em chapas metálicas e em tubulações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/caldeireiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (735, 'Camareiro', 'Realiza a limpeza, arrumação e organização de locais e conservação do ambiente. Organiza vestuários de clientes, identifica e controla as saídas, devoluções e substituições de roupas e acessórios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/camareiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (736, 'Caracterizador', 'Cria e desenvolve projetos de características físicas artificiais, maquilagem e penteados de personagens. Também participa na definição do figurino e adereços de atores em novelas, dentre outras atrações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/caracterizador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (737, 'Carpinteiro', 'Planeja trabalhos de carpintaria, prepara canteiro de obras e monta formas metálicas. Confecciona formas de madeira e forro de laje (painéis). Constrói andaimes e proteção de estruturas de madeira para telhado, portas e esquadrias. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/carpinteiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (738, 'Cartazista', 'Confecciona cartazes, faixas, banners e demais materiais de comunicação visual para estabelecimentos comerciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cartazista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (739, 'Carteiro', 'Realiza entrega de correspondências e encomendas em residências e empresas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/carteiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (740, 'Caseiro', 'Zela pelo patrimônio, faz pequenos reparos e serviços elétricos, pintura. Contribui para a conservação e o adequado funcionamento do lugar.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/caseiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (741, 'Cenógrafo', 'Elabora o projeto cenográfico de teatro, show, filmes e entretenimento em geral, formula o conceito artístico e pesquisa a obra no seu contexto histórico, perfil dos personagens, autor e conteúdo. Estuda a viabilidade na utilização de materiais e coordena equipes de cenotécnica, produção cenográfica e de montagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cenografo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (742, 'Cenotécnico', 'Estrutura o esqueleto de cenários de espetáculos e eventos, envolvendo a montagem e desmontagem e construção de adereços, conforme maquetes, croquis e plantas fornecidas pelo Cenógrafo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cenotecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (743, 'Chapeiro', 'Atua no preparo e montagem de lanches quentes, pratos rápidos e porções em geral. Organiza o local de trabalho, utensílios e geladeiras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chapeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (744, 'Chaveiro', 'Realiza cópia de chaves comuns e codificadas e abertura, instalação e conserto de fechaduras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chaveiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (745, 'Chefe de Bar', 'Supervisiona a rotina de atendimento ao cliente, elabora cardápio de coquetéis e drinks e controla estoque de bebidas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-bar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (746, 'Chefe de Compras', 'Analisa as solicitações de compra de matérias-primas, máquinas e equipamentos, acompanha a pesquisa de fornecedores e negociação de preços e controla prazos de entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (747, 'Chefe de Confeitaria', 'Organiza e supervisiona os serviços de confeitaria no preparo de massas, pães, bolos e demais doces. Controla estoque para reposição de materiais e acompanha processo de produção para assegurar a qualidade dos produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-confeitaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (748, 'Chefe de Costura', 'Orienta equipe nas atividades, manual ou à máquina, de costura de tecidos para produzir peças de vestuários, conforme moldes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-costura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (749, 'Chefe de Cozinha', 'Supervisiona o preparo e montagem de pratos nos restaurantes, orienta e verifica a elaboração das receitas e controla o estoque de alimentos, para assegurar a qualidade do serviço prestado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-cozinha/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (750, 'Chefe de Departamento Pessoal', 'Presta auxílio na administração de pessoas como admissão, folha de pagamento, rescisão, folha de ponto, benefícios, desde controle, cálculos e apontamentos. Fecha folha de pagamento, atua com imposto de renda, RAIS, GEFIF e atendimento aos funcionários. Faz o fechamento e imputa para a folha de pagamento e para o sistema de banco de horas. Realiza a apuração de banco de horas e informa o saldo via relatório para os gestores, executa homologação de rescisão de contrato e atende ao público interno.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-departamento-pessoal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (751, 'Chefe de Expedição', 'Acompanha a logística e o departamento de recebimento e expedição de mercadorias, controla o estoque. Faz vistoria do estoque, elabora romaneios de entrega e relatórios de atividades dos subordinados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-expedicao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (752, 'Chefe de Fila', 'Recepciona clientes em restaurantes e encaminha até a mesa adequada, de acordo com as preferências ou reserva. Controla os serviços de garçons, cumins e copeiros, a fim de assegurar a qualidade no atendimento aos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-fila/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (753, 'Chefe de Plantão Médico', 'Garante a condução do atendimento ao paciente. Lidera equipe de médicos do pronto-socorro e emergência. Acompanha atendimentos e relatórios gerenciais. Suporte a equipe médica, enfermagem e pacientes. Orienta a equipe no que for necessário.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-plantao-medico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (754, 'Chefe de Produção', 'Planeja, organiza e gerencia as atividades de produção, dentro das especificações e padrões de qualidade. Promove melhorias nos processos, visando atender as demandas e oscilações do plano de produção. Implanta controles e métodos usando técnicas e ferramentas da qualidade. Desenvolve e analisa relatórios gerenciais e estatísticos para monitoramento dos indicadores e auxilia no processo de treinamento e orientação dos colaboradores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (755, 'Chefe de Recepção', 'Lidera equipe de recepção, desenvolve procedimento de atendimento pessoal e telefônico e acompanha ocorrências e relatórios da auditoria noturna para reportar à gerência as informações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/chefe-de-recepcao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (756, 'Churrasqueiro', 'Prepara, corta e assa carnes bovinas e suínas, realiza manutenção da churrasqueira e organiza o local de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/churrasqueiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (757, 'Cilindrista', 'Prepara máquinas e moldes para fabricação de produtos de borracha. Acomoda a matéria-prima em recipientes e lamina as massas de acordo com as solicitações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cilindrista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (758, 'Cinegrafista', 'Opera câmera, ajusta foco e ângulo e define composição de quadro para tomar cenas de filmes e espetáculos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cinegrafista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (759, 'Classificador de Grãos', 'Faz classificação de produtos agrícolas de acordo com as normas vigentes, prepara ambientes para realizar análises, redige documentos e demais atividades da área.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/classificador-de-graos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (760, 'Cliente Oculto', 'Avalia a qualidade do atendimento prestado e levanta informações específicas sobre produtos e serviços. Visita locais e simula ser um cliente comum para realizar tarefas como comprar um produto, realizar perguntas, fazer reclamações ou se comportar, no estabelecimento, de determinada maneira. Fornece relatórios sobre como foi a experiência do cliente e aplica feedbacks.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cliente-oculto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (761, 'Coach', 'Assessora o cliente, levando-o a refletir, define ações e, principalmente, age em direção a seus objetivos, metas e desejos. Fornece suporte para uma pessoa mudar da maneira que deseja, assim como auxiliar a seguir a direção desejada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coach/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (762, 'Cobrador de Ônibus', 'Realiza cobrança de tarifas em transportes coletivos e repassa o troco se necessário. Presta informações aos usuários e mantém a ordem e limpeza do veículo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cobrador-de-onibus/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (768, 'Colorista', 'Prepara tintas e desenvolve novas cores, de acordo com tendências, verifica pigmentação e realiza testes para atender os padrões de qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/colorista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (769, 'Colorista de Papel', 'Desenvolve tintas, elabora formulário de cores, analisa e valida tonalidades e controla qualidade de impressão para certificar a conformidade com os padrões técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/colorista-de-papel/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (770, 'Colorista Têxtil', 'Desenvolve cartela de cores e prepara tintas para produção de materiais têxteis, verifica pigmentação, realiza testes e libera o produto conforme padrões de qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/colorista-textil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (771, 'Comissário de Avarias', 'Vistoria mercadorias, equipamentos e bens danificados em ocorrências de sinistros para reparação ou indenização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/comissario-de-avarias/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (772, 'Comissário de Bordo', 'Presta serviços aos usuários de transportes aéreos, demonstra aos passageiros procedimentos de segurança e emergência. Realiza serviço de bordo, servindo as refeições preparadas e bebidas aos passageiros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/comissario-de-bordo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (773, 'Comprador', 'Analisa a compra de materiais, equipamentos, matérias-primas e serviços, realiza cotação e negociação com fornecedores, emite pedidos e acompanha o fluxo de entrega para cumprimento de todas as condições negociadas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/comprador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (774, 'Comprador de Medicamentos', 'Efetua o planejamento, programação e controle para compras. Realiza cotações com fornecedores, negocia condições de pagamento, preços e prazos de entrega, elabora média de faturamento, realiza contato com fornecedores, estabelece negociações e mantém relacionamentos com parceiros. Acompanha procedimento burocrático de notas fiscais, pedidos e controle de informações de faturamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/comprador-de-medicamentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (775, 'Comprador Internacional', 'Realiza cotação e negociação de materiais e serviços com fornecedores internacionais, analisa fretes, acompanha importação de matéria-prima e produtos e desembaraço aduaneiro e controla toda documentação de transportes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/comprador-internacional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (776, 'Comprador Técnico', 'Analisa a compra produtos técnicos, equipamentos, matérias-primas e serviços, realiza cotação com fornecedores visando menor custo, emite pedidos e acompanha o fluxo de entrega para cumprimento de todas as condições negociadas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/comprador-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (777, 'Comunicador Social', 'Desenvolve, gerencia e alimenta canais de comunicações internas e externas. Elabora o plano de mídia, planeja e desenvolve campanhas e eventos. Registra as demandas identificadas, aplica palestras sobre o empreendimento para o público. Elabora os relatórios e faz os registros fotográficos de todas as ações. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/comunicador-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (778, 'Concierge', 'Atende hóspedes na prestação de informações e controla entrada e saída dos clientes. Presta apoio aos hóspedes na compra de passeios e aluguel de carros, orienta sobre indicações e reservas de restaurantes e atividades turísticas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/concierge/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (779, 'Confeiteiro', 'Elabora receitas de doces, bolos, biscoitos e outras sobremesas e controla materiais, zelando pela validade e qualidade dos produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/confeiteiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (780, 'Conferente', 'Confere entrada de materiais, comparando com as ordens e notas fiscais, organiza e controla estoque e inspeciona o estado das mercadorias. Prepara a documentação de saída e separa os lotes conforme região ou ordem de retirada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/conferente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (781, 'Conferente de Tesouraria', 'Contabiliza numerários das instituições financeiras, realiza a abertura de malotes, separação de numerários por valores, fechamento dos malotes e contagem de moedas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/conferente-de-tesouraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (782, 'Conferente Líder', 'Lidera equipe na conferência de mercadorias, verificando códigos, quantidade, validade e peso para garantir a exatidão entre os documentos fiscais. Acompanha carregamento, organização e transporte de cargas, de acordo com o produto, tamanho de caixa e itinerário de entrega, a fim de evitar danos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/conferente-lider/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (783, 'Conselheiro Tutelar', 'Atua na proteção e zelo dos direitos da criança e do adolescente, investiga violações, aplica medidas em situação de perigo e aconselha pais e responsáveis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/conselheiro-tutelar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (784, 'Consultor ABAP', 'Presta consultoria no desenvolvimento de sistemas em ABAP e participa na elaboração das especificações funcionais, parametrização, implantação e suporte ao usuário, compreendendo diagnósticos, planejamento estratégico de atuação e desenvolvimento dos trabalhos, a fim de propor alternativas e soluções à Direção da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-abap/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (785, 'Consultor Ambiental', 'Presta consultoria na implementação de projetos de gestão ambiental, elabora processos de tratamento de efluentes, acompanhando análises físico-químicas e microbiológicas e define plano de gerenciamento de resíduos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-ambiental/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (786, 'Consultor Contábil', 'Presta consultoria às empresas na administração de tributos e obrigações acessórias, elabora demonstrações contábeis e consolida balancetes, garantindo a identificação de divergências e recomendação de melhoria nos processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (787, 'Consultor CRM', 'Presta consultoria na implantação de sistema do CRM, mapeia e redesenha processos, analisa procedimentos e desenvolve planejamento estratégico de segmentação de clientes e relacionamento, a fim de propor alternativas e soluções à direção da empresa na conquista de melhores resultados e excelência operacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-crm/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (788, 'Consultor de Atendimento', 'Realiza contato com os clientes para esclarecimento de dúvidas ou suporte nas dificuldades identificadas. Participa na melhoria dos processos de atendimento da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-atendimento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (789, 'Consultor de Beleza', 'Realiza atendimento personalizado, demonstra produtos de beleza e métodos de utilização para clientes e esclarece dúvidas. Organiza produtos para exposição e material de merchandising, emite pedido de compra e entrega os produtos solicitados pelos clientes. Dissemina informações sobre novos produtos e/ou tratamentos estéticos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-beleza/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (790, 'Consultor de Benefícios', 'Participa do desenvolvimento, implantação e manutenção de programas de benefícios, identificando necessidades e propondo melhores alternativas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-beneficios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2125, 'Operador de Telemarketing', 'Atua com atendimento ao cliente, esclarecimento de dúvidas, registra informações e oferta produtos ou serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/operador-de-telemarketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (791, 'Consultor de Comércio Exterior', 'Presta consultoria às empresas na análise de tendências de mercado, identifica oportunidades de negócios e desenvolve parcerias entre empresas e fornecedores no exterior. Analisa o fluxo de documentos das operações de importações e exportações, regimes alfandegários e cambial.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-comercio-exterior/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (792, 'Consultor de Comunicação', 'Reestrutura e unifica a comunicação interna e externa, visando a construção e fortalecimento da marca e realiza a programação visual e conteúdo escrito dos valores e diretrizes da empresa. Realiza eventos internos, intermedeia a empresa frente à imprensa do grupo e agências através da elaboração de discursos e textos para a mídia.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-comunicacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (793, 'Consultor de Controladoria', 'Atua com suporte no mapeamentos dos processos organizacionais. Desenvolve e da suporte na implementação de normas e controles internos. Verifica o cumprimento das normas internas nas empresas, revisão da natureza e autorização das despesas em geral. Acompanha Inventário Físico Rotativos e Imobilizado, participa de processo de conciliações de contas contábeis, analisa e estuda o comportamento das despesas durante o exercício, faz comparações e estudos sobre grandes desvios orçamentários. Monitora soluções dos desvios detectados em auditorias externas realizadas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-controladoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (794, 'Consultor de Crédito Imobiliário', 'Realiza a prospecção para venda de imóveis, levanta dados e acompanha a análise econômico-financeira de clientes, com objetivo de definir os limites de crédito para operações. Faz o agendamento para assinatura de contrato com os clientes e bancos/financeiras.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (795, 'Consultor de Estética', 'Atua na área comercial de produtos estéticos, realizando contato pessoal e telefônico com empresas, a fim de fechar parcerias para comercialização de pacotes, além de prestar suporte pós-vendas para renovação de contratos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-estetica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (796, 'Consultor de Eventos', 'Capta novos negócios, realiza todo o processo dos eventos, desde a captação, negociação, fechamento e organização.  Elabora planilhas de orçamento. Cuida de reservas de Hotéis e espaços de eventos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-eventos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (797, 'Consultor de Franquias', 'Acompanha o desempenho financeiro de lojas ou franquias, para garantir o cumprimento dos padrões, orientar e treinar sobre a gestão do negócio. Presta suporte ao franqueado em todos os assuntos administrativos e operacionais e realiza o planejamento estratégico, com objetivo de avaliar o resultado e definir metas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-franquias/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (798, 'Consultor de Imigração', 'Presta assistência e orientação a clientes nos trâmites exigidos pela legislação brasileira na transferência temporária ou permanente de profissionais estrangeiros. Elabora documentos e acompanha processos de visto de trabalho junto aos órgãos oficiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-imigracao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (799, 'Consultor de Implantação', 'Implanta o sistema de gestão. Negocia com o cliente processos, prazos, eventuais alterações de tarefas, treinamentos e documentação do projeto. Auxilia no mapeamento dos processos, parametriza o sistema, ministra os treinamentos aos usuários e acompanha o processo de implantação. Zela pela comunicação entre o cliente e centro de desenvolvimento durante a implantação. Participa de reuniões periódicas com clientes, objetivando o alinhamento do projeto. Cumpre com a metodologia, normas e procedimentos da empresa. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-implantacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (800, 'Consultor de Infraestrutura', 'Presta consultoria no levantamento das necessidades de infraestrutura da empresa, realiza diagnóstico e desenho dos processos e analisa disponibilidade de rede, a fim de propor oportunidades de melhorias e soluções à direção da empresa e definir as políticas de segurança.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-infraestrutura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (801, 'Consultor de Inteligência de Mercado', 'Realiza o levantamento e análise de informações sobre o mercado, concorrência e tendência e identifica oportunidade de negócios, a fim de fornecer recomendações à área de Marketing para alcance dos objetivos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-inteligencia-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (802, 'Consultor de Intercâmbio', 'Realiza a venda de programas de intercâmbio, cursos, seguro de viagem e passagens aéreas internacionais. Auxilia em dúvidas gerais sobre documentação. Participa de eventos para divulgação de programas de intercâmbio e visita parceiros e clientes para prospecção de novos negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-intercambio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (803, 'Consultor de Investimentos', 'Presta consultoria na aplicação em mercado financeiro, analisa cenário econômico, perfil de investimento e de risco e acompanha renda fixa, variável e fundos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-investimentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (804, 'Consultor de Logística', 'Efetua a coleta de dados dos clientes, faz tratamento dos dados e análise estatística.  Elabora os indicadores de acompanhamento da implementação dos projetos. Presta o suporte no acompanhamento da implementação dos projetos. Desenvolve as melhorias nos processos internos. Conduz os projetos de porte pequeno. Lidera os estagiários e analistas alocados no mesmo projeto. Verifica os transportes e efetua a armazenagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-logistica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (805, 'Consultor de Loja', 'Acompanha a demanda de produtos, monitora indicadores de desempenho, identifica oportunidades de negócios e define estratégias para aumento das vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (806, 'Consultor de Marketing', 'Desenvolve e implementa as diretrizes estratégicas para apoio a área comercial e de produtos. Analisa o desempenho  e define planos táticos para contribuir com as áreas comerciais nos atingimento das metas de vendas estabelecidas no orçamento. Define conteúdo de divulgação de dados e informações. Potencializa o uso de canais como imprensa, anuários e outras publicações do mercado. Identifica, mapeia e desenvolve oportunidades de negócio.   Realiza a análise de concorrência, comercial e de definição de estratégias de Marketing.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (807, 'Consultor de Merchandising', 'Implementa material de merchandising. Faz o controle gerencial dos materiais. Conquista espaços para posicionamento dos produtos. Atua com negociação com foco em estoque e vendas. Atuar com abordagem de clientes nas lojas, positivação de materiais de merchandising, exposições de produtos, relacionamento com vendedores e gerentes das lojas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-merchandising/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (462, 'Assistente de SAC', 'Atua na orientação e auxílio do atendimento ao cliente via telefone, e-mail e chat.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/assistente-de-sac/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (808, 'Consultor de Moda', 'Presta consultoria de moda e estilo, acompanha tendências de cores, tecidos, cortes e texturas, seleciona produtos para lançamento de coleções, de acordo com contexto e público-alvo, e elabora estratégias para divulgação da marca.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-moda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (809, 'Consultor de Negócios', 'Identifica a oportunidade de novos negócios ou franqueados, estuda mercado, viabilidade e riscos e negocia pontos comerciais, a fim de validar locais para expansão e reportar à diretoria.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-negocios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (810, 'Consultor de Operações', 'Acompanha a manutenção e recuperação dos equipamentos que retornaram das obras. Monitora a limpeza da área de manutenção. Garante os níveis adequados de estoque de peças e materiais necessários para a manutenção dos equipamentos. Observa as operações a fim de assegurar o cumprimento da manutenção preventiva e corretiva. Documenta as ocorrências e soluções aplicadas, a fim de permitir a análise dos serviços realizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-operacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (811, 'Consultor de Planejamento Estratégico', 'Prepara e executa o planejamento do escopo de trabalho, acompanha a realização das atividades da sua frente. Garante a qualidade das entregas de sua frente e/ou equipe através de produtos e metodologias de trabalho preexistentes. Suporta o desenvolvimento entre equipes do projeto e do cliente. Atua em projeto de consultoria nas áreas de planejamento estratégico, gestão e monitoramento de metas e projetos estratégicos. Participa das etapas de mapeamento de oportunidades, elaboração da estratégia, implementação da estratégia e monitoramento de metas e projetos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-planejamento-estrategico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (812, 'Consultor de Pós-venda', 'Presta consultoria no relacionamento de pós-venda, acompanha indicadores de performance e identifica melhoria nos processos, com objetivo de fidelizar o cliente e solucionar problemas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-pos-venda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (813, 'Consultor de Processos', 'Realiza mapeamento, análise, diagnóstico e redesenho de processos organizacionais, elabora fluxograma e implementa ferramentas, a fim de identificar ineficiências e buscar melhorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-processos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (814, 'Consultor de Projetos', 'Realiza consultoria na área de Projetos, desenvolve o diagnóstico, mapeia fluxo de atividade, planeja estratégias e aplica alternativas e soluções para melhoria dos processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-projetos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (815, 'Consultor de Qualidade', 'Presta consultoria nos processos de qualidade, realizando diagnósticos e análise de fluxo operacional e planejamento estratégico, a fim de propor oportunidades de melhorias à direção da empresa e buscar excelência dos processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (816, 'Consultor de Recrutamento e Seleção', 'Estuda perfis, planeja, organiza e analisa os melhores meios de divulgação e captação de currículos, de acordo com a estratégia definida e perfil das vagas. Visita e seleciona candidatos nos vários meios de busca, tais como job boards, redes sociais e demais meios eletrônicos. Entra em contato telefônico com candidatos, recruta e seleciona profissionais. Desenvolve dinâmicas de grupo. Trabalha internamente ou externamente. Faz programas de Integração de equipes, interno ou no cliente, processos ou projetos determinados pela gestão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-recrutamento-e-selecao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (817, 'Consultor de Recursos Humanos', 'Responde pelas atividades de consultoria, em assuntos referentes à área de Recursos Humanos, compreendendo: diagnósticos, planejamento estratégico de atuação, desenvolvimento dos trabalhos, propondo alternativas e soluções à Direção da empresa.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (818, 'Consultor de Redes', 'Responde pelas atividades de consultoria, em assuntos referentes à área de Administração de Redes, compreendendo: diagnósticos, planejamento estratégico de atuação, desenvolvimento dos trabalhos, propondo alternativas e soluções à Direção da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-redes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (819, 'Consultor de Relacionamento', 'Desenvolve o relacionamento com carteira de clientes, realiza visitas para venda e identifica novos negócios. Realiza o mapeamento de mercado, identifica necessidades dos clientes e acompanha satisfação, a fim de garantir sua fidelização e a qualidade do serviço prestado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-relacionamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (820, 'Consultor de Responsabilidade Social', 'Elabora campanhas de sensibilização interna, relatórios de acompanhamento e atas de reunião. Cria oficinas de capacitação para facilitadores e realiza o gerenciamento da equipe.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-responsabilidade-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (821, 'Consultor de Segurança da Informação', 'Realiza inventários periódicos, testes de invasão e análise de vulnerabilidades no ambiente tecnológico para verificar a aderência às políticas e padrões de segurança. Planeja, desenvolve e aplica melhorias na segurança da informação. Gerencia a implementação e manutenção da política de segurança global, normas, diretrizes e procedimentos para garantir a manutenção permanente de segurança. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-seguranca-da-informacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (822, 'Consultor de Seguros', 'Presta consultoria na venda de seguros, atende e prospecta clientes, apresentando a linha de serviços conforme a necessidade e perfil. Administra carteira, cadastra e renova seguros em diversas áreas, como residencial, empresarial, automotivo, entre outros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-seguros/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (823, 'Consultor de Sistemas', 'Atende os clientes com dificuldades a serem supridas no sistema, durante a implantação. Mapeia os processos das funcionalidades do sistema ERP, adequando as necessidades do cliente durante o projeto. Identifica melhorias nos processos e direciona as regras de negócios do cliente, ao processo padrão existe no sistema ERP. Mantém o cliente informado sobre o andamento do cronograma do projeto, bem como suas visitas presenciais. Ao longo do projeto repassa as documentações das fases do projeto a equipe de suporte técnico e desenvolvimento. Elabora documentos, realiza levantamentos de atividades e dos acompanhamento dos prazos dos projetos junto a equipe.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-sistemas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (824, 'Consultor de Tecnologia da Informação', 'Presta consultoria na área de Tecnologia da Informação, compreendendo diagnóstico, levantamento de requisitos, elaboração de escopo, desenvolvimento de soluções e implantação de projetos no cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-tecnologia-da-informacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (825, 'Consultor de Trade Marketing', 'Presta suporte na inauguração dos franqueados e aplica know how de trade. Define empresas e produtos adequados para pontos extras e visita lojas e franqueados. Identifica necessidades de negócios, busca oportunidades e parcerias. Define parceria com a indústria de ações de trade, brindes e eventos para PDV. Acompanha a exposição e aplicabilidade de materiais promocionais de campanhas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-trade-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (918, 'Coordenador de Legalização', 'Coordena os processos para obtenção de licenciamentos, legalizações e petições de empreendimentos junto aos órgãos públicos, acompanha abertura, alteração e encerramento de empresas e elabora contratos societários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-legalizacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (826, 'Consultor de Treinamento e Desenvolvimento', 'Executa tarefas ligadas ao treinamento e aperfeiçoamento pessoal da empresa. Participa do levantamento de necessidades de treinamento junto as áreas evolutivas e prepara materiais para treinamento interno. Divulga cursos e palestras, incentivando a participação dos interessados.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (827, 'Consultor de Vendas', 'Realiza estudo de mercado, identifica tendências e necessidades do cliente, planeja estratégias de atuação e desenvolve projetos, com objetivo de propor melhorias nos processos de vendas e apresentar soluções à direção da empresa. Também prospecta clientes para divulgação de produtos e serviços, negocia e elabora propostas comerciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (828, 'Consultor Educacional', 'Presta consultoria no desenvolvimento de material didático, elaboração de metodologia de aprendizagem e estruturação de ambiente escolar, conforme propostas da instituição, orçamento e condições exigidas pelo órgão responsável de ensino. Também orienta estudantes no processo de intercâmbio.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-educacional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (829, 'Consultor Empresarial', 'Presta consultoria administrativa às organizações, realizando diagnósticos e análise dos processos, planejamento estratégico e desenvolvimento de trabalhos, a fim de propor alternativas e soluções à direção da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-empresarial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (830, 'Consultor ERP', 'Desenvolve projetos de sistemas ERP (Enterprise Resource Planning) em empresas clientes, levantando requisitos, mapeamento de processos e modelagem de dados, estuda e implementa sistemas de acordo com as regras de negócio acordado. Soluciona problemas técnicos, indica o caminho a seguir, mostrando possíveis soluções. Treinamento de clientes e criação de documentação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-erp/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (831, 'Consultor Financeiro', 'Faz o planejamento financeiro, análise de custos, montagem, análise e acompanhamento de relatórios financeiros. Elabora o Business Plan, faz análises financeiras e fluxo de caixa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-financeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (832, 'Consultor Fiscal', 'Presta consultoria no planejamento tributário da empresa, analisa os processos de apuração de impostos diretos e indiretos e das obrigações acessórias e acompanha atendimento às fiscalizações, orientando sobre as melhores práticas de acordo com a legislação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-fiscal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (833, 'Consultor Funcional', 'Presta consultoria na análise das necessidades dos clientes para identificar soluções de sistemas, compreendendo diagnóstico, levantamento de requisitos, elaboração de escopo, desenvolvimento de soluções e implantação de projetos no cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-funcional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (834, 'Consultor Gastronômico', 'Presta consultoria na elaboração e definição do cardápio ideal para ocasião ou estabelecimento e suporte na disposição dos aparelhos de cozinha. Contrata e treina funcionários na abertura de restaurantes e acompanha tendências e concorrências, a fim de implantar mudanças e identificar oportunidades de negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-gastronomico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (835, 'Consultor Jurídico', 'Presta consultoria para pessoas e empresas relacionadas às diversas áreas do Direito. Verifica a necessidade do cliente ajudando o a cumprir os requisitos legais. 
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (836, 'Consultor Mastersaf', 'Presta consultoria no desenvolvimento de solução Mastersaf e participa na elaboração das especificações funcionais, parametrização, implantação e suporte ao usuário, compreendendo diagnósticos, planejamento estratégico de atuação e desenvolvimento dos trabalhos, a fim de propor alternativas e soluções à Direção da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-mastersaf/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (837, 'Consultor Oracle', 'Presta consultoria no desenvolvimento de solução Oracle e participa na elaboração das especificações funcionais, parametrização, implantação e suporte ao usuário, compreendendo diagnósticos, planejamento estratégico de atuação e desenvolvimento dos trabalhos, a fim de propor alternativas e soluções à Direção da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-oracle/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (838, 'Consultor Pedagógico', 'Identifica e avalia instituições de ensino, orienta sobre os processos pedagógicos e presta apoio no desenvolvimento de conteúdo, envolvendo a adaptação e validação do material didático.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-pedagogico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (839, 'Consultor SAP', 'Presta consultoria no desenvolvimento e implantação de módulos SAP, analisa sistemas, levanta processos e requisitos para solução. Participa na aplicação de testes, análise de funcionalidade e elaboração de documentação técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-sap/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (840, 'Consultor Siebel', 'Presta consultoria no desenvolvimento de sistemas em Siebel e participa na elaboração das especificações funcionais, parametrização, implantação e suporte ao usuário, compreendendo diagnósticos, planejamento estratégico de atuação e desenvolvimento dos trabalhos, a fim de propor alternativas e soluções à Direção da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-siebel/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (841, 'Consultor Técnico', 'Realiza visitas e a venda de produtos e serviços, atende clientes na prestação de assistência técnica, identifica suas necessidades e dificuldade e acompanha pedidos, a fim de solucionar problemas e prospectar novos negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (842, 'Consultor Técnico de Veículos', 'Confere itens de parte mecânica, elétrica, funilaria e pintura conforme check-list estabelecido. Organiza o pátio para otimizar o espaço físico, facilitando o fluxo de carros. Recebe cliente no pós-venda e define pelos critérios a solicitação ou não de garantia, aprova serviços de garantia e revitalização dos carros, feitas pelos parceiros, a fim de atender as solicitações dos clientes internos e externos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-tecnico-de-veiculos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (843, 'Consultor Técnico Industrial', 'Presta consultoria nos processos industriais, realizando diagnósticos e análise dos fluxos produtivos, planejamento estratégico e desenvolvimento de trabalhos, a fim de propor alternativas e soluções à direção da empresa na redução de custos, conquista de melhores resultados e excelência em qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/consultor-tecnico-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (844, 'Consultor Tributário', 'Busca formas de diminuir o impacto dos tributos na empresa por meio de leis de incentivo.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (880, 'Coordenador de Contas a Pagar', 'Coordena o fluxo de caixa e controla o registro de entrada e saída de numerários, cheques, notas fiscais e demais documentos, a fim de cumprir com os compromissos financeiros da empresa. Acompanha previsões de caixa e analisa comportamento do mercado financeiro para recomendar decisões de captação ou aplicação de recursos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-contas-a-pagar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (845, 'Contabilista', 'Realiza análises, classificações, conciliações contábeis e financeiras/controle fiscal. Administra tributos, controla ativos, com análise de custos e apuração de resultados. Elabora demonstrações contábeis, balancetes e balanço, faz análise da situação patrimonial e financeira e elabora relatórios gerenciais. Supervisiona as atividades do departamento de contabilidade, visando assegurar que todos os relatórios e registros contábeis sejam feitos de acordo com as normas contábeis e legislação pertinente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/contabilista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (846, 'Contador', 'Elabora e analisa balancetes e demonstrações contábeis e realiza processos de legalização e encerramento de empresas. Administra os tributos da empresa, prepara obrigações acessórias ao fisco, órgãos competentes e contribuintes e controla o registro de livros fiscais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/contador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (847, 'Contador Gerencial', 'Identifica,  mede, acumula, analisa, prepara, interpreta e relata informações financeiras e operacionais para o uso da administração da empresa, nas  funções de planejamento, avaliação e controle de suas atividades e para  assegurar o uso apropriado e a responsabilidade de seus recursos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/contador-gerencial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (848, 'Contato Publicitário', 'Promove espaços publicitários em mídias online e offline, negocia e vende anúncios para clientes. Elabora propostas comerciais e acompanha o serviço prestado na pós-venda.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/contato-publicitario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (849, 'Conteudista', 'Apoia a área de treinamento na criação e desenvolvimento de conteúdos EAD, atualizações de conteúdos no portal de treinamento, novas metodologias de ensino, jogos de aprendizagem, exercícios, dinâmicas, apresentações e avaliações de treinamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/conteudista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (850, 'Contínuo', 'Executa pagamentos, serviços em cartórios, compra e busca de materiais conforme demanda apresentada. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/continuo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (851, 'Contra-regra', 'Presta apoio à produção na montagem e desmontagem de cenários e mudança de figurino de espetáculos, programas, telenovelas, entre outros eventos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/contra-regra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (852, 'Controlador de Custos', 'Controla os custos das unidades de negócios da empresa. Avalia se os gastos mensais orçados estão de acordo com o que de fato foi gasto e presta assessoria à gerência e diretoria, no que tange a custos de projetos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/controlador-de-custos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (853, 'Controlador de Pátio', 'Controla movimentação de entrada e saída de veículos em pátio e verifica documentos para liberação de cargas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/controlador-de-patio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (854, 'Controlador de Produção', 'Controla entradas e saídas de produção, acompanha programação e beneficiamento de máquinas e verifica tempo de fabricação, a fim de manter os processos dentro dos parâmetros estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/controlador-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (855, 'Controlador de Qualidade', 'Verifica e monitora o desempenho dos processos produtivos e produtos, registra os dados obtidos nas inspeções. Avalia criticamente e dá visibilidade ao resultado.Faz acompanhamento de disposição do produto e correção de processos. Reporta ao coordenador da qualidade e analistas as não conformidade encontradas e ações tomadas. Monitora e verifica os programas de autocontrole, auxilia em determinação de ações corretivas e preventivas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/controlador-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (856, 'Controlador de Tráfego', 'Realiza e acompanha a escala de viagem dos motoristas e garante a devida programação das próximas viagens e saída do motorista. Faz o rastreamento de todos os veículos de viagem da empresa ou de agregado, acompanha o percurso e emite relatórios. Libera veículos que estão presos na pesagem da carga, efetua cadastro de motoristas e cadastro de viagem para motoristas e agregados para manter dados cadastrais atualizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/controlador-de-trafego/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (857, 'Controlador de Tráfego Aéreo', 'Controla o tráfego aéreo, observa, adapta e orienta planos de voos e autoriza aterragem e decolagem de aeronaves, a fim de cumprir as normas e regulamentos determinados pelas autoridades aeronáuticas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/controlador-de-trafego-aereo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (858, 'Controller', 'Planeja, organiza e desenvolve planos econômico-financeiros e analisa informações contábeis e indicadores de performance para acompanhar projeções de faturamento, reduzir perdas e aumentar o lucro. Participa na definição de diretrizes em alinhamento ao planejamento estratégico da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/controller/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (859, 'Coordenador Administrativo', 'Coordena, organiza e controla as atividades da área administrativa relativas à segurança patrimonial, arquivo, ouvidoria, secretaria, manutenção predial e atividades afins, definindo normas e procedimentos de atuação para atender as necessidades e objetivos da empresa. Acompanha e analisa os indicadores de desempenho, definindo planos, em conjunto com a equipe. 
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (860, 'Coordenador Administrativo de Obras', 'Faz o gerenciamento da administração da obra, gestão do caixa rotativo da obra, previsão e demonstrativo de receitas e despesas. Garante a aplicação dos procedimentos das áreas de suprimentos, financeira e departamento pessoal. Coordena os relatórios financeiros e faz a gestão de contrato de empresa terceiras. Supervisiona o almoxarifado com diretrizes de armazenamento, controle de estoque e controle do uso dos equipamentos e ferramentas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-administrativo-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (861, 'Coordenador Comercial', 'Analisa estratégias de vendas, acompanha concorrência, identifica e aponta tendências mercadológicas, estuda projeções e quadros comparativos e elabora relatórios gerenciais, para subsidiar informações à gerência na tomada de decisão e atender os resultados previstos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (862, 'Coordenador Contábil', 'Coordena, orienta e fiscaliza a atividade realizada pela equipe contábil, envolvendo a análise, controle, contabilização, planejamento, investimento e a consolidação de resultado, de acordo com a legislação vigente, diretriz e a estratégia preestabelecida. Realiza a análise do informe gerencial e o relatório de receita, gasto, entrada, preço do produto e do serviço. Faz a comparação entre o orçado e o real para comentar a variação. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (863, 'Coordenador de Alimentos e Bebidas', 'Coordena as boas práticas do departamento de alimentos e bebidas, participa na elaboração de cardápios, acompanha os resultados, define orçamento da área e controla o inventário de produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-alimentos-e-bebidas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (881, 'Coordenador de Contas a Receber', 'Coordenar as rotinas de contas a receber, acompanha recebimento de títulos, cheques devolvidos e cobranças e elabora controle de fatura em aberto e recebimentos efetuados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-contas-a-receber/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (864, 'Coordenador de Almoxarifado', 'Confere notas fiscais, confronta notas e pedidos, encaminha materiais para armazenamento. Cuida de prazos de entrega dos produtos, solicita reposição de estoque. Acompanha pedidos de compra da empresa, administra atendimento a requisições de materiais e controla níveis de estoque. Otimiza estocagem de material. Acompanha prazo de vencimento de validade dos materiais. Realiza o levantamento de inventário físico, emite solicitações de compra e controle diversos de expedição dos materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-almoxarifado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (865, 'Coordenador de Armazém', 'Supervisiona, coordena e controla a realização das atividades gerais de armazenagem. Faz a administração e manutenção das máquinas e equipamentos. Confere a documentação emitidas, programa as operações de desova, estufagem, carregamentos e descargas. Analisa as não conformidades, reclamações de clientes. Define as ações corretivas e preventivas e acompanha as ocorrência.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-armazem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (866, 'Coordenador de Assistência Técnica', 'Coordena solicitações de manutenções dos clientes, controla custos, administra nível de serviço, fornece equipamentos para infraestrutura, avalia e aprova manuais de produtos e desenvolve colaboradores, acompanhando indicadores de desempenho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-assistencia-tecnica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (867, 'Coordenador de Assuntos Regulatórios', 'Coordena e prepara dossiês dos processos de registro de produtos, incluí alterações e renovações, mantendo os clientes informados sobre o andamento dos mesmos, garante a obtenção dos registros dentro das necessidades e expectativas da empresa.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (868, 'Coordenador de Atendimento ao Cliente', 'Coordena a performance operacional do atendimento. Elabora os relatórios e acompanha o desenvolvimento por meio de monitoramentos, feedbacks, índices de desempenho, avaliações e campanhas motivacionais. Reporta à equipe sobre os novos procedimentos e realiza as mudanças nas especificações dos processos, produtos, sistemas e fluxos. Estabelece e acompanha as normas de desempenho de atendimento aos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-atendimento-ao-cliente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (869, 'Coordenador de Auditoria', 'Coordena os serviços de auditoria, avalia controles internos e verifica a execução adequada dos procedimentos. Desenvolve planos táticos para revisar a aplicação de políticas e procedimento estabelecidos, a fim de identificar desvios e emitir recomendações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-auditoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (870, 'Coordenador de Benefícios', 'Coordenar os processos na manutenção de benefícios, administra contratos, participa de negociação com fornecedores e monitora os custos, a fim de analisar e propor melhorias na política da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-beneficios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (871, 'Coordenador de Biblioteca', 'Coordena as atividades de catalogação, classificação de livros, informativos, fichas, entre outros documentos, atende aos requisitantes e presta suporte a estudantes. Verifica as necessidades de aquisições de livros e elabora planos de fomento para desenvolvimento da biblioteca.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-biblioteca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (872, 'Coordenador de Business Intelligence', 'Faz análise e levantamento de requisitos, especificação, modelagem, construção de dashboards, ETL e relatórios. Analisa as estratégias de gestão de negócios através da gestão dos relacionamentos com clientes. Elabora e implementa KPIs, presta suporte as demais áreas da empresa com relação aos principais aspectos dos distribuidores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-business-intelligence/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (873, 'Coordenador de Campo', 'Coordena a equipe responsável por fazer análises de parâmetros analíticos e de processo. Emite relatórios diários, relatórios de inspeção e de performance. Identifica oportunidades de melhorias e suporte de vendas. Garante o alcance dos resultados com iniciativa para ajuste dos parâmetros de processo e analítico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-campo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (874, 'Coordenador de Cargos e Salários', 'Coordena a manutenção da estrutura de cargos e salários, conduzindo pesquisa salarial e negociações coletivas, a fim de definir e cumprir a política de carreira e remuneração.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-cargos-e-salarios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (875, 'Coordenador de Cobrança', 'Coordena o departamento de cobrança, estrutura processos para recuperar carteira de inadimplentes e renegociar dívidas e implanta melhorias na política para acompanhar risco de crédito.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (876, 'Coordenador de Comércio Exterior', 'Coordena a negociação de fretes e fechamento de câmbio, analisa e aprova documentos de importação e exportação, acompanha despacho e desembaraço aduaneiro de cargas e administra e contrata fornecedores nacionais e internacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-comercio-exterior/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (877, 'Coordenador de Compras', 'Coordena cadeia de suprimentos, acompanha estoques e aprova pedidos de materiais. Administra e prospecta fornecedores, analisa a qualidade do fornecimento e implementa novos métodos, sistemas e processos, voltados à redução de custos e aumento da lucratividade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (878, 'Coordenador de Comunicação', 'Realiza as estratégias de comunicação interna e externa. Revisa a redação de textos dos projetos e documentos em geral e propõe estudos mercadológicos, para avaliar e divulgar a imagem da instituição. Planeja e organiza a logística e divulgação dos eventos, seminários e congressos e responde pela coordenação das campanhas publicitárias realizadas pela agência terceirizada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-comunicacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (879, 'Coordenador de Contas', 'Coordena carteira de clientes, mapeia novos negócios e realiza a pós-venda, com o objetivo de fidelizar contas. Acompanha concorrência e mercado, analisa necessidades dos clientes e planeja estratégias de vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-contas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (882, 'Coordenador de Contas Médicas', 'Coordena as contas médicas, hospitalares, recursos de glosas e folha de pagamento de credenciados, analisando os honorários médicos aos procedimentos realizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-contas-medicas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (883, 'Coordenador de Contratos', 'Administra contratos de serviços, define cronogramas, escopos e recursos, analisa mapas de riscos e propostas comerciais e participa na negociação junto aos fornecedores, adaptando as cláusulas de acordo com as exigências. Acompanha cronograma para atender os prazos determinados e condições estabelecidas em contrato.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-contratos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (884, 'Coordenador de Controladoria', 'Desenvolve, elabora e consolida o budget e forecast, identifica e analisa as variações entre real e orçado de receitas, custos e despesas. Controla o fluxo de investimentos da empresas, a fim de alinhar com as necessidades estratégicas e orçamentárias. Analisa e planeja provisões e consolidação de relatórios financeiros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-controladoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (885, 'Coordenador de Controles Internos', 'Coordena o mapeamento e análise de fluxo de processos, acompanha a revisão e implementação de políticas e normativas administrativas, a fim de identificar riscos e irregularidades, promover eficiências operacional e alcançar os resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-controles-internos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (886, 'Coordenador de Credenciamento', 'Coordena e aprova propostas de credenciamento e extensões, reajustes das redes credenciadas, aprova manutenção de cadastro em sistema.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-credenciamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (887, 'Coordenador de Crédito', 'Coordena, analisa, define e libera limites de crédito, acompanha a rotina de levantamento e avaliação sócio-econômica do cliente. Participa no desenvolvimento de políticas, processos e estratégias para mitigar a exposição do risco de crédito e perda financeira da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-credito/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (888, 'Coordenador de Crédito e Cobrança', 'Faz solicitações, autorizações e pagamentos de despesas. Faz devolução de vendas, emissão e conferência de relatório de movimentação do caixa. Coordena divide as tarefas no crediário. Acompanha carteira de cobrança, faz emissão, cancelamento e entrada e saída de notas fiscais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-credito-e-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (889, 'Coordenador de Crédito Imobiliário', 'Coordena produtos e serviços de análise de crédito imobiliário, acompanhando os processos de liberação, desde a negociação de valores, verificação da documentação apresentada, montagem de processos, envio ao banco e conferência de contratos. Conduz a prospecção de novos clientes e elabora informações de indicadores das áreas, para suporte às gerências na tomada de decisões.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-credito-imobiliario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (890, 'Coordenador de CRM', 'Faz mapeamento dos processos de CRM. Desenvolve planejamento e ações para o marketing de relacionamento. Identifica clientes potenciais realizando aderência para efetivação. Busca meios para obter total satisfação do cliente. Realiza plano estratégico de CRM. Acompanha as ações buscando soluções e melhorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-crm/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (891, 'Coordenador de Customer Service', 'Coordena pedidos até a entrega de mercadorias para garantir a eficiência e eficácia do ciclo de vendas e presta suporte na pós-venda no atendimento as manifestações dos clientes. Monitora indicadores do nível de serviço e desenvolve soluções nos processos e procedimentos de relacionamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-customer-service/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (892, 'Coordenador de Custos', 'Prepara e acompanha o orçamento (Budget e Forecast). Controle dos gastos de fabricação em relação ao orçamento e dos investimentos em relação ao orçamento. Elabora demonstrativo de custo dos produtos vendidos, relatórios demonstrativos de custos de produção. Estabelece critérios e meios adequados na formação do preço de venda. Apura os custos dos produtos fabricados, integrado com a contabilidade. Assessora a Diretoria na análise dos relatórios gerenciais de custos. Mantém o controle e análise das variações de preços. Coordena inventários, implanta sistema de custos integrado com contabilidade. Acompanha o planejamento de negócios e implementação. Elabora simulações e análises prévias de custos para novos produtos e alterações em produtos existentes. Acompanha, desenvolve e orienta colaboradores quanto às políticas da empresa. Apura margens de custos dos produtos. Faz análises para tomada de decisão.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (893, 'Coordenador de Departamento Pessoal', 'Coordena e acompanha os processos de admissão e rescisão de colaboradores, fechamento de folha de pagamento e controla o plano de benefícios. Analisa resultados e acompanha índices de turnover e absenteísmo para apresentar soluções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-departamento-pessoal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (894, 'Coordenador de Desenvolvimento de Sistemas', 'Planeja, coordena e executa projeto de desenvolvimento de sistemas. Controla, monitora e orienta a equipe interna ou externa no que tange os projetos de desenvolvimento de sistemas da organização. Apoia a gerência no planejamento de sistemas subsidiando com informações técnica para a tomada de decisão. Planeja e coordena o desenvolvimento dos projetos de novos portais e sistemas corporativos da equipe. Planeja arquitetura dos novos sistemas da organização. Elaborar solicitações de propostas para novos projetos da organização. Documenta e confecciona processos internos. Faz a interface entre clientes e fornecedores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-desenvolvimento-de-sistemas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (895, 'Coordenador de E-commerce', 'Criação e manutenção de todas as atividades diárias da loja virtual e sites, lidera e executa esforços para escolher e configurar a plataforma de comércio eletrônico. Atua como principal responsável pela administração da ferramenta se responsabilizar pelas atividades de vendas online (desde a efetivação do pedido ao fornecedor até a efetiva entrega do produto certo ao cliente certo no prazo estipulado), incluindo a criação de ações para aumento das vendas. Planeja e executa campanhas, acompanha o desempenho da loja virtual e disparo de e-mail marketing, compara e seleciona a plataforma de comércio eletrônico da empresa, configura e torna a ferramenta operacional, coordena as vendas online e colabora com os gerentes de categoria, a fim de promover os produtos adequadamente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-e-commerce/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (896, 'Coordenador de Enfermagem', 'Elabora escala de plantões de enfermagem. Distribui os pacientes entre os enfermeiros, coordena e acompanha visitas dos enfermeiros. Mantém a comunicação com equipe de enfermagem. Solicita escala de enfermagem para a escalista em caso de necessidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-enfermagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (897, 'Coordenador de Engenharia', 'Coordena os projetos de engenharia, compreendendo o levantamento das necessidades de materiais e mão de obra, análise de viabilidade e acompanhamento de cronograma e orçamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-engenharia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (898, 'Coordenador de Estilo', 'Desenvolve e elabora coleções, de acordo com pesquisa de tendências, definição de tecidos, cartela de cores e materiais e criação de briefing. Aprova e acompanha peças pilotos e realiza contato com fornecedores até a aprovação final.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-estilo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (899, 'Coordenador de Estoque', 'Atua na área de estoque, faturamento e logística. Elabora inventário, custo de entrada, análise e redução de despesas de custos. Analisa e controla desperdício e avarias, estabelece comunicação com setor comercial, quando necessário ajustar ou melhorar o desempenho do setor. Cria e apresenta desempenho e sugestões de melhorias.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (900, 'Coordenador de Eventos', 'Coordena eventos, acompanha sua execução, formaliza propostas, realiza follow-up dos fechamentos dos negócios e avalia resultados, para assegurar satisfação de clientes e manutenção do padrão de qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-eventos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (901, 'Coordenador de Expedição', 'Faz emissão de relatórios e pedidos para carregamentos. Prepara plano de carga com antecedência. Solicita veículos e contrata transportadoras. Distribui e fiscaliza as atividades dos auxiliares operacionais. Analisa os relatórios e prevê carregamentos. Garante que os produtos carregados estão identificados, embalados e sem danos. Garante que as cargas planejadas no dia foram realizadas. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-expedicao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (902, 'Coordenador de Facilities', 'Coordena todo o processo de prestação de serviços terceirizados de manutenção predial, recepção, limpeza, telefonia, segurança patrimonial e compras. Auxilia no planejamento de custos e despesas previstos em contrato e avalia os resultados alcançados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-facilities/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (903, 'Coordenador de Faturamento', 'Coordena a emissão de notas fiscais, duplicatas e espelho de notas, analisa processos de cobrança, elabora demonstrativos de faturamento e acompanha indicadores, a fim de identificar inconsistências e subsidiar informações para tomada de decisão junto à diretoria.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-faturamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (904, 'Coordenador de Ferramentaria', 'Coordenar a equipe de ferramentaria, orienta tecnicamente as atividades e funcionários distribuindo as atividades conforme habilidades e necessidades. Verifica a necessidade de implantação de mudanças, sempre buscando a melhoria dos processos e avaliando os resultados operacionais mediante o indicador de departamento. Acompanha o estudo da ferramenta ou dispositivo a ser construído ou reparado, examinando desenhos, esboços, modelo e especificações, para programar o roteiro das operações. Acompanha os testes das das ferramentas, estampos e dispositivos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-ferramentaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (905, 'Coordenador de Filial', 'Coordena as atividades das áreas administrativa, comercial, financeira e operacional da filial e representa a empresa junto a clientes e fornecedores, com o objetivo de cumprir metas e resultados determinados pela diretoria. Acompanha as rotinas dos funcionários, analisa os resultados da filial e participa na definição das estratégias do negócio.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (906, 'Coordenador de Folha de Pagamento', 'Coordena os processos de folha de pagamento, como recolhimento de impostos e obrigações trabalhistas, cálculo e conferência de pagamentos, encaminhando para o departamento financeiro, a fim de cumprir os prazos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-folha-de-pagamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (907, 'Coordenador de Garantia da Qualidade', 'Coordena as atividades de garantia da qualidade, elabora mapeamento de processos, implementa procedimentos e acompanha análise de desvios e avaliação de ações corretivas, preventivas e de melhoria contínua, para garantir a aplicação dos padrões da empresa e boas práticas de fabricação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-garantia-da-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (908, 'Coordenador de Help Desk', 'Gerencia equipe de Help Desk, monitora os acordos de níveis de serviços, elabora e acompanha os relatórios com análises de causas raiz, elaborando propostas de plano de ação para melhoria dos serviços. Direciona solicitações para as equipes responsáveis e acompanha todo o ciclo de vida dos chamados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-help-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (909, 'Coordenador de Hotelaria', 'Coordena as atividades da equipe de hotelaria, administra custos e compra de materiais de limpeza e controla os serviços prestados, a fim de garantir a qualidade e padrões de higiene.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-hotelaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (910, 'Coordenador de Implantação', 'Coordena projetos de instalação, comissionamento e integração de sistemas, avaliando o custo, rentabilidade e o risco, conforme com escopo, prazo e nível de serviço estabelecido.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-implantacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (911, 'Coordenador de Incorporação', 'Coordena o processo de aprovação de empreendimentos imobiliários junto aos órgãos municipais, estaduais e federais, aquisição de terreno, emissão de alvará de construção e estudo de viabilidade financeira do projeto. Elabora e avalia contratos junto ao jurídico, materiais de lançamento e decorados e acompanha venda do produto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-incorporacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (912, 'Coordenador de Infraestrutura', 'Administra as rotinas de manutenção, implantação e configuração de infraestrutura de telecomunicações, redes, servidores e internet, acompanha o suporte aos usuários e desenvolve soluções de tecnologia, a fim de manter a alta disponibilidade dos serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-infraestrutura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (913, 'Coordenador de Instalações', 'Coordena e planeja instalação de sistemas, equipamentos e máquinas prediais e industriais, controla orçamento e compra de materiais e acompanha qualidade dos serviços realizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-instalacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (914, 'Coordenador de Inteligência de Mercado', 'Coordena os processos de mapeamento, segmentação e estudo de mercado e concorrência, analisa pontos fortes e fraquezas, constrói cenários e identifica oportunidade de negócios, com o objetivo de definir políticas comerciais e recomendar à gerência posicionamento e estratégias para conquista de clientes e alcance dos resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-inteligencia-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (915, 'Coordenador de Inventário', 'Administra inventários realizados em estoque, acompanha transações de entradas, saídas e ajustes de mercadorias e implementa programas de redução de perdas e discrepância nas informações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-inventario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (916, 'Coordenador de Investimentos', 'Coordena a captação e aplicação de recursos no mercado financeiro e administra riscos, para aumentar a rentabilidade e assegurar liquidez e segurança nas operações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-investimentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (917, 'Coordenador de Laboratório', 'Faz cotação de insumos laboratoriais, atendimento a clientes internos e externos, elabora e apresenta  relatórios de gestão, redige laudos e encaminha à direção, acompanha  e valida análises químicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-laboratorio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1914, 'Médico Cirurgião Torácico', 'Trata de patologias pulmonares e torácicas passíveis de abordagem cirúrgica à exceção das que acometem o coração e grandes vasos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (919, 'Coordenador de Licitação', 'Coordena o setor de licitações. Responsável pela parte documental da empresa quanto as licitações, editais e declarações. Realiza confecções de proposta comercial e cadastro de documentos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (920, 'Coordenador de Limpeza', 'Coordena toda rotina de limpeza, organiza o local, faz a escalas de funcionários da limpeza e pedidos de produtos faltantes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-limpeza/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (921, 'Coordenador de Logística', 'Lidera os processos de logística. Realiza gestão do centro de distribuição e armazém, trabalha com faturamento, recebimento, armazenamento e expedição de materiais. Gerencia os prestadores de serviço e controla o orçamento da área. Faz o acompanhamento dos indicadores de desempenho dos processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-logistica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (922, 'Coordenador de Loja', 'Coordena a rotina da equipe de vendas, recruta, treina e avalia profissionais, controla estoque, abertura e fechamento de caixa e organiza loja. Elabora planilhas e relatórios à gerência com os resultados das metas de vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (923, 'Coordenador de Manutenção', 'Coordena equipes na preparação, instalação, montagem e manutenção de equipamentos industriais e prediais, bem como pela prevenção, correção e ajustes em problemas emergenciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (924, 'Coordenador de Manutenção Automotiva', 'Coordena equipe na montagem e manutenção preventiva e corretiva de veículos automotores, acompanha ordens de serviço e administra orçamento para controlar custos e implementar melhorias nos processos de perdas de materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-manutencao-automotiva/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (925, 'Coordenador de Manutenção Industrial', 'Coordena os serviços de manutenção nas máquinas e nos equipamentos da empresa. Realiza a coordenação da equipe de manutenção, verificando quais são os recursos necessários. Treina e capacita os funcionários. Solicita ao mecânico ou eletricista que solucione o problema para dar continuidade ao ciclo da produção. Realiza os processos de melhorias de máquinas e elabora o processo de manutenção preventiva, verifica os equipamentos e as máquinas. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-manutencao-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (926, 'Coordenador de Manutenção Predial', 'Realiza reparos na área de manutenção predial, inspeciona regularmente as dependências da empresa, observa potenciais problemas de manutenção e toma medidas necessárias para saná-las. Avalia de acordo com checklist, presença de não conformidades, elabora e envia os relatórios semanais a gerência. Realiza vistorias de entrada e saída das instalações e equipamentos da empresa, antes e depois de cada evento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (927, 'Coordenador de Marketing', 'Coordena, elabora e acompanha os planos e programas de marketing, por meio do desenvolvimento e promoção dos produtos, participando da criação da política comercial da empresa. Analisa e avalia os enfoques promocionais, os veículos e canais de propaganda utilizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (928, 'Coordenador de Marketing Digital', 'Coordena e elabora planos e programas de marketing digital, acompanha performance em Google Adwords e Analytics, desenvolve estratégias SEO e campanhas de e-mail marketing e mensura ações digitais, a fim de gerar leads qualificados para conversão em vendas e fortalecimento da marca.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-marketing-digital/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (929, 'Coordenador de Meio Ambiente', 'Coordena atividades de licenciamento e auditoria ambiental de empresas de pequeno, médio e grande porte, acompanha levantamentos de impactos e relatórios técnicos de monitoramento e desenvolve planos de recuperação de áreas degradadas, gestão de resíduos e programas de educação ambiental.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-meio-ambiente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (930, 'Coordenador de Melhoria Contínua', 'Responsável pelo setor de Melhoria Contínua, coordenando equipes multifuncionais e interdisciplinares para que seja feita uma análise rigorosa dos problemas crônicos que afetam os resultados da empresa. Identifica a causa dos problemas, elaboração e coordenação das ações realizadas para diminuir ou eliminar os problemas que afetam os resultados, melhoria nos equipamentos e capacitação dos colaboradores. Controle de documentos seguindo requisitos da ISO com foco em qualidade do produto e serviço prestado. 
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (931, 'Coordenador de Merchandising', 'Realiza pesquisas periódicas de preços, por meio de visitas nos pontos de vendas, busca oportunidades de negócios e acompanha indicadores de performance. Seleciona, avalia e contrata promotores junto à área de recursos humanos ou agência. Participa dos treinamentos, realizando a logística de materiais para os promotores. Garante abastecimento e manutenção dos espaços dos produtos e orienta os promotores em sua rotina.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-merchandising/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (932, 'Coordenador de Mídia', 'Coordena o planejamento e desenvolvimento de peças publicitárias, define seu conteúdo e público-alvo, para transmitir mensagens específicas, envolvendo a divulgação de produtos, ou mensagens institucionais da empresa, da forma mais eficaz. Assegura o alcance dos objetivos da empresa em termos de comunicações com o mercado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-midia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (933, 'Coordenador de Mídias Sociais', 'Coordena e produz conteúdo para mídias sociais, participa da elaboração das estratégias de ações, planejamento e Gestão de mídia envolvendo AdNetworks, Social Networks, Display, RTB, DSP, DCO, Mobile.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-midias-sociais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (934, 'Coordenador de Negócios', 'Coordena as rotinas de avaliação de perfil de mercado, monitora as práticas da concorrência, identifica clientes potenciais e oportunidades de crescimento e participa no planejamento de estratégias de vendas, com objetivo de desenvolver os negócios da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-negocios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (935, 'Coordenador de Obras', 'Gerencia obras na interface entre construtora e incorporadora. Aponta e propõe ações de correção. Reporta ao escritório tudo o que diz respeito a obra que passe por suas mãos. Acompanha e fiscaliza as obras, presta suporte no acompanhamento de custos e realiza o planejamento da concepção de produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (936, 'Coordenador de Orçamento', 'Coordena os parâmetros econômicos e a projeção de perspectivas para a elaboração do plano financeiro da empresa, com base na receita estimada e relatórios de despesas, vendas e custos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-orcamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (995, 'Corretor de Seguros', 'Realiza a venda de apólices de seguros de vida, saúde, automóvel, previdência privada e outros ramos. Oferece aos clientes as melhores escolhas de coberturas e realiza cotação de prêmios. Relaciona com companhias seguradoras e presta assistência ao segurado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/corretor-de-seguros/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (937, 'Coordenador de Patrimônio', 'Faz o cadastro, classificação, identificação e inventário de bens patrimoniais da empresa, controla baixas e transferências e calcula depreciação para atualizar em sistema o controle de ativo imobilizado. Realiza periodicamente consultas aos órgãos fiscalizados e reguladores de serviços, verificando possíveis alterações, providenciando novos documentos de forma preventiva diante de novas fiscalizações. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-patrimonio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (938, 'Coordenador de PCP', 'Coordena as atividades de planejamento e controle de produção, acompanha estoque de matérias-primas e processos de fabricação e identifica necessidade de aquisição de equipamentos. Determina quantidade de compra de produtos, analisa capacidade de produção e programação de máquinas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-pcp/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (939, 'Coordenador de Pesquisa de Mercado', 'Coordena equipe de pesquisadores internos e externos, envolvendo o levantamento das necessidades da empresa, elaboração de questionários e instruções em campo. Processa e interpreta dados, analisa informações sobre o consumidor, concorrência e tendências e elabora relatório de resultados, a fim de gerar informações para o planejamento estratégico dos negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-pesquisa-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (940, 'Coordenador de Pesquisa e Desenvolvimento', 'Coordena projetos de pesquisa e desenvolvimento (P&amp;D), que promovam o uso de novas tecnologias, protocolos e aplicações em redes de computadores. Coordena a implantação de grupos de trabalho e acompanha o desenvolvimento do projeto junto aos grupos de trabalho. Avalia relatórios e protótipos e faz transferência de tecnologia. Realiza divulgação dos resultados dos projetos de pesquisa e promove a apresentação dos projetos de pesquisa em eventos. Coordena a implantação de serviços experimentais para distintas comunidades de usuários, a partir de resultados dos programas de P&amp;D. Participa em projetos de P&amp;D coordenados e financiados por outras instituições, nacionais e internacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-pesquisa-e-desenvolvimento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (941, 'Coordenador de Planejamento', 'Planeja e desenvolve metas de longo prazo e objetivos de curto prazo. Suporta a execução de planos de negócios e iniciativas importantes. Identifica ameaças ou oportunidades. Elabora os relatórios de progresso e analisa as questões que afetam a produtividade, lucratividade e crescimento da empresa. Planeja as demandas da equipe e certifica-se que todos os prazos para envio dos relatórios sejam cumpridos. Interage junto aos gestores no aprimoramento e desenvolvimento de novas visões de relatórios que atenda suas necessidades e cria condições para a tomada de decisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-planejamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (942, 'Coordenador de Pós-venda', 'Implementa as políticas de pós-vendas definidas pela empresa, acompanha indicadores de reclamações e trocas de produtos, intermedeia o atendimento aos clientes e desenvolve soluções para os problemas apresentados, com objetivo de garantir a satisfação e qualidade nos serviços prestados.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (943, 'Coordenador de Prevenção de Perdas', 'Coordena a segurança e prevenção de perdas, planeja gestão de risco para controle de quebras e prevenção de furtos e roubos, elabora normas e políticas referentes às operações e desenvolve estratégias para aplicar ações corretivas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-prevencao-de-perdas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (944, 'Coordenador de Pricing', 'Atua com gestão de equipe e pesquisa de preços de mercado. Identifica e analisa os principais competidores. Faz apresentação de projetos para executivos, análise e cálculo de competitividade, desenvolve tabelas comparativas de custos e elabora relatórios referentes às comparações de preços praticados.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (945, 'Coordenador de Processos', 'Define e aplica uma metodologia estruturada de gerenciamento de processos e monitora os processo da empresa. Comunica as mudanças da empresa e fornece treinamentos constantes. Define, desenvolve, configura e apoia todas as aplicações de informática. Desenvolve e implanta soluções mais eficientes e de baixo custo para a empresa. Desenvolve soluções de informática. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-processos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (946, 'Coordenador de Produção', 'Coordena, programa e acompanha os processos de produção, lidera operadores em chão de fábrica, analisa e avalia os planejamentos de fabricação e define os indicadores de controle, a fim de garantir a padronização técnica, qualidade, custo e entrega dentro do prazo.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (947, 'Coordenador de Produto', 'Realiza pesquisa de mercado e faz a análise comparativa dos produtos. Acompanha projetos de desenvolvimento de produtos, cronogramas e lançamento de produtos. Atua com produção de propostas técnicas e comerciais, relacionados aos seus produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-produto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (948, 'Coordenador de Projeto Social', 'Atua com planejamento e execução dos programas de interação social. Realiza o planejamento e execução de projeto, de acordo com o plano de ação e o cronograma. Operacionaliza eventos, cursos e palestras dos projetos. Acompanha e avalia os resultados dos projetos executados. Orienta as pessoas envolvidas nos projetos. Organiza os recursos dos projetos. Planeja, solicita e presta contas dos recursos financeiros e materiais necessários. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-projeto-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (949, 'Coordenador de Projetos', 'Coordena o planejamento, detalhamento, estimativa de custos e cronograma de projetos. Participa de reuniões e elabora relatórios técnicos da área.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-projetos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (950, 'Coordenador de Projetos de Construção Civil', 'Coordena projetos de engenharia civil para construção ou manutenção de obras, estuda viabilidade e riscos, administra cronograma e custos e desenvolve métodos de trabalho para orientar o andamento dentro dos padrões técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-projetos-de-construcao-civil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (951, 'Coordenador de Qualidade', 'Coordena e executa os programas de auditoria interna. Levanta e analisa os procedimentos existentes em todas as áreas da empresa. Administra reuniões do comitê da qualidade para análise crítica do sistema. Faz implantação de gestão do sistema, visando contribuir para o aprimoramento dos processos, sistema de informações e qualidade dos produtos, serviços e manutenção da certificação das normas ISO. Administra as não conformidades geradas, providencia ações corretivas para eliminar qualquer não conformidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (952, 'Coordenador de Recepção', 'Coordena as atividades de recepção, avalia o desempenho de equipe, monitora escalas e realiza o levantamento das necessidades de treinamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-recepcao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1023, 'Dentista Protesista', 'Responsável por diagnosticar o paciente. Planeja e requisita próteses.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-protesista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (953, 'Coordenador de Recrutamento e Seleção', 'Faz gestão de equipe de recrutamento e seleção, elabora e controla os indicadores da área, acompanha as atividades de recrutamento e seleção. Desenvolve e implementa os procedimentos e políticas de recursos humanos. Cria e implanta planos de conscientização motivacionais, orientativos e sociais de integração. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-recrutamento-e-selecao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (954, 'Coordenador de Recursos Humanos', 'Coordena o departamento de recursos humanos atuando com treinamento,recrutamento e seleção, admissão e demissão de funcionários, folhas de pagamento, cálculos rescisórios e de férias, ponto eletrônico e desenvolve planos de benefícios e salários.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (955, 'Coordenador de Redes', 'Atua com liderança e coordenação de projetos de ampliação de redes, controle de projetos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (956, 'Coordenador de Reservas', 'Coordena reservas de clientes, controla disponibilidade de apartamentos e acompanha resultados, a fim de garantir a qualidade dos serviços prestados e satisfação dos clientes. Elabora estratégias de vendas para captação de novas hospedagens.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-reservas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (957, 'Coordenador de Responsabilidade Social', 'Elabora, desenvolve, incentiva e coordena projetos na área de responsabilidade social. Responder pela elaboração de relatórios de controle da área, conduzir e acompanhar reuniões, entre demais atividades. Levanta informações para indicadores de responsabilidade social e presta suporte às campanhas internas beneficentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-responsabilidade-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (958, 'Coordenador de Restaurante', 'Coordena os funcionários na elaboração de refeições, administra e controla a reposição e abastecimento de alimentos e acompanha os recursos financeiros do restaurante.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-restaurante/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (960, 'Coordenador de Segurança da Informação', 'Coordena e identifica oportunidades de aplicação de tecnologia e interage com outras áreas de maneira a assegurar a segurança das informações da empresa.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (961, 'Coordenador de Segurança do Trabalho', 'Coordena as atividades de segurança do trabalho, elabora e divulga normas e regulamentos internos, controla a instalação de equipamentos de segurança e acompanha perícias técnicas para levantamento de riscos e aplicação de medidas corretivas. Orienta o funcionamento da CIPA (Comissão Interna de Prevenção de Acidentes) e ministra palestras e treinamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-seguranca-do-trabalho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (962, 'Coordenador de Segurança Patrimonial', 'Coordena, distribui e acompanha as atividades de segurança patrimonial, instrui a equipe, com assuntos inerentes ao dia a dia, regulamentos e esquemas estabelecidos pela gerência de segurança. Orienta os colaboradores da Central de Operações, rastreando todo sistema de CFTV com o objetivo de localizar pessoas em atitudes suspeitas nas dependências e elabora relatório de ocorrência diária.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-seguranca-patrimonial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (963, 'Coordenador de Seguros', 'Coordena atividades relativas à área de Seguros, estabelecendo metodologia, realizando estudos de leis, regulamentos e publicações especializadas. Promove reuniões para disseminar instruções e possibilitar o perfeito desenvolvimento dos programas propostos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (964, 'Coordenador de Service Desk', 'Faz criação de processos e procedimentos de Service Desk, catálogo de serviços, métricas de atendimento de forma geral como Pico, TMA (tempo médio de atendimento), SLA (Objetivos de Nível de Serviço), SLO(Acordo de Nível de Serviço). Atua com liderança de equipes locais e remotas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-service-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (965, 'Coordenador de Serviços Gerais', 'Coordena as atividades de serviços gerais, recepção e expedição de correspondência, limpeza, copa, portaria, telefonia e transporte interno, organizando e orientando os trabalhos. Sugere a aquisição de novos equipamentos e contratação de mão-de-obra especializada para o aprimoramento das atividades desenvolvidas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (966, 'Coordenador de Sinistro', 'Coordena processos de classificação e análise de sinistros, a fim de cumprir com as políticas da empresa e definir normas e procedimento na redução de sinistralidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-sinistro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (967, 'Coordenador de Suporte Técnico', 'Atua com liderança de suporte em TI, projetos e processos de TI. Coordena a equipe técnica, definido escala, prioridade, agenda de visitas dos técnicos. Faz coordenação de service desk, service support e field services. Faz gerenciamento dos níveis de serviços e avaliação de performances. Elabora relatórios operacionais e gerenciais de TI e negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-suporte-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (968, 'Coordenador de Suprimentos', 'Elabora meta e indicadores e define as políticas de compras e de administração de estoques. Controla a execução do contrato, faz cumprir as cláusulas, negocia, renova e reincide contratos com fornecedores. Acompanha incidentes relativos a pagamentos, aplicação de penalidades e apuração de responsabilidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-suprimentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (969, 'Coordenador de Sustentabilidade', 'Coordena programas e projetos de sustentabilidade, no âmbito social, ambiental e econômico, administra orçamentos, estabelece parcerias, planeja estratégias na captação de recursos e monitora investimentos para alcance dos melhores resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-sustentabilidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (970, 'Coordenador de Tecnologia da Informação', 'Coordena as atividades das equipes de TI, avalia e identifica soluções tecnológicas para otimizar os processos, planeja projetos de implantação de sistemas e acompanha as necessidades do negócio e dos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-tecnologia-da-informacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (971, 'Coordenador de Telecomunicações', 'Implanta, avalia e recomenda o uso de tecnologias de comunicação, definição das prioridades de execução das atividades, avaliação dos custos com telecomunicações corporativas e indicação de ações de proteção das informações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-telecomunicacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (972, 'Coordenador de Tesouraria', 'Coordena os processos de análise e elaboração de fluxo de caixa previsto e realizado, a fim de identificar discrepâncias e solicitar correções. Acompanha o desempenho econômico-financeiro da empresa por meio de levantamento de dados e elabora relatórios e indicadores de tesouraria.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-tesouraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (973, 'Coordenador de Topografia', 'Coordena os processos de topografia no levantamento, nivelamento e locação em obras civis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-topografia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (974, 'Coordenador de Trade Marketing', 'Coordena o planejamento estratégico de marketing e vendas por meio da análise dos hábitos e preferências dos consumidores, alinhado aos objetivos da marca, tomada de decisões e resultados. Define canais para comercialização, estabelece a necessidade de adaptação de produtos e logística e analisa indicadores de performance.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-trade-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (975, 'Coordenador de Transportes', 'Coordena as operações de transportes, como embarque, transbordo e desembarque de carga. Acompanha performance dos meios de transportes e distribuição, desenvolve indicadores e identifica melhoria nos processos para atender prazo e qualidade no serviço.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (976, 'Coordenador de Treinamento e Desenvolvimento', 'Coordena os programas de treinamento e aperfeiçoamento de pessoal para todas as áreas da empresa. Elabora orçamentos para treinamento externo e cronogramas de programação. Define objetivos, conteúdo programático, metodologias de ensino e recursos instrucionais no processo de treinamento. Analisa os programas oferecidos pela empresa para todos os níveis hierárquicos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (977, 'Coordenador de Usinagem', 'Coordena os processos de usinagem para garantir a programação de produção, disponibilidade de equipamentos e definição de pessoal, elabora procedimentos de trabalho no atendimento às especificações técnicas e monitora procedimento e normas de qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-usinagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (978, 'Coordenador de Vendas', 'Coordena as atividades de vendas, orienta vendedores e representantes e analisa performance da equipe, visando o cumprimento de metas estabelecidas. Acompanha os processos na pós-venda, prazos de entregas e pagamentos e participa na definição da estratégia de vendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (979, 'Coordenador de Visual Merchandising', 'Coordena estudo de tendências de visual merchandising, elabora campanhas temáticas e desenvolve projetos de melhorias em lojas propondo ações para aprimorar performance de exposição e motivar consumidores às compras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-de-visual-merchandising/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (980, 'Coordenador Editorial', 'Coordena o cronograma e processo de edição e revisão de conteúdos de livros, jornais, revistas, dentre outros documentos, garantindo a qualidade e prazos estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-editorial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (981, 'Coordenador Eletrônico', 'Coordena as atividades de instalação e manutenção eletrônica preventiva e corretiva de máquinas e equipamentos para cumprimento dos planos e ordens em conformidade com os padrões de qualidade e segurança.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-eletronico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (982, 'Coordenador Financeiro', 'Coordena as atividades relativas as áreas de contas a pagar e receber, crédito e cobrança, tesouraria e faturamento. Acompanha fluxo de caixa e notas fiscais eletrônicas e negociação com instituições bancárias. Elabora relatórios financeiros para apresentação à gerência, participando na tomada de decisões sobre investimentos de recursos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-financeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (983, 'Coordenador Fiscal', 'Coordena procedimentos para atender os requisitos legais em  conformidade com a legislação municipal, estadual e federal. Controla fechamento fiscal, apuração de impostos e validação das obrigações acessórias, elabora relatório gerencial de demonstração financeira e acompanha atendimento a fiscalização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-fiscal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (984, 'Coordenador Imobiliário', 'Coordena as rotinas de atendimento, prospecção de clientes, venda e locação de imóveis, acompanha a elaboração de cadastro, contrato e condição de pagamento. Analisa mercado e concorrência, identifica oportunidades de negócios e elabora relatórios gerenciais para alcance das metas e resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-imobiliario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (986, 'Coordenador Jurídico', 'Coordena equipe jurídica interna e externa, realiza planejamento da área para proteger os direitos e interesses da companhia, mantendo suas operações dentro das normas legais e defendendo seus interesses. Seleciona e coordena escritórios de advocacia terceirizados. Executa contatos e interlocução com autoridades governamentais quando necessário.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-juridico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (987, 'Coordenador Médico', 'Responsável pela coordenação da equipe médica, elaboração e acompanhamento do plano de atendimento médico dos pacientes. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-medico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (988, 'Coordenador Operacional', 'Coordena as rotinas operacionais das áreas de logística e manutenção predial. Recebe, confere, armazena e distribui produtos e materiais, realizando o lançamento e a movimentação de entrada e saída do estoque. Elabora relatórios e atua na implementação de melhorias para a otimização de processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-operacional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (989, 'Coordenador Pedagógico', 'Coordena as atividades dos docentes da instituição e as disciplinas lecionadas, assegurando o cumprimento dos objetivos e conteúdos dos programas educacionais. Assessora a direção da escola no desenvolvimento de ações pedagógicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-pedagogico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (990, 'Coordenador Tributário', 'Coordena planejamento tributário e revisão de processos de acordo com as alterações das leis, a fim de reduzir os riscos e a carga tributária da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-tributario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (991, 'Copeiro', 'Prepara alimentos e arruma bandejas e mesas. Atende o público interno, servindo e distribuindo alimentos e bebidas. Recolhe utensílios e equipamentos utilizados, promovendo a limpeza, higienização e conservação da copa e da cozinha.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/copeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (992, 'Copiador de Chapa', 'Realiza a impressão de chapas metálicas, confere e armazena materiais e limpa máquinas processadoras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/copiador-de-chapa/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (993, 'Coreógrafo', 'Elabora movimentos de dança de variados ritmos realizados por bailarinos, para montagem de obras coreográficas. Desenvolve e dirige espetáculos musicais para teatro, cinema ou televisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coreografo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (994, 'Corretor de Imóveis', 'Realiza o atendimento a clientes e o intermédio da compra, venda, permuta, locação e administração de imóveis e avalia a documentação necessária para efetuar a transação. Capta imóveis para novos negócios e acompanha clientes em visitas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/corretor-de-imoveis/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (996, 'Cortador', 'Prepara materiais, equipamentos e ferramentas para a confecção de artefatos para o corte e cortam a mão e a máquina. Realiza testes e inspeções e compõem conjuntos e lotes de peças. Prepara máquinas para corte e realiza manutenção produtiva de máquinas, equipamentos e ferramentas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cortador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (997, 'Cortador de Tecidos', 'Realiza corte de tecidos manuais ou em máquinas, enfesto e risco em material.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cortador-de-tecidos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (998, 'Cortador de Vidro', 'Realiza o corte, a furação e lapidação de vidros, analisando informações das áreas de interface, seleciona ferramentas, requisita matérias-primas, equipamentos e materiais para movimentação na linha de produção. Risca de maneira precisa a parte superior do vidro. Controla os processos de corte e acabamento em vidros e cristais.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (999, 'Costureiro', 'Prepara máquinas e amostras de costura, manuseia equipamentos na montagem em série de peças de vestuário. Projeta e modela confecções de roupas sob encomenda e na preparação de peças e produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/costureiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1000, 'Cozinheiro', 'Prepara pratos, atentando para as especificações da comanda ou cardápio. Manipula e tempera alimentos e verifica o estado de conservação dos ingredientes utilizados, para atender as exigências dos pedidos e assegurar o padrão de qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cozinheiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1001, 'Crepeiro', 'Prepara crepes e recheios doces e salgados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/crepeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1002, 'Cronoanalista', 'Planeja, controla e programa a produção, levanta dados sobre tempo e suprimentos para sugerir melhorias nos processos de fabricação. Auxilia na elaboração de manuais com instruções de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cronoanalista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1003, 'Cuidador de Crianças', 'Realiza os cuidados pertinentes à crianças de 0 à 16 anos, como preparo da alimentação, banho, cuidado com as roupas, limpeza e organização do quarto, esterilização dos brinquedos e auxílio no seu desenvolvimento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cuidador-de-criancas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1004, 'Cuidador de Idosos', 'Auxilia o idoso que apresente ou não limitações nas atividades da vida cotidiana. Desempenha atividades voltadas ao seu bem estar físico, mental e social.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cuidador-de-idosos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1005, 'Cumim', 'Auxilia o garçom no atendimento e serve refeições e bebidas aos clientes em restaurantes, bares, clubes e eventos. Organiza mesas e utensílios e limpa salão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/cumim/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1006, 'Curador de Arte', 'Organiza e desenvolve espaço para exposição de arte, em parceria com museólogos, arquitetos e montadores e seleciona artistas para mostra.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/curador-de-arte/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1007, 'Dançarino', 'Desenvolve e participa de apresentações de dança individuais ou em conjunto e apoia na montagem de obras coreográficas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dancarino/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1008, 'Data Scientist', 'O Cientista de Dados - ou Data Scientist - é considerado a nova geração de especialistas em análise de dados, com habilidades técnicas para resolver problemas complexos e curiosidade para explorar quais problemas necessitam de solução.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1009, 'DBA', 'Planeja e executa as manutenções em bancos de dados de produção. Pesquisa inovações tecnológicas para banco de dados. Garante que backups de produção estejam sendo executados. Maximiza e aprimora a performance do banco de dados. Garante a segurança dos bancos de dados de produção. Administra servidores de bancos de dados de produção. Identifica riscos de atrasos nos trabalhos. Presta suporte aos usuários, orienta analistas e desenvolvedores na otimização de performance das aplicações referente a área de banco de dados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dba/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1010, 'DBA Oracle', 'Administra banco de dados, cria e implementa sistemas de integração com outros servidores e sistemas, atuando na análise, controle e modificação de métodos e processos, localizando falhas, ineficiências e implementando soluções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dba-oracle/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1011, 'DBA SQL', 'Administra banco de dados SQL, cria e implementa sistemas de integração com outros servidores e sistemas, atuando na análise, controle e modificação de métodos e processos, localizando falhas, ineficiências e implementando soluções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dba-sql/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1012, 'Decorador', 'Projeta soluções para espaços interiores residenciais, comerciais e institucionais, visando harmonizar móveis, objetos e acessórios sem alteração física da obra. Pesquisa produtos e materiais, planeja cores, acabamentos e iluminação de acordo com a personalidade e necessidades do cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/decorador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1013, 'Dedetizador', 'Dedetiza terrenos baldios, residências, prédios comerciais e órgãos públicos para eliminar e controlar pragas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dedetizador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1014, 'Degustador', 'Realiza abordagem de clientes oferecendo produto em forma de degustação. Faz a venda de mercadorias, demonstra seu funcionamento oferecendo para degustação. Distribui amostras e informa sobre suas qualidades e vantagens de aquisição.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/degustador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1015, 'Dentista', 'Cuida da saúde e da estética da boca. Restaura, extrai e limpa dentes, projeta e coloca próteses e realiza algumas cirurgias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1016, 'Dentista Auditor', 'Analisa planos de tratamento indicado pelo dentista credenciado no plano odontológico. Cuida da documentação odontológica, vendo sua veracidade.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1017, 'Dentista Endodontista', 'Trata alterações na poupa e na raiz dos dentes . Faz tratamento de canal.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-endodontista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1018, 'Dentista Estomatologista', 'Previne, diagnostica e tratar as doenças que se aparecem na cavidade da boca e no maxilo-mandibular. Encaminha ao médico se necessário.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-estomatologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1019, 'Dentista Implantodontista', 'Faz cirurgias e próteses isoladas, parciais ou completas (dentaduras) nos maxilares, utilizando implantes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-implantodontista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1020, 'Dentista Odontopediatra', 'Trata problemas bucais em dentes de criança.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-odontopediatra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1021, 'Dentista Ortodontista', 'Altera a mordida e a posição dos dentes com aparelhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-ortodontista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1022, 'Dentista Periodontista', 'Cuida de gengivas e dos ossos que dão sustentação aos dentes. Faz cirurgias raspagens, e outros procedimentos em volta dos dentes afetados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-periodontista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1024, 'Dentista Traumatologista', 'Diagnostica traumatismos, lesões na boca, na face e no sistema estomatogmático (que envolvem o sistema de mastigação, maxilar, mandíbula e gengiva). Faz cirurgias, implantes, transplantes e enxertos para recuperá los.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dentista-traumatologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1025, 'Depiladora', 'Faz depilação usando técnicas e manuseio de produtos com equipamentos adequados e esterilizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/depiladora/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1026, 'Dermoconsultor', 'Demonstra dermocosméticos aos consumidores e indica produtos de acordo com a necessidade do cliente. Acompanha estoque e realiza treinamento a colaboradores de rede de perfumaria e drogaria.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/dermoconsultor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1027, 'Desenhista', 'Elabora e interpreta desenhos (esboços) conforme as normas técnicas, visando demonstrar as características técnicas e funcionais dos equipamentos. Modifica e atualiza os desenhos existentes. Realiza mudanças sobre os desenhos adequando-os as possibilidades de produção local. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1028, 'Desenhista Cadista', 'Elabora os desenhos técnicos em CAD. Elabora projeto de produtos, máquinas e instalações, interpreta os projetos existentes, analisa o croqui e aplica as normas técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-cadista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1029, 'Desenhista Copista', 'Analisa solicitações de desenhos. Interpreta documentos de apoio, tais como plantas, projetos, catálogos, croquis e normas. Observa as características técnicas de desenhos, define formatos e escalas, sistemas de representação e prioridades de desenhos, conforme cronogramas. Desenha detalhes de projetos de desenhos. Envia desenhos para revisão, realiza cópias de segurança e disponibiliza desenhos finais e revisões.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-copista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1030, 'Desenhista Elétrico', 'Elabora desenhos de projetos de painéis e comandos elétricos, de acordo com esboços, modelos ou especificações técnicas. Desenvolve instruções de trabalho e sugere melhorias para o processo de desenvolvimento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-eletrico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1031, 'Desenhista Industrial', 'Elabora desenhos industriais referentes a dispositivos, equipamentos, instalações e produtos e define formatos e escalas, de acordo com especificações técnicas, normas e padronizações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1032, 'Desenhista Mecânico', 'Elabora desenhos de partes ou conjuntos de máquinas de fabricação, ferramentas e dispositivos, de acordo com normas e especificações técnicas. Realiza o levantamento de custos e especifica materiais usados no projeto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1033, 'Desenhista Orçamentista', 'Realiza a leitura e interpretação de desenhos técnicos, elabora orçamentos e visita clientes para medição.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-orcamentista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1034, 'Desenhista Projetista', 'Elabora e emite desenhos relativos aos projetos de produtos, máquinas e ferramentas. Desenvolve desenhos em AutoCAD, SolidWorks e outras ferramentas. Elabora fluxogramas, layouts, peças para fabricação e documentação técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-projetista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1035, 'Desenhista Projetista Civil', 'Elabora desenhos de projetos de infraestrutura e construção civil, de acordo com especificações técnicas, estuda viabilidade e realiza levantamento quantitativos de materiais e serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-projetista-civil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1036, 'Desenhista Projetista Elétrico', 'Elabora desenhos de projetos elétricos, de acordo com especificações técnicas, estuda viabilidade, realiza levantamentos quantitativos de materiais e serviços e acompanha as fases do projeto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-projetista-eletrico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1037, 'Desenhista Projetista Hidráulico', 'Elabora desenhos de projetos de instalações hidráulicas, de acordo com especificações técnicas, estuda viabilidade, realiza levantamentos quantitativos de materiais e serviços e acompanha as fases do projeto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-projetista-hidraulico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1038, 'Desenhista Projetista Mecânico', 'Realiza desenhos de projetos mecânicos realizando o planejamento e detalhamento das peças. Implanta e controla a documentação, acompanha as fases do projeto e o produto final. Trabalha de acordo com normas e procedimentos técnicos e segurança no trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-projetista-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1039, 'Desenhista Técnico', 'Executa desenho de peças utilizadas em equipamentos mecânicos, faz desenhos de equipamentos com vista explodida, lista de materiais com codificações e especificações técnicas das peças e materiais usados para compra. Organiza arquivos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desenhista-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1040, 'Desentupidor', 'Realiza desentupimento de empresas, condomínio, residências e demais estabelecimentos, manipulando ferramentas e aplicando produtos químicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/desentupidor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1042, 'Designer de Acessórios', 'Realiza desenhos de acessórios como sapatos, bolsas e artefatos em geral de acordo as necessidades do consumidor e análise das tendências de moda.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-acessorios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1043, 'Designer de Calçados', 'Desenvolve projetos de calçados, envolvendo a pesquisa referencial, cartela de cores, materiais e criação de modelos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1044, 'Designer de Embalagem', 'Desenvolve projetos de novas embalagens e identifica alternativas e soluções de layout, de acordo com as exigências da empresa ou clientes e atendimento as legislações e normas de rotulagens. Valida e acompanha aprovações de layout, define conceito de identidade visual das embalagens e elabora documentação técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-embalagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1045, 'Designer de Interação', 'Prevê possíveis restrições e possibilidades de design de acordo com as tecnologias e recursos disponíveis, cria símbolos gráficos e trata imagens. Faz testes e pesquisas de usabilidade. Avalia e incorpora as necessidades do usuário para o site como: produto, sistema e aplicativo com base em pesquisa.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1046, 'Designer de Interface', 'Atua com a criação de interfaces gráficas para aplicativos móveis e desktop. Realiza testes e concepção de produtos junto ao setor de Front-end. Ajuda na definição de problemas e oportunidades. Cria wireframes, protótipos e guidelines dos produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-interface/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1047, 'Designer de Interiores', 'Elabora projetos para espaços internos, residenciais, comerciais e industriais, seleciona desde o mobiliário adequado até os materiais, cores, acabamento e iluminação. Desenvolve o cronograma físico e financeiro e realiza visita aos fornecedores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-interiores/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1093, 'Duteiro', 'Instala dutos de ar-condicionado, seguindo normas de qualidade, realiza manutenção preventiva, e traçado, corte, dobramento e fechamento na produção de dutos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/duteiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1048, 'Designer de Jogos', 'Planeja e cria jogos eletrônicos para computador, videogame, celular ou internet, desenvolve o layout na disposição dos menus e escolha de fontes e elabora a parte gráfica no desenho de cenários e personagens e composição de cores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-jogos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1049, 'Designer de Joias', 'Desenvolve modelos de joias, realiza desenhos e esboços e elabora projetos de restauração e conservação de peças antigas. Estuda tendências de comportamento e as necessidades de mercado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-joias/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1050, 'Designer de Moda', 'Desenvolve produtos de moda de acordo com sua função estética, prática e simbólica, necessidades do consumidor e demandas do mercado. Pesquisa tecnologias têxtil, cria estampas e participa na compra de tecidos ou materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-moda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1051, 'Designer de Produto', 'Atua na concepção estética de produtos, elabora a identidade visual com foco na adequação material, ergonomia, atenção ambiental, praticidade e beleza. Analise desenho e requisitos, seleciona matéria-prima a ser utilizada, define processo produtivo, acompanha a fabricação e comercialização, na definição de material promocional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-produto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1052, 'Designer de Sobrancelha', 'Desenha sobrancelhas por meio da retirada de pelos e identifica o modelo mais adequado para cada formato de rosto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-de-sobrancelha/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1053, 'Designer Digital', 'Desenvolve interfaces digitais de softwares, sites, jogos, entre outras aplicações, por meio de conceitos de criação, arte e estética.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-digital/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1054, 'Designer Gráfico', 'Elabora conceitos visuais e projetos gráficos de informação impressa e criação de artes de comunicação visual online e offline. Realiza a diagramação de documentos e auxilia na construção de uma identidade visual.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-grafico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1055, 'Designer Instrucional', 'Implementa, avalia e planeja o desenvolvimento de projetos didáticos e metodológicos nas modalidades de ensino presencial ou a distância. Acompanha e avalia os processos educacionais, pesquisa e desenvolve conteúdos e roteiriza materiais para diferentes meios e mídias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-instrucional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1056, 'Designer Multimídia', 'Atua com rotinas de animação e ilustração digital, com criação de interfaces e acompanhamento de projetos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-multimidia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1057, 'Designer Têxtil', 'Elabora projetos de estamparia, texturas e cores de tecidos para moda e decoração, de acordo com as tendências de mercado e coleções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/designer-textil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1058, 'Despachante', 'Realiza a regularização dos documentos de veículos perante aos órgãos competentes. Solicita a documentação necessária para efetuar os processos e atender aos clientes. Garante o desembaraço e liberação da documentação de veículos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/despachante/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1059, 'Despachante Aduaneiro', 'Realiza o desembaraço de mercadorias e bagagens, solicita vistoria aduaneira, efetua pagamento de taxas e impostos e apresenta documentos à Receita Federal e demais órgãos pertinentes, a fim de garantir a liberação de cargas. Opera sistema de comércio exterior para registro de informações sobre mercadorias e assessora clientes nos processos de importação e exportação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/despachante-aduaneiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1060, 'Despachante Imobiliário', 'Presta assessoria aos clientes na tramitação e despacho de papéis em entidades públicas na compra de imóveis, envolvendo o reconhecimento de firmas, emissão de documentos junto aos cartórios e levantamento de débitos de IPTU', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/despachante-imobiliario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1061, 'Despachante Operacional de Voo', 'Fornece informações aos pilotos sobre reserva, manual e sistema de coordenação de voo. Emite navegações, verifica meteorologia e quantidade de passageiros, realiza cálculo de combustível e controla informações para o plano de voo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/despachante-operacional-de-voo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1062, 'Diagramador', 'Diagrama conteúdo de acordo com projeto gráfico, adaptando textos e imagens a uma programação visual. Desenvolve layouts, trata imagens e distribui os elementos gráficos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diagramador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1064, 'Digitador de Laudos', 'Digita laudo médico, acompanha exames e organiza documentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/digitador-de-laudos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1065, 'Digitalizador', 'Realiza a indexação e digitalização de documentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/digitalizador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1066, 'Diligenciador', 'Realiza processo de diligenciamento de materiais e serviços junto aos fornecedores, analisando situação de fabricação ou montagem, de acordo com contratos e cronogramas, para garantir os prazos de entrega estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diligenciador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1067, 'Diretor Administrativo', 'Dirige, planeja, organiza e controla as atividades, planos e programas das áreas administrativas da empresa. Fixa as políticas estratégicas de gestão dos recursos financeiros, administrativos e adequação de processos, tendo em vista os objetivos da organização.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1068, 'Diretor Comercial', 'Responde pela direção das atividades e políticas do departamento comercial, alinhando a área aos objetivos estratégicos da empresa. Avalia resultados, acompanha indicadores de desempenho e desenvolve plano de negócios para cumprir metas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1069, 'Diretor de Arte', 'Desenvolve projetos gráficos para criação de identidade visual de logos e peças de comunicação. Elabora layouts e artes criativas atendendo briefings e dirige a produção da arte final.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-arte/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1070, 'Diretor de Cinema', 'Gerencia a seleção de elenco e toda a composição da obra, como trilha sonora, efeitos especiais, figurinos e cenários. Define o cronograma de filmagens e participa na conclusão técnica da produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-cinema/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1071, 'Diretor de Compras', 'Planeja, dirige e controla as compras de materiais, máquinas e equipamentos, de acordo com as políticas e necessidades da empresa. Orienta e participa no desenvolvimento de novos fornecedores e definição de forma a obter melhores preços, condições de pagamento e prazo de entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1072, 'Diretor de Criação', 'Dirige equipe no planejamento e desenvolvimento de peças publicitárias de acordo com o briefing, envolvendo as rotinas de definição de mídia e criação de layout.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-criacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1073, 'Diretor de Logística', 'Dirige e planeja as atividades da área de Logística, envolvendo o transporte, compra e armazenagem de matérias-primas, máquinas, equipamentos, materiais e serviços em geral. Investiga e recomenda o desenvolvimento de novas fontes de suprimentos, solicita avaliação técnica de seu potencial em termos de produtos, equipamentos, capacidade produtiva, pessoal, responsabilidades financeiras e demais referências.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1074, 'Diretor de Loja', 'Dirige e planeja atividades nos comércios varejista e atacadista e administra e estrutura equipes de trabalho. Gerencia recursos materiais e financeiros, contratos e projetos, planeja estratégias de vendas, preços e promoções e acompanha indicadores de desempenho da loja.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1075, 'Diretor de Marketing', 'Desenvolve estratégias de marketing e identidade visual das marcas. Define canais de comunicação específicos, exclusivos e adequados para cada público (interno e externo). Planeja e define campanhas voltadas para promoção de produtos e serviços. Estabelece objetivos, políticas e ações de acordo com as tendências do mercado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1076, 'Diretor de Negócios', 'Planeja, desenvolve estratégias de negócios para pessoa física ou jurídica, de acordo com o modelo da empresa e pelo atingimento das metas de faturamento. Realiza prospecção e atendimento dos maiores clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-negocios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1077, 'Diretor de Obras', 'Planeja, organiza, dirige e controla os projetos de engenharia civil para construção e manutenção de obras de edificações, definindo sistemas e programas de atuação. Realiza gestão baseada em resultados, coordena equipes com foco na obtenção de margem de lucro do empreendimento e na gestão de pessoas. Coordena os processos de normatização, racionalização e avaliação de custo e benefício dos programas e processos de engenharia civil.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1078, 'Diretor de Operações', 'Dirige as operações da empresa, desenvolve o planejamento estratégico, orçamento econômico-financeiro e plano de investimento empresarial e implanta o projeto de negócios para alcançar os objetivos de rentabilidade, custos e crescimento. Monitora a implementação e aplicação de políticas, processos e procedimentos organizacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-operacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1079, 'Diretor de Recursos Humanos', 'Responde pelo planejamento, organização, direção e controle das atividades de recursos humanos, através da definição de normas e políticas. Estabelece diretrizes para implantação e desenvolvimento de programas de administração de salários e benefícios, treinamento, desenvolvimento, avaliação de desenvolvimento, planos de carreiras, etc.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1080, 'Diretor de Teatro', 'Dirige a montagem e produção de espetáculos, como teatro ou musical, seleciona elenco e define os processos de cenografia, sonoplastia, ambientação e figurinos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-teatro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1081, 'Diretor de Tecnologia da Informação', 'Dirige as atividades de tecnologia da informação, observando cronogramas, prioridades e orçamentos aprovados. Acompanha o levantamento das necessidades dos usuários, definindo estratégicas e plano de investimento para prover a empresa de sistemas e recursos. Administra infraestrutura de redes, programas e sistemas implantados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-tecnologia-da-informacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1082, 'Diretor de Vendas', 'Responde pela direção da área de acordo com os indicadores estabelecidos, desenvolve a estratégia de vendas e define políticas, metas e programas de incentivo, a fim de aumentar o potencial de receita, atendendo as previsões e planos de crescimento dos negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1083, 'Diretor Financeiro', 'Planeja, organiza, dirige e controla as atividades financeiras da empresa, fixando políticas para a gestão dos recursos disponíveis e para a estruturação, racionalização e adequação dos serviços de apoio. Implanta processos financeiros, contábeis, fiscais, de controladoria e de escrituração, respondendo pelo planejamento, pela organização e pelo desenvolvimento de curto, médio e longo prazo. Analisa o resultado operacional e elabora relatórios gerenciais demonstrando a eficácia da aplicação dos recursos e o desempenho econômico da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-financeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1084, 'Diretor Geral', 'Dirige, planeja, organiza e controla as atividades de diversas áreas da empresa, fixando políticas de gestão dos recursos financeiros, administrativos, estruturação, racionalização, e adequação dos serviços diversos. Desenvolve planejamento estratégico, identifica oportunidades, avalia a viabilidade e faz recomendações sobre novos investimentos ou desenvolvimento de novos negócios.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1085, 'Diretor Hospitalar', 'Atua no planejamento estratégico da organização e no gerenciamento de um hospital. Mantém a infraestrutura do espaço físico, determinando o melhor uso para ele. Define o número de médicos, enfermeiros e especialidades que o local pode atender. Faz o planejamento da manutenção preventiva dos equipamentos médicos, o controle dos estoques de materiais, a limpeza e até a destinação dos resíduos hospitalares.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-hospitalar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1086, 'Diretor Industrial', 'Planeja, organiza e controla as atividades de fabricação dos produtos da empresa. Acompanha  e avalia os resultados da produção e se relaciona com toda área Operacional. Prepara e acompanha o plano de operações anual. Realiza implantação do sistema Lean Manufacturing.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1087, 'Diretor Jurídico', 'Define as políticas e objetivos, assegura a obtenção dos resultados estabelecidos nos planos da área jurídica e das áreas correlatas. Conduz a elaboração e implementação dos planos estratégicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-juridico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1088, 'Diretor Pedagógico', 'Coordena, planeja e acompanha junto com a equipe pedagógica. Administra o cotidiano escolar, desenvolve os calendários, participa do planejamento, execução das reuniões pedagógicas, conselhos de classe e reuniões de pais. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/diretor-pedagogico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1089, 'Divulgador', 'Realiza a divulgação interna ou externa de produtos e serviços e agenda reuniões com possíveis clientes, a fim de garantir a venda final.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/divulgador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1091, 'Documentador', 'Criação e revisão de documentos referentes a softwares, elaboração de e-learning e elaboração de apostilas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/documentador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1092, 'Doméstica', 'Cuida da limpeza e conservação da casa, lava e passa roupas, cozinha e atividades do dia-a-dia.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/domestica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1094, 'Ecólogo', 'Avalia riscos e impactos ambientais, acompanha o planejamento e a instalação de empreendimentos para conseguir a licença ambiental. Gerencia projetos, faz auditoria e consultoria ambiental para empresas públicas e privadas e organizações não governamentais. Implanta e fiscaliza projetos de restauração de ambientes degradados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ecologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1095, 'Economista', 'Realiza o planejamento, execução orçamentária, elabora o plano de negócio, faz a projeção de resultados econômicos e elabora demonstrativos de resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/economista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1096, 'Editor de Imagens', 'Realiza a edição, recorte e tratamento de imagens. Participa na produção de vinhetas, comerciais, filmes e documentários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/editor-de-imagens/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1097, 'Editor de Moda', 'Desenvolve roteiros sobre moda, direciona pautas, orienta a produção na escolha de matérias, identifica tendências e acompanha o trabalho de fotografia e reportagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/editor-de-moda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1098, 'Editor de Texto', 'Faz revisão e preparo definitivo de conteúdos a serem divulgados em jornais, revistas, televisão, rádio, internet, assessorias de imprensa e quaisquer outros meios de comunicação com o público, bem como conferir materiais didáticos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/editor-de-texto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1099, 'Editor de Vídeo', 'Estrutura vídeos e narrações, edita imagens e áudio e acompanha a finalização de produções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/editor-de-video/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1100, 'Educador Ambiental', 'Prepara e ministra aulas sobre educação ambiental, desenvolve planos de ensino e implanta projetos em instituições de ensino e comunidades, a fim de garantir a conscientização à preservação do meio ambiente e sustentabilidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/educador-ambiental/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1101, 'Educador Social', 'Ministra oficinas e atividades pedagógicas voltadas ao desenvolvimento social com crianças e adolescentes, e monitora o bem estar dos educandos que participam das atividades. Elabora planejamento e relatórios pedagógicos e instrumentos avaliativos. Conduz reuniões com as famílias, a fim de compreender o universo das crianças e adolescentes, articulando e pensando em práticas para contribuir com o atendimento da demanda gerada pelas instituições sociais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/educador-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1102, 'Eletricista', 'Realiza manutenção preventiva e corretiva, instalação de quadros de distribuição de força e analisa consumo de energia, ligação e desligamento de aparelhos elétricos e eletrônicos. Identifica defeitos elétricos para reparar ou substituir componentes, ajustando peças e simulando o funcionamento dos equipamentos.  
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1103, 'Eletricista de Ar Condicionado', 'Realiza manutenção preventiva e corretiva de equipamentos de ar condicionado, analisa as necessidades de troca e regulagem e aplica testes de funcionamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-de-ar-condicionado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1104, 'Eletricista de Empilhadeira', 'Faz manutenção preventiva e corretiva em empilhadeiras a gás e elétricas, reparos e troca de peças.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-de-empilhadeira/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1105, 'Eletricista de Força e Controle', 'Realiza manutenção preventiva, preditiva e corretiva, montagem e desmontagem de painéis elétricos industriais e máquinas. Realiza testes e condicionamento de equipamentos. Faz planilhamento de cabos, para desligamento e religamento de máquinas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-de-forca-e-controle/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1106, 'Eletricista de Instalações', 'Planeja serviços elétricos, instalação e distribuição de alta e baixa tensão. Monta e repara instalações elétricas e equipamentos auxiliares em residências, indústrias e comerciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-de-instalacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1107, 'Eletricista de Veículos', 'Realiza instalação e manutenção elétrica preventiva e corretiva em veículos, analisa as necessidades de troca e regulagem, monta sistemas e aplica testes de funcionamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-de-veiculos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1108, 'Eletricista Eletrônico', 'Realiza manutenção corretiva e preventiva dos equipamentos elétricos e eletrônicos da planta. Responde pela execução das Manutenções Corretivas e Preventivas das máquinas e equipamentos. Responde pelo correto controle das manutenções corretivas e pela correta passagem entre turnos. Opera e monitora os equipamentos da área de utilidades industriais. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-eletronico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1109, 'Eletricista Industrial', 'Realiza manutenção elétrica preventiva e corretiva em motores, máquinas, equipamentos e instalações industriais, analisa as necessidades de troca e regulagem de componentes e aplica testes de funcionamento. Interpreta desenhos elétricos para montagem de painéis de tensão e cabeamento estruturado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1110, 'Eletricista Montador', 'Faz ajustes, monta e regula painéis elétricos e eletrônicos em máquinas e equipamentos. Interpreta desenhos, esboços, especificações e outras informações. Executa o encaixe das peças e montagem das mesmas, instala e conecta fios elétricos no conjunto, faz testes no equipamento, reparar os defeitos e desmontar peças.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-montador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1112, 'Eletricista Predial', 'Realiza manutenção preventiva e corretiva quadro de força, comandos elétricos e demais equipamento de energia em prédios, analisa as necessidades de troca e regulagem de peças e aplica testes de funcionamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletricista-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1113, 'Eletromecânico', 'Faz instalações de elevadores, escadas rolantes, portas e portões automáticos. Organiza e executa serviços preparando locais para instalação dos equipamentos. Realiza manutenção preventiva e corretiva em sistemas e equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletromecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1114, 'Eletromecânico de Manutenção', 'Realiza manutenção corretiva e preventiva em equipamentos, máquinas, instrumentos e dispositivos de componentes elétricos e mecânicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletromecanico-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1115, 'Eletrotécnico', 'Automatiza equipamentos por meio de sensores e inversores, assim como pelos acionamentos pneumáticos e controladores de temperatura. Faz leitura, interpretação e execução de projetos elétricos de A.T e B.T. Conhece a manutenção, avaliação e tratamento de falhas em sistemas e realiza de estudos de melhoria. Faz manutenção preventiva e corretiva, atua na implantação de ferramentas integradas de Segurança, Meio Ambiente e Qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/eletrotecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1116, 'Embalador', 'Embala, etiqueta, confere e separa produtos. Faz contagem de produtos e insumos. Recebe pedidos do setor comercial, verifica códigos e especificações. Inspeciona produtos verificando seu aspecto físico. Identifica produtos conforme descrição e código.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/embalador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1117, 'Embriologista', 'Responsável pela aplicação de tecnologias relacionadas à reprodução humana assistida (inseminação artificial).
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1118, 'Emissor de CTRC', 'Emite CTRC (Conhecimento de Transporte Rodoviário de Cargas), acompanha embarque nacional e internacional, elabora manifesto de carga e preenche planilhas de controles.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/emissor-de-ctrc/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1119, 'Emissor de Passagens', 'Atende clientes na cotação, reserva, emissão e venda de passagens nacionais e internacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/emissor-de-passagens/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1120, 'Empacotador', 'Empacota, embala, ensaca e encaixota mercadorias. Presta apoio ao trabalho aos demais funcionários levando e trazendo produtos da loja, checando pesos, preços e códigos, levando e trazendo documentos, objetos. Faz atendimento direto aos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/empacotador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1121, 'Encadernador', 'Encaderna materiais impressos como livros, cadernos, revistas, entre outros, manualmente e à máquina, dobra e recorta as folhas e monta capas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encadernador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1122, 'Encanador', 'Opera projetos de instalações de tubulações além de definir traçados e dimensões das mesmas. Especifica, quantifica e inspeciona materiais. Realiza testes operacionais de pressão de fluidos e os testes de estanqueidade. Protege as instalações e faz as manutenções nos equipamentos e acessórios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encanador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1123, 'Encanador Industrial', 'Atua com atividades ligada à indústria de construção civil, eletricidade, gás e captação, purificação e distribuição de água. Instala válvulas, ralos, registros, torneiras e aparelhos sanitários. Faz testes de pressão para localizar vazamentos, repara e mantêm instalações emendando tubos, desobstruindo ralos, substitui tubos, registros, torneiras ou aparelhos sanitários.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1124, 'Encapsulador', 'Manipulação, pesagem de matérias primas sólidas, semi sólidas e liquidas, rotulagem, controle de estoque entre outras atividades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encapsulador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1125, 'Encarregado Administrativo', 'Lidera equipe nas atividades administrativas, acompanha processos, controla fluxo de trabalhos e elabora relatórios gerenciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-administrativo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1126, 'Encarregado Administrativo de Obras', 'Realiza a programação da sequência de montagem das obras, faz o controle e liberação de notas fiscais, solicita materiais e controla trabalho e deslocamento de funcionários para as obras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-administrativo-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1127, 'Encarregado Comercial', 'Participa do planejamento comercial, envolvendo a definição de propostas e políticas de preços e acompanha as vendas e indicadores do mercado, com objetivo de apresentar à gerência os resultados das metas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1128, 'Encarregado Contábil', 'Analisa, valida e apresenta os demonstrativos contábeis e de resultados, bem como sugere novas estratégias à diretoria. Desenvolve estudos e propõe alternativas para o aproveitamento de benefícios legais, com consequente redução de custos com impostos. Participa em estudos para desenvolvimento e aprimoramento de produtos e processos, contribuindo com informações de caráter econômico financeiro. Atende e presta informações à auditores, fiscais, fornecedores, instituições financeiras e clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1129, 'Encarregado de Açougue', 'Lidera equipe de balconistas e açougueiros, elabora escala de folgas e horários, aplica treinamentos e verifica o corte e pesagem de carnes. Controla reposição de mercadorias, higiene e limpeza de câmaras frias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-acougue/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1130, 'Encarregado de Almoxarifado', 'Supervisiona equipe e rotina de separação, estocagem e armazenamento de produtos. Anota as informações de chegada, saída, armazenagem e devolução do produto ou mercadoria no sistema.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-almoxarifado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1131, 'Encarregado de Assistência Técnica', 'Realiza a implementação de novos projetos. Aumenta substancialmente o volume de negócios, através da venda de peças de reposição e serviços de assistência técnica e manutenção, através da retomada e reativação de clientes inativos, fidelização de clientes ativos e principalmente incorporação de novos clientes. Acompanha as atividades de instalação, reparo e troca de peças de equipamentos ou máquinas mecânicas, elétricas e eletrônicas. Elabora relatórios técnicos sobre níveis de serviços, a fim de identificar oportunidades de melhorias e redução de custos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-assistencia-tecnica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1132, 'Encarregado de Caldeiraria', 'Lidera a equipe de caldeiraria, verifica a fabricação dos produtos, reparos das caldeiras, tanques, reservatórios e outros recipientes de chapas de aço. Acompanha o recorte, modelagem e execução de trabalhos em barras perfiladas de materiais ferrosos e não ferrosos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-caldeiraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1133, 'Encarregado de Carpintaria', 'Dirige e controla os serviços de reparo e construção em madeira e colocação de portas, batentes, prateleiras, entre outros. Constrói, monta, desmonta e faz manutenção em divisórias de madeira.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-carpintaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1134, 'Encarregado de Cobrança', 'Analisa cadastro de clientes e empresas, realiza o faturamento, cobrança, comissão de agência. Trabalha com boletos de cobranças e nota fiscal e esclarece dúvidas de clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1135, 'Encarregado de Compras', 'Analisa necessidade de compra de materiais, pesquisa fontes alternativas de suprimentos, avalia e valida cotações, realiza contato com fornecedores e controla orçamentos do departamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1136, 'Encarregado de Corte', 'Orienta a equipe e executa atividades, manual ou à máquina, cortando tecidos na produção em série de peças de vestuários, utilizando máquinas e outros instrumentos apropriados, conforme moldes e especificações predefinidas para confeccionar ternos, terninhos, calças, camisas e outras peças de roupas similares.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1137, 'Encarregado de Elétrica', 'Analisa com o superior detalhes e instruções técnicas do projeto a ser executado. Orienta e acompanha a execução do cronograma. Interpreta projetos, relatórios, registros da construção e ordens de serviço, participa das instalações elétricas do canteiro de obras, definindo locais físicos conforme projeto compor equipes, distribui tarefas e acompanha a realização das mesmas, controla estoques de materiais, bem como resíduos e desperdícios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-eletrica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2018, 'Motoboy', 'Faz a coleta e entrega de malotes. Efetua pagamentos de boletos e depósito. Organiza documentos, traça rotas e verifica endereços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motoboy/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1138, 'Encarregado de Estacionamento', 'Lidera equipes, organiza as rotinas de trabalho, elabora relatórios e escala de folga. Responsável pela abertura e fechamento de caixa, cálculo de período de permanência e cobrança de clientes e pelo controle de entrada e saída de valores e contagem de numerários. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-estacionamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1139, 'Encarregado de Estamparia', 'Acompanha o processo de estamparia, prensas robotizadas e verifica a qualidade da peças. Faz o planejamento de produção, dublagem, tratamento têxtil, espalmagem e uso de ferramentas manuais e elétricas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-estamparia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1140, 'Encarregado de Estoque', 'Supervisiona a armazenagem dos produtos e materiais em almoxarifados, armazéns, silos e depósitos. Lidera equipe, faz conferência de entrada e saída de produtos, assegurando a integridade do estoque. Acompanha e responde pela equipe de inventários, confere notas fiscais, verifica estocagem e distribui produtos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1141, 'Encarregado de Expedição', 'Coordena a logística e todo o departamento de recebimento e expedição de mercadorias, controla o estoque. Faz vistoria do estoque, elabora romaneios de entrega e relatórios de atividades dos subordinados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-expedicao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1142, 'Encarregado de Faturamento', 'Emite, orienta e confere a emissão de notas fiscais eletrônicas de entrada ou saída, inclusive de importação. Lidera equipe, organiza e supervisiona o departamento, treina e participa da integração de novos colaboradores, resolve problemas de danos de cargas junto às transportadoras, lança e confere livros de entrada ou saída e confere tributos. Gerencia inventário físico de estoque de alguns produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-faturamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1143, 'Encarregado de Frota', 'Lidera equipe no controle da frota de transportes, inspeciona condições dos veículos para realizar manutenção preventiva ou corretiva, verifica documentação de motoristas e acompanha ordem de serviços e programação de rotas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-frota/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1144, 'Encarregado de Hidráulica', 'Atua com orientação de equipes de obras de instalações hidráulicas, realiza a inspeção, faz o acompanhamento e medição da obra, leitura e interpretação de projetos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-hidraulica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1145, 'Encarregado de Injeção Plástica', 'Lidera e orienta equipe na operação de máquinas injetoras na produção de plástico, controla entrada e saída de materiais e acompanha metas de fabricação e qualidade dos produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-injecao-plastica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1146, 'Encarregado de Instalações', 'Lidera equipe na instalação e manutenção civil, elétrica e hidráulica de prédios e indústrias e controla a aquisição de materiais, equipamentos e ferramentas para realização dos trabalhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-instalacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1147, 'Encarregado de Jardinagem', 'Supervisiona equipe na manutenção de jardins e distribui atividades, materiais e equipamentos de segurança.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-jardinagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1148, 'Encarregado de Limpeza', 'Lidera e orienta equipe de trabalho na realização das atividades de conservação e limpeza e acompanha o check list das rotinas. Controla a distribuição de materiais e tarefas e escalas de colaboradores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-limpeza/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1149, 'Encarregado de Logística', 'Supervisiona o adequado armazenamento dos produtos. Planeja e organiza a disposição das mercadorias estocadas, facilitando sua identificação, localização e manuseio. Elabora relatórios diários de produção e qualidade. Identifica desvios dos padrões nos processos produtivos e desenvolve ações corretivas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-logistica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1150, 'Encarregado de Loja', 'Acompanha as atividades de vendas na loja, com objetivo de atender com excelência os clientes e associados. Verifica a limpeza do estabelecimento e confere o estoque, recebimento, reposição e organização de produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1151, 'Encarregado de Manutenção', 'Programa e supervisiona os serviços de instalação, paradas de máquinas para manutenção preventiva e corretiva, visando minimizar o tempo não produtivo dos equipamentos. Elabora os projetos de desenvolvimento de dispositivos para máquinas e equipamentos. Seleciona e contrata serviços de terceiros para consertos em equipamentos, mediante avaliação técnica, pesquisa mercado e análise da relação custo x benefício do trabalho. Supervisiona o recebimento e inspeção de materiais para manutenção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1152, 'Encarregado de Manutenção Predial', 'Acompanha e orienta as atividades de manutenção corretiva e preventiva de equipamentos e infraestruturas prediais. Distribui serviços aos funcionários, elabora escalas de trabalho e acompanha ordem de serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1153, 'Encarregado de Marcenaria', 'Confere a qualidade e acabamento de peças e produtos, faz leitura de desenho técnico, contribui para organização e limpeza das ferramentas e local de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-marcenaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1154, 'Encarregado de Mecânica', 'Lidera equipe e distribui tarefas diárias, planeja e controla os serviços de instalação e manutenção das máquinas e equipamentos. Realiza leitura e interpretação de desenhos e utiliza instrumentos técnicos de medidas de precisão.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-mecanica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1155, 'Encarregado de Montagem', 'Atua na supervisão de equipe de montagem, administra as metas e resultados de produção, controla a qualidade dos produtos e assegura a manutenção dos equipamentos. Elabora relatórios técnico e controle diário.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-montagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1156, 'Encarregado de Obras', 'Supervisiona colaboradores, leitura e execução de projetos, acompanha cronograma e medições de obras e controla equipamentos, contratação de serviços e matéria-prima. Participa na compras de suprimentos e prospecção de fornecedores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1157, 'Encarregado de Oficina', 'Dirige e controla os serviços de consertos, ajustes, preparação, regulagens de máquinas, equipamentos e motores e manutenção dos veículos da empresa. Faz controle, movimento e estoque de materiais, peças e equipamentos, acessórios e ferramentas. Providencia para que as fichas de serviços executados sejam sempre atualizadas. Eventualmente, realiza trabalhos relativos a pequenas construções mecânicas de dispositivos e outros componentes de máquinas, motores e veículos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1257, 'Escriturário Hospitalar', 'Presta suporte administrativo, controla prontuários para faturamento, digita prescrições médicas, organiza documentos e solicita estoque de materiais e medicamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/escriturario-hospitalar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1158, 'Encarregado de Operações', 'Supervisiona a manutenção e recuperação dos equipamentos que retornaram das obras. Controla a equipe e informa as diretrizes das frentes de trabalho. Monitora a limpeza da área de manutenção. Garante os níveis adequados de estoque de peças e materiais necessários para a manutenção dos equipamentos. Supervisiona os serviços executados pela equipe, observando as operações a fim de assegurar o cumprimento da manutenção preventiva e corretiva. Documenta as ocorrências e soluções aplicadas, a fim de permitir a análise dos serviços realizados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-operacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1159, 'Encarregado de Padaria', 'Supervisiona a confecção de pães, elaborar rotina de trabalho, lidera a equipe de padeiros, controla escalas e demais atividades relacionadas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-padaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1160, 'Encarregado de Pátio', 'Supervisiona entrada e saída de veículos em pátios para carga e descarga de materiais, acompanha conferência de mercadorias e administra pessoal na aplicação de treinamentos e controle de escalas e férias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-patio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1161, 'Encarregado de Peixaria', 'Gerencia e controla as atividades de venda de peixe, crustáceos e mariscos. Coordena os contatos com clientes e escolhe, prepara e conserva os alimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-peixaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1162, 'Encarregado de Perecíveis', 'Verifica a exposição das mercadorias nas prateleiras, bem como reserva de espaço destinados aos produtos, verificando também a higiene e limpeza. Acompanha diariamente a evolução das vendas, margens, estoque, quebras entre outros indicadores. Contribui para a realização de inventários, de acordo com as necessidades específicas de cada grupo de produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-pereciveis/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1163, 'Encarregado de Prevenção de Perdas', 'Supervisiona equipe de monitoramento e prevenção, acompanha o cumprimento de normas e procedimentos de abordagem e analisa vulnerabilidade nos processos logísticos, a fim de garantir a segurança física e patrimonial do estabelecimento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-prevencao-de-perdas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1164, 'Encarregado de Produção', 'Acompanha o desempenho dos operadores e orienta quando necessário para manter o ritmo, qualidade e produtividade do trabalho. Analisa os relatórios diários de produção e identifica desvios dos padrões de processo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1165, 'Encarregado de Serralheria', 'Monitora processos de serralheria. Garante a programação da produção, dimensionando disponibilidade dos equipamentos e definindo pessoal em função do tipo, da especificação do serviço, das prioridades e da sequência da produção.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1166, 'Encarregado de Serviços Gerais', 'Acompanha os serviços de limpeza, verifica se as equipes estão dimensionadas para o volume de atividades, controlando assiduidade, pontualidade e reposição de quadros. Controla estoque de equipamentos, uniformes e insumos. Administra processos de controle de ponto, atestados, afastamentos entre outras atividades administrativas. Faz o controle dos funcionários terceirizados, controle de pragas, retirada do lixo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-servicos-gerais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1167, 'Encarregado de Solda', 'Supervisiona atividades de soldagem, de acordo com a programação de produção. Acompanha procedimentos de soldagem e inspeciona processos de fabricação para cumprimento das normas de qualidade, preservação do meio ambiente e segurança do trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-solda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1168, 'Encarregado de Sondagem', 'Supervisiona operações com sonda em poços de petróleo e na manutenção de equipamentos, fornecendo orientações e suporte técnico e providenciando os recursos necessários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-sondagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1169, 'Encarregado de Transportes', 'Supervisiona atividades e escalas de motoristas, inspeciona documentação, analisa condições de veículos, acompanha embarque e desembarque de cargas e participa de reuniões para alinhar estratégias e garantir agilidade nos processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-de-transportes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1170, 'Encarregado de Usinagem', 'Encarregado pelas equipes de trabalho de usinagem, nos métodos, processos produtivos e da qualidade. Monitora processos de usinagem, conformação e tratamento dos metais.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1171, 'Encarregado Financeiro', 'Atua com rotinas de contas a pagar e receber, crédito, cobranças, controle e variação de gastos, além de emitir e acompanhar notas fiscais e cheques, supervisionando a distribuição das atividades para os colaboradores envolvidos, definição de metas e cronogramas. Controla o faturamento e recursos financeiros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-financeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1172, 'Encarregado Industrial', 'Estabelece uma sistemática de planejamento, operação e controle da produção. Elabora quadros de produtividade e faz cronograma de execução, assessora a gerência e diretoria financeira na elaboração de projetos. Realiza estudos e simulações do seu departamento, faz cálculos de capacidade produtiva.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1173, 'Encarregado Operacional', 'Atua na supervisão dos recursos materiais, financeiros, contratos, projetos e operações logísticas, a fim de reduzir custos e para o crescimento contínuo da organização. Auxilia no planejamento estratégico, identificando riscos no fluxo de trabalho e nos processos de produção, controlando a cadeia de fornecimento, estoque, fabricação e demais atividades, com foco na otimização da rotina. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/encarregado-operacional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1174, 'Enfermeiro', 'Atua na promoção e recuperação de pacientes e na prevenção de doenças, administra medicamentos e prescrição de curativos. Orienta, treina e comanda equipes de técnicos e auxiliares de enfermagem no atendimento. Participa no planejamento e avaliação de programas de saúde.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1175, 'Enfermeiro Auditor', 'Conferência de processos, análise e autorização de uso de materiais hospitalares, procedimentos efetuados, medicamentos, controle do hospital e reembolso por parte do SUS e das operadoras de saúde. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-auditor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1176, 'Enfermeiro CCIH', 'Identifica casos de infecção hospitalar, implementa e avalia programas de controle. Elabora normas de padronização e capacita colaboradores da instituição para prevenção. Monitora o uso de antimicrobianos, germicidas e materiais médico-hospitalares. Desenvolve indicadores, notificação de suspeita e agravos de doenças e contribui com medidas para que não ocorra disseminação de micro-organismos no ambiente hospitalar.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-ccih/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1236, 'Engenheiro Eletroeletrônico', 'Desenvolve projeto de equipamentos e sistemas eletroeletrônicos, preparando especificações, desenhos, técnicas de execução, recursos necessários e outros requisitos, para possibilitar a construção, montagem, funcionamento e manutenção dentro de padrões técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-eletroeletronico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1177, 'Enfermeiro Chefe', 'Distribui e orienta as atividades para a equipe, avalia os cuidados prestados aos pacientes, coordena o atendimento em situações de emergência. Presta assistência e auxilia a equipe de enfermagem nos cuidados especiais. Orienta e avalia a equipe na utilização de equipamentos, materiais permanentes, roupas, solicitando manutenção e reposição quando necessário. Atende ao público e acompanhantes. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-chefe/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1178, 'Enfermeiro CME', 'Responsável pelo armazenamento de material estéril, para posterior distribuição aos diversos setores do hospital / clínica. 
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1179, 'Enfermeiro de Centro Cirúrgico', 'Acompanha desde a admissão de pacientes, acompanhamento e recuperação após cirurgias, garante insumos necessários para o procedimento, ministra a medicação, controla psicotrópicos e pedidos de medicamentos e mantém atualizados os registros em prontuários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-de-centro-cirurgico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1180, 'Enfermeiro de Educação Continuada', 'Planeja e acompanha reciclagens da equipe de enfermagem, em grupos ou individualmente, sempre que mostrarem dificuldades técnicas por qualquer colaborador da enfermagem ou por novas regras, normas ou procedimentos. Coordena o calendário de treinamentos, visitas, estágios e encontros científicos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-de-educacao-continuada/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1181, 'Enfermeiro de UTI', 'Faz exame físico, executa tratamento, aconselhando e ensinando a manutenção da saúde e orientando os pacientes para uma continuidade do tratamento. Cuida do paciente nas diferentes situações críticas dentro da UTI.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-de-uti/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1182, 'Enfermeiro do Trabalho', 'Realiza atendimento ambulatorial em empresas, estuda condições de segurança e periculosidade, acompanha programas de promoção à saúde dos funcionários e analisa ações de prevenção de acidentes e doenças profissionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-do-trabalho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1183, 'Enfermeiro Estomaterapeuta', 'Atende pacientes para tratamento, adaptação e reabilitação de pessoas com feridas agudas e crônicas, estomias e incontinências e presta orientação aos familiares sobre práticas e cuidados necessários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-estomaterapeuta/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1184, 'Enfermeiro Nefrologista', 'Trabalha em hospitais e clínicas com atendimento aos pacientes com problemas renais, ou em clínicas de hemodiálise, na assistência de enfermagem direta aos pacientes renais crônicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-nefrologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1185, 'Enfermeiro Obstetra', 'Presta assistência às gestantes e lactantes no acompanhamento de pré-natal e pós-parto e realiza parto de baixo risco.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-obstetra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1186, 'Enfermeiro Responsável Técnico', 'Responsável por organizar e praticar a equipe de enfermagem. Informa procedimentos, elabora escalas, procedimento, relatórios e planilhas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-responsavel-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1187, 'Enfermeiro SCIH', 'Trabalha com serviço de infecção hospitalar, faz os controles e orientações aos colaboradores de enfermagem, visando o controle e a prevenção de infecções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-scih/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1188, 'Enfermeiro Visitador', 'Cuida de pacientes em sua residência, dando continuidade ao tratamento hospitalar. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfermeiro-visitador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1189, 'Enfestador', 'Opera máquina enfestadeira na sobreposição e corte de tecidos, definindo o tipo de material, equipamentos disponíveis e necessidades de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/enfestador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1190, 'Engenheiro Aeronáutico', 'Realiza projetos, construção e manutenção de aeronaves, inspeciona estruturas e equipamentos, testa navegabilidade e projeta sistemas de controle e de simuladores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-aeronautico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1191, 'Engenheiro Agrícola', 'Projeta, implanta e administra técnicas e equipamentos necessários à produção agrícola. Planeja métodos de armazenagem e constrói silos, armazéns e estufas. Constrói açudes, barragens, sistemas de irrigação e de drenagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-agricola/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1192, 'Engenheiro Agrimensor', 'Gerencia atividades de agrimensura e cartografia no levantamento e cálculo de pontos topográficos para realização de obras, acompanha monitoramento geotécnico e define características e condições do trabalho a ser realizado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-agrimensor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1193, 'Engenheiro Agrônomo', 'Planeja o preparo e cultivo do solo, acompanha o controle de pragas e estuda os melhores procedimentos de adubação e irrigação. Cuida da alimentação e reprodução de animais, bem como do manejo dos produtos depois do abate.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-agronomo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1194, 'Engenheiro Ambiental', 'Desenvolve soluções para problemas ambientais como poluição do solo, água e ar, descarte de resíduos sólidos, entre outros. Acompanha exploração dos recursos naturais, avalia impactos ambientais e da produção industrial e elabora projetos de sistemas hidráulicos e recuperação de áreas degradadas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-ambiental/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1195, 'Engenheiro Automobilístico', 'Desenvolve projetos para fabricação, manutenção e montagem de equipamentos e sistemas automotivos e realiza teste de segurança, desempenho e ergonomia de veículos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-automobilistico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1196, 'Engenheiro Biomédico', 'Cuida da concepção de equipamentos médicos, biomédicos e odontológicos, voltados para diagnóstico ou tratamento terapêutico. Projeta a estrutura, desenvolve e monta os equipamentos e faz a sua manutenção corretiva e preventiva.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-biomedico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1197, 'Engenheiro Calculista', 'Realiza cálculo dos elementos estruturais das construções, como as reações, os esforços e as deformações. Calcula e dimensiona as peças necessárias para suportar esforços em conformidade com as normas técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-calculista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1198, 'Engenheiro Cartógrafo', 'Analisa, armazena e interpreta dados e imagem de aspectos geográficos para elaboração de mapas cartográficos e cartas topográficas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-cartografo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1199, 'Engenheiro Civil', 'Gerencia e desenvolve projetos de construções e reforma de prédios, rodovias, ferrovias, viadutos, portos entre outros. Acompanha cronograma físico e financeiro da obra, elabora orçamentos e realiza levantamento quantitativo de equipamentos, materiais e serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-civil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1256, 'Escriturário', 'Presta atendimento aos clientes, confere documentos e relatórios, realiza lançamentos em contas, acompanha toda movimentação financeira e analisa registros patrimoniais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/escriturario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1200, 'Engenheiro Clínico', 'Trabalha em Hospitais, clínicas, ou empresas no setor de saúde sendo responsável por todo o ciclo de vida da tecnologia e  manutenção dos equipamentos médico-hospitalares. Participa do processo de aquisição, recebimento, testes de aceitação, treinamento, manutenção, alienação e todos os assuntos referentes aos equipamentos. Gerencia tecnologias de saúde, durante todo o ciclo de vida.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-clinico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1201, 'Engenheiro da Qualidade', 'Audita, corrige e elabora documentações de qualidade. Planeja e supervisiona as atividades de ensaios, inspeção e assistência técnica. Monitora a implementação das ações corretivas para solução das irregularidades constatadas nos processos de auditoria e realizar as avaliações técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-da-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1202, 'Engenheiro de Alimentos', 'Cuida das etapas de preparo e conservação de alimentos de origem animal e vegetal. Seleciona a matéria-prima, como leite, carnes, peixes, legumes e frutas, e define a melhor forma de armazenagem, acondicionamento e preservação dos produtos, projetando embalagens. 
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1203, 'Engenheiro de Aplicação', 'Planeja e desenvolve documento de projetos e produtos, com dados sobre detalhes técnicos, especificações, prazos e estimativa de custos, avalia processos e levanta informações para interpretar as necessidades de negócios do cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-aplicacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1204, 'Engenheiro de Ar Condicionado', 'Desenvolve projetos de sistemas de condicionamento de ar, realiza diagnóstico de problemas e acompanha os serviços de instalação, inspeção e manutenção de equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-ar-condicionado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1205, 'Engenheiro de Assistência Técnica', 'Presta assistência técnica de serviços, máquinas e equipamentos da empresa. Analisa solicitações de garantia, valida testes de desempenho dos produtos, levanta informação do mercado e analisa dados. Realiza instalação e manutenção preventiva e corretiva. Planeja e executa treinamentos técnicos para os prestadores de serviços técnicos terceirizados. Responde pelos indicadores do setor, pelas atividades de controle e organização pertencentes à rotina de assistência técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-assistencia-tecnica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1206, 'Engenheiro de Automação Industrial', 'Projeta e acompanha a execução de sistemas de comando, automação e controle de motores elétricos de média ou baixa tensão e demais componentes existentes em plantas industriais, redes, subestações e instalações elétricas em geral de força e comando. Elabora estimativa de custos de materiais e de outros fatores relacionados com os processos de fabricação, instalação, funcionamento e manutenção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-automacao-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1207, 'Engenheiro de Campo', 'Trabalha com diversas áreas ligadas a engenharia como: Mecânica, Civil, Produção, Elétrica entre outras, com soluções de problemas e suporte para o departamento de projetos, produtos e processos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1208, 'Engenheiro de Compras', 'Realiza interface entre empresa e fornecedores de máquinas e equipamentos industriais e suprimentos técnicos, desenvolve e implanta estratégias de compras e identifica novos mercados para atender as necessidades da organização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1209, 'Engenheiro de Computação', 'Desenvolve projetos e constrói componentes de software e hardware, implanta equipamentos de rede, comunicação e automação e identifica novas tecnologias para aperfeiçoar os sistemas e computadores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-computacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1210, 'Engenheiro de Controle e Automação', 'Desenvolve projetos de sistemas de automação predial, industrial e comercial, realiza a adaptação de softwares aos processos e cria ferramentas experimentais e computacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-controle-e-automacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1211, 'Engenheiro de Custos', 'Faz análise, composição e controle de custos de projetos (obras). Elabora e controla os orçamentos, planeja apropriação de custos diretos e indiretos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-custos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1212, 'Engenheiro de Instalações', 'Acompanha a elaboração dos projetos e elabora o cronograma físico e financeiro. Desenvolve os processos relacionados à montagem e instalação de sistemas. Coordena a execução e a fiscalização da mão de obra. Faz o levantamento de materiais e equipamentos, requisita materiais e elabora relatórios técnicos da área. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-instalacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1213, 'Engenheiro de Instrumentação', 'Desenvolve sistemas e instrumentos de medição e controle dos processos para garantir a confiabilidade e segurança das plantas industriais, otimização da produção e redução dos custos operacionais. Coordena a implementação de novos dispositivos, realiza ensaios e elabora documentação técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-instrumentacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1214, 'Engenheiro de Manufatura', 'Planeja os processos de fabricação, administra suprimentos, tecnologias e recursos financeiros, realiza cálculo e avaliação de materiais e ferramentas, identifica melhores metodologias de produção e desenvolve novas soluções técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-manufatura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1215, 'Engenheiro de Manutenção', 'Elabora e executa projetos de manutenção preventiva e corretiva, avalia o desempenho de equipamentos e identifica necessidades de trocas de peças. Prepara planos de manutenção, métodos de trabalho e recursos necessários, de acordo com especificações técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1216, 'Engenheiro de Manutenção Predial', 'Coordena e supervisiona as atividades da equipe de manutenção. Faz o planejamento de manutenção preventiva e corretiva dos sistemas que envolvem o setor de manutenção (hidráulico, elétrico, telefonia). Realiza o controle de eficiência dos equipamentos e custos de manutenção. Garante as operações de manutenção da unidade em perfeitas condições e de acordo com as normas técnicas. Busca alternativas para melhoria contínua em disponibilidade e custos de manutenção. Analisa continuamente o fluxo de trabalho de manutenção de modo a propor ajuste, correções e melhorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1217, 'Engenheiro de Materiais', 'Pesquisa e cria materiais, como resinas, plásticos, cerâmicas e ligas metálicas. Melhora suas propriedades sugere novas combinações, que resultam em produtos novos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1218, 'Engenheiro de Minas', 'Supervisiona atividades minerárias de produção, como: perfuração, desmonte, escavação, carregamento, transporte, classificação, lavagem, concentração, secagem e embalagem de embarque. Inspeciona áreas de interesse da mineração, avalia riscos da atividade e gerencia recursos humanos, financeiros e materiais. Planeja e executa essas atividades e outras como a pesquisa mineral e prospecção. Projetam empreendimentos minerários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-minas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1219, 'Engenheiro de Pesca', 'Trabalha com cultivo, captura, industrialização e venda de organismos aquáticos. Estuda e aplica métodos e tecnologias para localizar, capturar, beneficiar e conservar peixes, crustáceos e frutos do mar.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-pesca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1220, 'Engenheiro de Petróleo', 'Realiza manutenção dos níveis de produção de petróleo e gás nos poços, elabora projetos de exploração e expansão de jazidas para eliminar as perdas de produtividade e acompanha o transporte de petróleo e derivados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-petroleo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1221, 'Engenheiro de Planejamento', 'Faz a leitura e interpretação de projetos. Mantém o acompanhamento e gestão da rotina do planejamento. Executa cronograma físico, de suprimento e de projeto. Faz atualização da produtividade e da meta de produção. Verifica o cronograma, com a emissão de relatório semanal com toda a informação consolidada, com o gráfico e com a foto / programação futura.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-planejamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1222, 'Engenheiro de Processos', 'Estuda as atividades de produção e elabora proposta de melhorias em processos, equipamentos, dispositivos e métodos, correção de tempos de fabricação de componentes de produtos e do produto final. Pesquisa melhor aproveitamento da mão de obra e das matérias-primas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1223, 'Engenheiro de Produção', 'Atua na gestão de projetos e otimização de processos e sistemas, envolvendo recursos humanos, materiais e equipamentos, acompanhando a produção do início ao fim na entrega do produto ou serviço, a fim de alcançar melhores resultados com menores custos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1224, 'Engenheiro de Produto', 'Desenvolve estudos de concorrentes e de necessidades do mercado, com objetivo de identificar a criação de novos produtos. Elabora e implementa estratégias para comercialização de produtos, analisando valores de mercado da concorrência para precificação. Realiza visitas aos clientes finais para análise de vantagem dos locais de aplicação do produto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-produto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1225, 'Engenheiro de Projetos', 'Elabora e executa projetos de engenharia, preparando especificações, desenhos, técnicas de execução e recursos necessários para possibilitar a construção, montagem, funcionamento, manutenção e reparo de instalações e equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-projetos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1226, 'Engenheiro de Propostas', 'Elabora propostas técnicas e orçamentárias de produtos e serviços, envolvendo análise de precificações, viabilidade e riscos e formação de preços, apresentando ao cliente soluções para os negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-propostas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1227, 'Engenheiro de Segurança do Trabalho', 'Coordena, elabora e orienta atividades de segurança do trabalho, a fim de adequar às normas legais e realizar a prevenção de riscos para preservação da saúde e integridade dos indivíduos. Desenvolve programas de prevenção de acidentes, vistoria instalações e emite laudos técnicos para identificar e corrigir riscos no ambiente de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-seguranca-do-trabalho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1228, 'Engenheiro de Serviços', 'Realiza as vistorias para elaboração de propostas técnicas voltadas a prospecções de novos negócios. Efetua o desenvolvimento e acompanhamento de parcerias estratégicas para execução dos serviços em obras de infraestrutura e em ambientes seguros. Mantém o relacionamento direto com clientes e fornecedores na negociação e planejamento de obras de infraestrutura. Elabora os cronogramas, definições de orçamento, planejamento estratégico e operacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-servicos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1229, 'Engenheiro de Sistemas', 'Atua com processos de desenvolvimento estruturado que se estende do conceito ao projeto do sistema, análise recursiva da interligação e integração de subsistemas, com verificação e validação de requisitos de sistemas. Define requisitos de sistemas. Realiza gestão de inventário de hardware e software. Implanta, automatiza e auditar processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-sistemas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1230, 'Engenheiro de Software', 'Desenvolve softwares e aplicativos, realiza a análise de requisitos, define arquitetura de produtos, testes unitários e funcionais. Aplica as melhores práticas para gerenciamento do ambiente de dados e sua integração.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-software/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1231, 'Engenheiro de Suprimentos', 'Melhora os processos de suprimentos na empresas a partir de abordagens sistêmicas e integradas dos mesmos. Usa técnicas apropriadas para o mapeamento e melhoria dos processos, técnicas de gerenciamento de almoxarifados e o uso racional da Tecnologia da Informação nos processos de suprimentos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1232, 'Engenheiro de Telecomunicações', 'Planeja, cria, e constrói aparelhos e equipamentos utilizados nas telecomunicações e dá manutenção aos sistemas e redes implantados.  Responsável pelos cabeamentos aéreos e subterrâneos, satélites artificiais, centrais de transmissão, captação, codificação e retransmissão dos sinais que interligam o planeta.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-telecomunicacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1233, 'Engenheiro de Testes', 'Participa de processos de engenharia na introdução de novos produtos, realiza testes funcionais, validação e controle de qualidade, a fim de verificar performance, detectar falhas e implementar melhorias contínuas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-testes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1234, 'Engenheiro de Vendas', 'Desenvolve e apresenta soluções de produtos e serviços, de acordo com a necessidade do cliente e avaliação do mercado e identifica novas oportunidades de negócio. Elabora proposta técnicas comerciais e realiza atendimento na pós-venda e presta assistência técnica aos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1235, 'Engenheiro Eletricista', 'Elabora, executa e orienta projetos de engenharia, nas áreas de geração, transmissão e distribuição de energia elétrica. Desenvolve especificações, desenhos e outros requisitos para possibilitar a construção, montagem, funcionamento e manutenção dentro de padrões técnicos adequados.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1237, 'Engenheiro Eletrônico', 'Projeta, desenvolve e gerencia novos sistemas eletroeletrônicos ou equipamentos para ser usados na indústria.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1238, 'Engenheiro Ferroviário', 'Gerencia operações e manutenção de ferrovias, planeja linhas férreas, acompanha construção de pontes e fiscaliza obras de infraestrutura.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-ferroviario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1239, 'Engenheiro Florestal', 'Estuda e administra parques e reservas florestais, gerencia processos de exploração que preserve os recursos naturais e recupera áreas degradadas. Realiza atividades em educação ambiental e ecoturismo, incentiva as ações de preservação da biodiversidade da fauna e da flora. Elabora e promove projetos de reflorestamento das espécies arbóreas para aumentar sua produtividade. Pesquisa sementes e o melhoramento genético da vegetação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-florestal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1240, 'Engenheiro Geotécnico', 'Realiza visitas em campo, estuda solos e rochas e faz levantamento técnico de projetos para fundações, escavações, estruturas de contenções, entre outros, interpreta ensaios geotécnicos e desenvolve soluções para terrenos acidentados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-geotecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1241, 'Engenheiro Hidráulico', 'Executa obras de abastecimento e tratamento de água, sistemas de esgoto etc. Planeja o sistema de abastecimento de água e o de esgoto dos prédios, determinando os materiais mais adequados, como encanamentos e tubulações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-hidraulico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1242, 'Engenheiro Hídrico', 'Planeja a exploração, transporte, tratamento e gestão dos recursos hídricos, orienta quanto a utilização de águas de bacias hidrográficas e elabora redes de água e esgoto, irrigação e drenagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-hidrico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1243, 'Engenheiro Industrial', 'Supervisiona o processo de produção para garantir a qualidade do produto.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1244, 'Engenheiro Mecânico', 'Elabora, orienta e fiscaliza projetos de engenharia mecânica, desenvolve especificações, desenhos, recursos necessários e outros requisitos. Realiza construção e manutenção de máquinas e equipamentos, participa de inspeções de fabricação e acompanha montagem e testes em campo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1245, 'Engenheiro Mecatrônico', 'Projeta, implanta, testa o funcionamento e aperfeiçoa sistemas automatizados. Realiza manutenção, elabora documentação técnica e assessora a comercialização de produtos. Coordena equipe de trabalho e ministra treinamentos na área de Mecatrônica.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1246, 'Engenheiro Metalúrgico', 'Desenvolve ligas metálicas que contenham ferro, acompanha as fases de fabricação em usinas siderúrgicas, do planejamento ao controle de qualidade da produção.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1247, 'Engenheiro Naval', 'Coordena a construção de embarcações (navios, barcos e lanchas) e de sistemas flutuantes em geral, incluindo plataformas de produção de petróleo. Gerencia serviços de manutenção e conservação de cascos, motores, máquinas e sistemas de bordo.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1248, 'Engenheiro Orçamentista', 'Estuda viabilidade econômica da obra.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1249, 'Engenheiro Químico', 'Cria e aprimora produtos na indústria química, petroquímica e de alimentos e analisa sua viabilidade técnica e econômica. Busca melhorias no processo de fabricação ou beneficiamento de produtos, introduzindo novas tecnologias e adaptando as que estão em operação.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1250, 'Engenheiro Rodoviário', 'Analisa projetos rodoviários, máquinas e equipamentos utilizados na construção, discute o conteúdo contratual, especificações técnicas, dados comerciais e aspectos técnicos da construção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-rodoviario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1251, 'Engenheiro Sanitarista', 'Desenvolve e implanta projetos para construção de sistemas de abastecimento de água, esgoto, drenagem e irrigação pluvial e limpeza urbana e de resíduos, realiza inspeções e vistorias sanitárias e elabora regulamentos. Coordena atividades de monitorização e vigilância dos fatores ambientais com incidência na saúde humana. Participa do planejamento e avaliação de programas ambientais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/engenheiro-sanitarista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1252, 'Engenheiro Têxtil', 'Desenvolve e acompanha processos da indústria têxtil, como produção de fibras e fios para fabricação de novos tecidos, projeta e implanta novas tecnologias, instalações e equipamentos relacionadas às etapas mecânicas e químicas. Estuda viabilidade econômica e materiais a serem utilizados, bem como seus custos de matéria-prima e produto final.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1253, 'Entregador', 'Prepara cargas e descargas de mercadorias, movimenta materiais nos caminhões e realiza a entrega dos pedidos aos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/entregador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1254, 'Ergonomista', 'Contribui para o planejamento, projeto e a avaliação de tarefas, postos de trabalho, produtos, ambientes e sistemas de modo a torná-los compatíveis com as necessidades, habilidades e limitações das pessoas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ergonomista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1255, 'Escrevente', 'Atua com atendimento ao público, rotinas cartoriais, recebe documentos, protocolos entre outras atividades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/escrevente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1460, 'Estágio na área de Estoque', 'Atende fornecedores e clientes, fornecendo e recebendo informações sobre produtos e serviços. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-na-area-de-estoque/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1258, 'Especialista Contábil', 'Elabora o cronograma mensal e anual de fechamento contábil. Atua na rotina do fechamento contábil zelando pelo cumprimento dos prazos estabelecidos. Controla, calcula, classifica e lança a apropriação das contas contábeis. Realiza a montagem completa das peças contábeis à serem enviadas aos órgãos reguladores. Acompanha os relatórios de auditoria (interna e externa), supervisionando as ações de respostas do auditado para os pontos de recomendações, assim como efetuar análises e registros contábeis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1259, 'Especialista de Produto', 'Presta consultoria na análise e introdução de produtos, realiza levantamento de demandas, estuda mercado e concorrência, identifica novas oportunidades de negócios, define posicionamento estratégico e planeja ciclo de vida, a fim de acompanhar os resultados para alcance das metas e objetivos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-de-produto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1260, 'Especialista de Projetos', 'Atua com gestão de projetos de instalação, implementação, homologação, desenvolvimento e testes integrados de softwares. Faz a abertura, planejamento, execução e encerramento do projeto, controle de cronograma, escopo e financeiro do projeto. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-de-projetos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1261, 'Especialista de Suprimentos', 'Faz a negociação de contratos com diversos fornecedores, realiza estudos de mercado e de custo total de propriedade. Gerencia o forecast de aquisição de equipamentos junto ao cliente interno e fornecedores. Atua como facilitador em reuniões de melhoria de performance entre fornecedores e clientes internos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-de-suprimentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1262, 'Especialista em Compras', 'Planeja, negocia e controla todas as atividades de aquisição. Realiza os processos de importação e exportação, com acompanhamento direto em todas as etapas internas e externas. Faz contato com o despacho aduaneiro e agente de carga e análise de códigos de NCM, custo de importação, impostos, logística, contato e negociação de fretes com as companhias aéreas e marítimas. Desenvolve novos fornecedores e fontes alternativas de insumos, objetivando a qualidade e o custo dos produtos, condições de pagamento e prazo de entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-compras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1263, 'Especialista em Marketing', 'Planeja e implementa ações de marketing e acompanha os indicadores de performance dos produtos. Identifica oportunidades de melhoria no planejamento da área, através dos resultados das análises e estudos realizados. Monitora as ações da concorrência, através de benchmarking, relatórios e matérias publicadas na imprensa. Define o público a ser trabalhado nas ações segmentadas realizadas, através da análise de perfil dos clientes, mensuração das ações anteriores, metas estipuladas para o período e controle do orçamento da área. Elabora apresentações e relatórios gerenciais. Realiza a gestão e correta utilização da marca de forma padronizada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1264, 'Especialista em Marketing Digital', 'Identifica as necessidades e oportunidades no mercado digital, estuda o comportamento dos consumidores e suas influências, concorrentes e tendências por meio de ferramentas de web analytics. Seleciona mercados-alvos e/ou segmentos, gerencia métricas e elabora planejamento estratégico para aprimorar os canais digitais da empresa, como site, aplicativos e redes sociais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-marketing-digital/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1265, 'Especialista em Mídia', 'Atua na gestão de campanhas on-line em google Adwords, Facebook ads, portais e outros, além de atuar no planejamento e gestão de planos de mídias digitais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-midia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1266, 'Especialista em Recrutamento e Seleção', 'Define e acompanha os processos de recrutamento e seleção, estabelece e implanta métricas e realiza mapeamento de perfil de profissionais. Monitora clima organizacional, estrutura a integração de novos funcionários e elabora relatórios gerenciais para subsidiar informações nas decisões e políticas da área de Recursos Humanos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-recrutamento-e-selecao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1267, 'Especialista em Recursos Humanos', 'Planeja as atividades da área de recursos humanos, nos processos de recrutamento e seleção, treinamento e desenvolvimento e participa na elaboração de políticas, normas e procedimentos específicos para as rotinas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-recursos-humanos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1268, 'Especialista em Segurança da Informação', 'Administra sistemas de informação e disponibilidade dos recursos, identifica vulnerabilidades em servidores, sistemas, aplicações e networking, a fim de garantir maior segurança e integridade dos dados da empresa. Monitora a segurança e implementa processos e políticas de proteção.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1269, 'Especialista em Telecomunicações', 'Planeja, implanta e cuida de projetos de diferentes sistemas de telecomunicações, como telefonia, transmissão, comunicação de dados, serviços celulares, TV a cabo, TV digital, serviços telemáticos e outros. Desenvolve e implanta sistemas de transmissão de sinais digitais e analógicos por meio de satélites.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-telecomunicacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1270, 'Especialista em Tesouraria', 'Elabora estudo de viabilidade econômica de investimentos. Faz análise e acompanhamento da realização de investimentos e elaboração de relatório.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-tesouraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1271, 'Especialista em Treinamento e Desenvolvimento', 'Analisa as necessidades de treinamento técnico e comportamental, elabora conteúdos e metodologias de aprendizagem, desenvolve processos de avaliação de desempenho e clima corporativo e revisa as políticas de desenvolvimento organizacional. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/especialista-em-treinamento-e-desenvolvimento/');
