INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1555, 'Estágio Técnico em Farmácia', 'Realiza análises laboratoriais para diagnóstico de doenças, exames químicos e microbiológicos. Ajuda na produção e controle de qualidade de cosméticos, produtos de higiene pessoal e biológicos naturais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-farmacia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1556, 'Estágio Técnico em Gestão Empresarial', 'Ajuda na criação de medidas preventivas e corretivas para proteger vidas, o patrimônio e restaurar as atividades normais da empresa. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-gestao-empresarial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1557, 'Estágio Técnico em Informática', 'Ajuda na instalação de componentes da parte interna do computador, dentre eles: placas, pentes de memória, drives, discos rígidos, conexão de monitor, teclado, mouse, equipamentos periféricos, impressoras, scanners, no-breaks e estabilizadores. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-informatica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1558, 'Estágio Técnico em Logística', 'Ajuda nas ações de planejamento, movimentações, armazenagens, sistema da empresa e contrato de transportes. Atua nas rotinas dos setores de Logística, Estoque, Expedição e PCP, envolvendo a distribuição de produtos no mercado, cumprimento de normas e requerimento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-logistica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (764, 'Colchoeiro', 'Opera máquina de acabamento de colchão, revisa artefatos e verifica falhas na produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/colchoeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1559, 'Estágio Técnico em Mecânica', 'Ajuda na construção e manutenção corretiva e preventiva de máquinas e equipamentos e participa da criação de protótipos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1560, 'Estágio Técnico em Mecatrônica', 'Ajuda nas pesquisas, elaborações e confecções de componentes mecânicos de precisão, dá suporte para fabricação, montagem, manutenção, instalações e operações de equipamentos mecatrônicos, incluindo robôs industriais, realizando análise, experimentação, ensaio e divulgação técnica de máquinas, sistemas e processos automáticos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-mecatronica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1561, 'Estágio Técnico em Meio Ambiente', 'Ajuda na recuperação de áreas contaminadas, resíduos, implantação de áreas verdes e de sistema de monitoramento ambiental, desenvolvendo relatórios para acompanhamento de impactos, visando à proteção e conservação do patrimônio natural. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-meio-ambiente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1562, 'Estágio Técnico em Nutrição', 'Ajuda no planejamento de programas de alimentação e nutrição para a elaboração de cardápios, de acordo perfil e/ou tratamento do paciente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-nutricao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1563, 'Estágio Técnico em Plásticos', 'Ajuda no processamento de moagem de matéria-prima, acompanhando todas as misturas realizadas com substâncias químicas responsáveis pela cor, resistência, flexibilidade e outras características, verificando a qualidade dos produtos fabricados. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-plasticos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1564, 'Estágio Técnico em Prótese Dentária', 'Ajuda na construção de modelos odontológicos em gesso, confecção de próteses parciais e totais em resina, fixas e metaloplástico, assim como pela elaboração de aparelhos ortodônticos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-protese-dentaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1565, 'Estágio Técnico em Publicidade e Propaganda', 'Ajuda na elaboração e divulgação de campanhas e peças publicitárias, envolvendo ações de criação, produção, lançamento e exibição na mídia.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-publicidade-e-propaganda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1566, 'Estágio Técnico em Qualidade', 'Auxilia na análise de documentos e relatórios de qualidade, acompanha auditorias e procedimento de certificação, e participa na identificação de inconformidades e controle de ações corretivas em processos, produtos ou serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1567, 'Estágio Técnico em Química', 'Realiza pesquisas para criação e aperfeiçoamento de produtos, envolvendo a análise de matérias-primas e dos materiais fabricados. Auxilia as análises físico-químicas e coleta de amostras, realizando a identificação de reações, verificação e purificação de compostos químicos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-quimica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1568, 'Estágio Técnico em Secretariado', 'Prepara reuniões. elabora atas, organiza agendas, recebe telefonemas e visitas e cuida dos arquivos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-secretariado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1569, 'Estágio Técnico em Segurança do Trabalho', 'Ajuda na elaboração de política de saúde e segurança no trabalho (SST). Realiza estudos sobre controle de doenças, acidentes e qualidade de vida, pesquisando sobre o desenvolvimento de ações educativas na área de saúde e segurança do trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-seguranca-do-trabalho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1570, 'Estágio Técnico em Telecomunicações', 'Faz a manutenção de sistemas e equipamentos de telecomunicações, realizando análise de projetos, prestando consultoria e orientação técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-em-telecomunicacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1571, 'Estágio Técnico na área de Manutenção', 'Executa reparos em todas as funções elétricas, hidráulicas e mecânicas, atende as requisições de serviços, realizando conservações e manutenções preventivas e corretivas em máquinas e equipamentos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-na-area-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1834, 'Leiturista', 'Realiza a leitura e medição do consumo de água, gás e energia elétrica e inspeciona equipamentos para constatar irregularidades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/leiturista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1572, 'Estágio Técnico na área de Recursos Humanos', 'Ajuda na rotina de recrutamento e seleção, envolvendo a triagem e análise de currículos, contato com candidatos, realização de entrevistas, dinâmicas e encaminhamento de candidatos às várias etapas da seleção. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-na-area-de-recursos-humanos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1573, 'Estágio Técnico na área de Vendas', 'Atende clientes, realizando administração estratégica de vendas, demonstrações e comercialização de produtos e serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estagio-tecnico-na-area-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1574, 'Estampador', 'Atua com estampas em tecidos, impressão, realiza o preparo de tintas e materiais, regula máquinas e organizar o local.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estampador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1575, 'Estatístico', 'Planeja pesquisas, coletando, analisando e tratando dados para estudo de fenômenos naturais, econômicos ou sociais. Desenvolve metodologias e técnicas para pesquisa, realiza experimentos e constrói modelos e teorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estatistico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1576, 'Estatístico Trainee', 'Acompanha e desenvolve atividades na área de Estatística, envolvendo a obtenção e tratamento de informações, análise de dados e elaboração de cenários, a fim de capacitar para ocupação do cargo profissional planejado e aprimorar o conhecimento dos negócios, produtos ou serviços da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estatistico-trainee/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1577, 'Esteticista', 'Realiza procedimentos para cuidados corporais e faciais, como tratamentos estéticos, limpeza de pele, drenagem linfática, depilação, entre outros para benefício da beleza, saúde e bem-estar. Aplica e indica cosméticos de acordo com as necessidades ou preferências dos clientes. Prepara e supervisiona as condições adequadas do local a ser realizados os procedimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/esteticista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1578, 'Estilista', 'Desenvolve estilos de roupas e acessórios, elabora croquis, desenha coleções de acordo com a análise das tendências de comportamento e elabora desfiles para divulgação. Também atua no projeto e montagem de vitrines e estandes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estilista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1580, 'Estoquista', 'Faz recebimento, conferência e verificação de validades dos produtos. Controla avarias e perdas, organiza estoques, realiza balanços e inventários. Emite relatórios, verifica operações de entrada e saída e armazena mercadorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/estoquista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1581, 'Etiquetador', 'Realiza a separação, etiquetação e estoque de materiais, fixando etiquetas de acordo com padrão de qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/etiquetador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1582, 'Executivo de Contas', 'Administra e desenvolve contas estratégicas e identifica oportunidades de novos negócios por meio do levantamento de informações sobre o mercado. Analisa concorrência e tendências e condições do mercado para aceitação de produtos ou serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/executivo-de-contas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1583, 'Executivo de Vendas', 'Identifica e prospecta clientes em potencial para desenvolver novos negócios, a fim de atender suas necessidades e garantir a satisfação. Analisa concorrência e tendências e condições do mercado para aceitação de produtos e/ou serviços. Planeja estratégias de vendas e realiza o mapeamento de oportunidades para alcance das metas e objetivos da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/executivo-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1584, 'Extrusor', 'Prepara máquinas e moldes para fabricação dos produtos, controla documentos para parâmetros do processo produtivo. Auxilia o setor de qualidade, avaliando produtos conformes e não conformes. Atua de acordo com as normas e procedimentos técnicos de qualidade, segurança, higiene, saúde e preservação ambiental. Opera máquinas, planeja e executa serviços de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/extrusor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1585, 'Farmacêutico', 'Pesquisa e desenvolve processos para produção de medicamentos, alimentos e cosméticos, realiza análises físico-químicas e microbiológicas no controle da qualidade e acompanha o registro de produtos conforme as normas sanitárias. Analisa instalações e equipamentos de laboratório adequados na conservação e distribuição de produtos farmacêuticos e elabora manual de boas práticas e procedimentos operacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/farmaceutico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1586, 'Farmacêutico Bioquímico', 'Analisa exames para verificar as alterações do organismo que são detectados através de testes feitos em laboratórios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/farmaceutico-bioquimico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1587, 'Farmacêutico Homeopata', 'Manipulação de medicamentos com homeopatia (produto natural que pode ser de origem vegetal, animal ou mineral). Elabora fórmulas, leitura de receitas entre outras atividades. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/farmaceutico-homeopata/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1588, 'Farmacêutico Hospitalar', 'Responsabiliza-se por todo o ciclo do medicamento, desde sua seleção (ativos e fornecedores), armazenamento, controles, até o último momento, a dispensação e o uso pelo paciente. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/farmaceutico-hospitalar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1589, 'Farmacêutico Industrial', 'Planeja e avalia projetos de estrutura física em indústrias de produção de medicamentos, cosméticos e correlatos bem como da adequação de instalações físicas, equipamentos e utensílios. Implanta, organiza, gerencia e avalia todas as etapas dos processos de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/farmaceutico-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1590, 'Farmacêutico Trainee', 'Acompanha e desenvolve atividades na área de Farmácia, desenvolvendo pesquisa, análise e preparo de medicamentos, alimentos e demais produtos, análises clínicas e toxicológicas e orientação farmacológica, a fim de capacitar para ocupação do cargo profissional planejado e aprimorar o conhecimento dos negócios, produtos ou serviços da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/farmaceutico-trainee/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1591, 'Faturista', 'Emite notas fiscais de produtos e serviços e realiza cálculo de impostos e alíquotas. Recebe e prepara documentos para análise cadastral e liberação de crédito a fim de gerar faturamento e liberar pedidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/faturista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1592, 'Faturista Hospitalar', 'Realiza faturamento de contas médicas e hospitalares, emite notas fiscais, boletos e duplicatas. Analise a cobrança correta de clientes conforme contrato e prontuário e revisa glosas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/faturista-hospitalar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1593, 'Ferramenteiro', 'Realiza a confecção e reparo de ferramentas de corte, dobra, repuxo e outras para estruturar chapas de aço e demais materiais metálicos e desenvolve molde de sopro e injeção plástica, de alumínio ou estampos. Monta e ajusta componentes das peças, de acordo com manuais e instrumentos de medição para atender dimensões e alinhamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ferramenteiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1594, 'Ferramenteiro de Moldes Plásticos', 'Desenvolve desenhos, peças e moldes de injeção plástica, efetua manutenções preventivas e corretivas em sistemas de injeção (câmara quente) e realiza reparos e troca de peças.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ferramenteiro-de-moldes-plasticos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1595, 'Ferreiro', 'Molda metais, à quente ou frio, consertando ou produzindo objetos e ligas metálicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/ferreiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1596, 'Figurinista', 'Elabora o figurino de personagens de uma produção artística, seja ela teatral, musical, cinematográfica ou televisiva, envolvendo os trajes, adereços e acessórios de acordo com os aspectos do roteiro.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/figurinista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1597, 'Fiscal de Caixa', 'Providencia dinheiro para troco, faz cancelamentos, estornos, devoluções de valores registrados por engano e controla as maquinetas POS. Organiza filas, controla o horário de eventuais substituições.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/fiscal-de-caixa/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1598, 'Fiscal de Campo', 'Fiscaliza os serviços realizados em campo, acompanha e valida instalações e manutenções e identifica melhorias nos processos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1599, 'Fiscal de Loja', 'Atua na prevenção de perdas e danos no interior da loja, fiscaliza a entrada e a saída de funcionários e presta atendimento ao cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/fiscal-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1600, 'Fiscal de Obras', 'Atua na vistoria e verificação dos projetos. Orienta e fiscaliza as atividades e obras de construção civil. Elabora relatórios de vistorias realizadas, para assegurar a continuidade dos serviços. Avalia e otimiza as etapas de serviços dentro do cronograma apresentado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/fiscal-de-obras/');
