INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1765, 'Gestor Ambiental', 'Gerencia e planeja projetos de preservação ambiental, administração de resíduos industriais e programas de reciclagem de matérias-primas. Elabora estratégias para reduzir impactos de poluição industrial e tratamento de efluentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/gestor-ambiental/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1766, 'Gestor Portuário', 'Planeja e organiza as atividades modais de importação e exportação, controla a infraestrutura e custos das operações portuárias e gerencia as cargas e os trâmites legais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/gestor-portuario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1767, 'Governanta', 'Dirige e organiza os serviços de atendimento aos hóspedes, como limpeza, lavanderia, conservação de andares e quartos, entre outros, a fim de assegurar os padrões requeridos pelo hotel e atender as necessidades e satisfação dos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/governanta/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1768, 'Greidista', 'Realiza cálculo de material a ser usado em terraplanagem, orienta e acompanha trabalho de corte e aterro, nivelamento e compactação de pistas e verifica estacas e marcações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/greidista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1769, 'Guia de Turismo', 'Acompanha e orienta pessoas ou grupo de turistas em visitas, excursões e viagens. Realiza roteiro turístico e fornece informações geográficas e históricas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/guia-de-turismo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1770, 'Historiador', 'Desenvolve objeto de estudo e pesquisa sobre relações humanas e sociais. Realiza coleta, tratamento e análise de dados e informações. Organiza acervos públicos e privados de museus, cultura, biblioteca, entre outros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/historiador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1771, 'Hostess', 'Recepciona clientes em restaurantes, bares, eventos ou festas e organiza fila de espera, a fim de assegurar a qualidade dos serviços prestados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/hostess/');
