INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1890, 'Mecânico de Bicicletas', 'Realiza serviços de montagem, manutenção e limpeza de bicicletas e componentes, zela pela organização e preservação dos equipamentos e ferramentas de trabalho. Faz contato direto com o cliente, oferece consultoria sobre mecânica geral, manutenção preventiva e aquisição de equipamentos de ciclismo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-bicicletas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1891, 'Mecânico de Empilhadeira', 'Atua com manutenção preventiva e corretiva em empilhadeiras elétricas. Realiza a desmontagem, montagem, ajustes, regulagens, cálculos e medições dos componentes dos sistemas mecânicos da empilhadeira.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-empilhadeira/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1892, 'Mecânico de Manutenção', 'Faz manutenção de máquinas, motores e equipamentos industriais, repara e substitui peças, faz ajustes e
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1893, 'Mecânico de Manutenção de Aeronaves', 'Realiza manutenção preventiva e corretiva de motores e demais partes mecânicas de aeronaves e repara ou substitui peças para garantir seu perfeito funcionamento e conforme os procedimentos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-manutencao-de-aeronaves/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1894, 'Mecânico de Manutenção Hidráulica', 'Atua no setor de manutenção de máquinas, prensas hidráulicas e pneumáticas. Realiza as correções, prestar suporte, analisar possíveis falhas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-manutencao-hidraulica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1895, 'Mecânico de Manutenção Predial', 'Faz a manutenção corretiva e preventiva nos equipamentos das unidades produtivas, bem como os equipamentos que forem para a oficina mecânica. Realiza alinhamento nos acionamentos dos equipamentos, em correias transportadoras e fabrica peças diversas para utilização nas unidades produtivas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1896, 'Mecânico de Máquinas', 'Realiza manutenção preventiva e corretiva, realiza a troca de componentes danificados e testes de funcionamento. Faz leitura e interpretação de desenho mecânico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-maquinas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1897, 'Mecânico de Refrigeração', 'Faz a manutenção preventiva e corretiva de instalações e equipamentos de refrigeração, ventilação, calefação e ar-condicionado. Realiza teste, lubrificação e regulagem nos sistemas de refrigeração e climatização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-refrigeracao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1898, 'Mecânico de Sopro', 'Realiza manutenção corretiva e preventiva de máquinas hidráulicas e pneumáticas, repara, troca e regula moldes de sopro e injeção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-sopro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1899, 'Mecânico de Usinagem', 'Realiza usinagem em torno convencional, solda convencional e TIG, montagem de estruturas de máquinas e ajustes de máquinas em geral.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-usinagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1900, 'Mecânico de Veículos', 'Realiza manutenção mecânica preventiva e corretiva de veículos, monta e troca peças, lubrifica motor, regula mecanismos e alinha direção. Auxilia o cliente quanto à substituição e aproveitamento de componentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-de-veiculos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1901, 'Mecânico Eletricista', 'Realiza manutenção preventiva e corretiva, faz a instalação de quadros de distribuição de força, análise de consumo de energia, ligação e desligamento de aparelhos elétricos e eletrônicos, além de executar manutenções prediais. Identifica defeitos elétricos, possibilitando a reparação e substituição de componentes, ajustando peças e simulando o funcionamento dos componentes e equipamentos. Interpreta correção de esquemas, conectando cabos aos equipamentos e acessórios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-eletricista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1902, 'Mecânico Hidráulico', 'Executa serviços de manutenção mecânica, monta e desmonta máquinas e equipamentos, repara e substitui partes e peças. Traça, fura, abre rosca, corta peças, manual ou mecanicamente, para confecção de peças e máquinas. Acompanha os testes de produção, verificando o adequado funcionamento das máquinas. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-hidraulico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1903, 'Mecânico Industrial', 'Instala equipamentos mecânicos, realiza manutenção preventiva e corretiva. Desenvolve desenhos e projetos de conjuntos mecânicos, componentes e ferramentas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1904, 'Mecânico Lubrificador', 'Executa a manutenção mecânica e lubrificação em máquinas e equipamentos, para garantir o pleno atendimento aos parâmetros de processo e de qualidade necessários para a produção evitando paradas na linha devidas falhas de natureza mecânica. Atua na manutenção mecânica e antevê anormalidades que possa interferir neste desempenho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-lubrificador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1905, 'Mecânico Montador', 'Atua na montagem e desmontagem de máquinas industriais, operação de instrumentos de medição mecânica e ajuste de peças. Realiza a expedição e instalação dos equipamentos, manutenção corretiva de maquinários, usinagem, hidráulica, pneumática, elétrica e caldeiraria.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mecanico-montador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1906, 'Médico Acupunturista', 'Utiliza métodos terapêuticos para tratar lesões funcionais ou aliviar dores.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1907, 'Médico Alergologista', 'Trata doenças alérgicas e não alérgicas e suas co-morbidades, como conjuntivite, sinusite, faringite, otites, alergia a medicamentos e drogas em geral.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1908, 'Médico Ambulatorial', 'Atua em unidade de pronto atendimento, no tratamento e acompanhamento de pacientes em situação de urgência e emergência, e em condições físicas e patológicas que necessitem de cuidados. Realiza procedimentos para manutenção de funções vitais e, se necessário, referenciar para atendimento de maior complexidade.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1909, 'Médico Anestesista', 'Aplica anestesia com a finalidade de evitar dor em um paciente que está sendo submetido a uma intervenção cirúrgica ou exame mais complexo. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-anestesista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1910, 'Médico Auditor', 'Desenvolve auditoria em todo sistema hospitalar, analisando despesas e custos com tratamentos a pacientes e autorização de procedimentos, a fim de garantir o correto aproveitamento dos leitos e evitar irregularidades. Realiza exame pericial e analítico em pacientes e avalia indicação de afastamento de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-auditor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1911, 'Médico Cardiologista', 'Atendimento, diagnóstico e tratamento das doenças do coração bem como os outros componentes do sistema circulatório.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1912, 'Médico Cirurgião', 'Faz cirurgias em pronto socorro e hospitais, prepara procedimentos, orientações aos pacientes, analisa quadro clínico e acompanha pós operatório.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-cirurgiao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1915, 'Médico Cirurgião Vascular', 'Diagnóstico e tratamento clínico das doenças vasculares. Realiza cirurgias e aplicações para retirada de varizes e vasos sanguíneos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-cirurgiao-vascular/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1916, 'Médico Clínico Geral', 'Recebe e acompanha pacientes em consultórios, hospitais, clínicas, empresas, residências e em locais de urgência. Conversa com o paciente para ouvir sua história médica, queixas e descrição dos sintomas e examina o paciente para fazer o diagnóstico.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1917, 'Médico Colonoscopista', 'Trabalha em consultório médico, clínica ou hospital, faz a limpeza do intestino, aplica sedação no reto para verificar se há obstrução, por meio de equipamentos específicos como colonoscópio para acompanhar a saúde do paciente.  ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-colonoscopista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1918, 'Médico Dermatologista', 'Realiza atendimento, diagnóstico e tratamento de doenças e infecções de pele, cabelos e unhas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-dermatologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1919, 'Médico do Esporte', 'Previne e trata problemas originados de atividade esportiva. Orientação médica e acompanha times e equipes.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1920, 'Médico do Trabalho', 'Previne e trata doenças causadas pelo ambiente de trabalho ou por práticas profissionais. Emite Laudo Técnico das Condições Ambientais de  Trabalho (LTCAT) e coordena o Programa de Controle em Medicina Ocupacional (PCMSO).', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-do-trabalho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1921, 'Médico do Tráfego', 'Aplica exames clínicos de aptidão mental e física em candidatos para renovação ou obtenção da carteira nacional de habilitação. Realiza perícias e estuda causas de acidente de tráfego.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-do-trafego/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1922, 'Médico Ecocardiografista', 'Faz exames de ecocardiograma, orienta pacientes, laudos, prescreve medicamentos, trata e encaminha para outros exames (se necessário).', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-ecocardiografista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1923, 'Médico Emergencista', 'Diagnóstico e tratamento de pacientes que necessitem de cuidados rápido em uma situação imprevista, sem agendamento prévio, com uma doença ou lesão aguda. Cuidados pré-hospitalares até o atendimento hospitalar. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-emergencista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1924, 'Médico Endocrinologista', 'Cuida e trata de problemas hormonais e de metabolismo.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1925, 'Médico Esteticista', 'Faz procedimentos de estética, mesoterapia, carboxiterapia, esclerose, implante (botox / preenchimento) e peeling.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1926, 'Médico Fisiatra', 'Trata e faz prevenção da perda da função para algumas atividades como; Dor, AVC (derrames), lesão medular (tetraplegia e paraplegia), paralisia cerebral, traumatismos craniencefálicos, Diabetes, amputados, entre outros.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1927, 'Médico Gastroenterologista', 'Faz diagnóstico, trata e ajuda a prevenir as doenças do sistema digestório. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-gastroenterologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1928, 'Médico Gastropediatra', 'Faz diagnóstico, trata e ajuda a prevenir as doenças do sistema digestório infantil.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1929, 'Médico Geneticista', 'Cuida dos transtornos da genética humana. Lida com o diagnóstico, tratamento e controle dos distúrbios genéticos e hereditários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-geneticista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1930, 'Médico Geriatra', 'Previne e trata de doenças relacionadas à velhice, prolongamento da vida do paciente. Cuida da qualidade de vida dos idosos, considerando os aspectos ambientais, sociais e culturais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-geriatra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1931, 'Médico Ginecologista', 'Trata da saúde da mulher, cuida e previne doenças do sistema reprodutor feminino (útero, vagina, ovários e tubas uterinas).', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-ginecologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1932, 'Médico Hebiatra', 'Responsável pela saúde de adolescentes, acompanha o desenvolvimento físico prevenindo ou tratando doenças e atua também discutindo questões relacionadas às esferas social e psicológica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-hebiatra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1933, 'Médico Hematologista', 'Atendimento, análise dos exames de sangue por meio do hemograma e exames específicos, verificar se existe alguma deficiência na medula dos pacientes.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1934, 'Médico Hemoterapeuta', 'Organiza, orienta e realiza campanhas de doação de sangue. Realiza triagem clínica dos doadores. Fornece resultado dos exames de doações e orientação terapêutica. Supervisiona a realização dos exames. Assessora o corpo clínico do hospital nas dúvidas relativas à especialidade. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-hemoterapeuta/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1936, 'Médico Imunologista', 'Trata das doenças do sistema imunológico. Faz exames, avalia em que estado se encontra o organismo, para então fazer o diagnóstico correto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-imunologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1937, 'Médico Infectologista', 'Atua com estudo de doenças infecciosas e parasitárias que podem ser causados por bactérias, fungos, vírus e demais microorganismos. Trata e acompanha pacientes com doenças infecciosas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1938, 'Médico Intensivista', 'Responsável pela UTI, aplica técnicas avançadas para restabelecer funções comprometidas quando há risco de morte.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-intensivista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1939, 'Médico Mamografista', 'Faz radiografia das mamas, utilizando da radiação para revelar possíveis alterações no órgão, como tumores ou simples nódulos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-mamografista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1940, 'Médico Mastologista', 'Trata, previne e diagnostica pacientes com doenças da mama.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-mastologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1941, 'Médico Nefrologista', 'Responsável pelo diagnóstico e tratamento clínico das doenças do sistema urinário, principalmente relacionadas ao rim.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-nefrologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1942, 'Médico Neurocirurgião', 'Trata adultos e crianças portadores de doenças do sistema nervoso central (tumores, doenças vasculares, degenerativas), Traumas craniencefálicos e lesões raqui-medulares passíveis de abordagem cirúrgica. Tratar também da substituição de órgãos sensoriais (olho e ouvido interno) disfuncionais por dispositivos artificiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-neurocirurgiao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1943, 'Médico Neurologista', 'Investiga, diagnostica e tratar distúrbios neurológicos em crianças e adultos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1944, 'Médico Nutrólogo', 'Diagnostica e trata doenças nutricionais (que incluem as doenças nutroneurometabólicas de alta prevalência nos dias de hoje como a obesidade, a hipertensão arterial, gastrite e o diabetes mellitus). Solicita e avalia exames diagnósticos, quando necessário. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-nutrologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1945, 'Médico Obstetra', 'Faz consultas com as mulheres, orienta sobre a importância da consulta periódica com o ginecologista e aconselhamento pré-gestacional, realiza pré-natal: exame que ajuda a diagnosticar patologias que possam vir a acometer a mãe e o feto.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1946, 'Médico Oftalmologista', 'Investiga, estuda, diagnostica e trata as doenças relacionadas com os olhos, como: astigmatismo, coriorretinite,  ambliopia, catarata, degeneração macular, Identifica e corrigi erros apresentados nos olhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-oftalmologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1947, 'Médico Oncologista', 'Prevenção, diagnostico, tratamento e acompanhamento do câncer do paciente, orientando - o quanto ao melhor tratamento, como: cirúrgico, hormônio terapia, quimioterapia, radioterapia, ou até a combinação dos mesmos, indicando - o também, quando necessário, a procura de outros especialistas para complementação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-oncologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1948, 'Médico Ortomolecular', 'Trata o organismo com quantidades de vitaminas e minerais de que ele precisa para funcionar perfeitamente. Trata para que os ciclos metabólicos do organismo mantenham e recebam tecidos saudáveis, adiando o envelhecimento da pele, cabelo e do organismo em geral. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-ortomolecular/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1949, 'Médico Ortopedista', 'Cuida das doenças e deformidades dos ossos, músculos, ligamentos, articulações e elementos relacionados ao aparelho locomotor.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-ortopedista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1950, 'Médico Otorrinolaringologista', 'Trata doenças relacionados ao nariz, orelha e garganta. Faz cirurgias estéticas da face.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-otorrinolaringologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1951, 'Médico Patologista', 'Coordena testes laboratoriais em materiais biológicos, analisa e emite laudos, interpretando alterações provocadas pelas doenças nos órgãos afetados, a fim de estabelecer diagnóstico e colaborar na decisão de tratamentos aos pacientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-patologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1952, 'Médico Pediatra', 'Realiza consultas de rotina e acompanha o crescimento da criança. Previne e trata as possíveis enfermidades, orienta e aconselha as mães e faz diagnóstico e tratamento de doenças desde o nascimento até a adolescência.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-pediatra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1953, 'Médico Plantonista', 'Faz atendimento de urgência e emergência para adulto e pediátricos. Atende com prioridade os pacientes de urgência e emergência identificados de acordo com a classificação definida pelo enfermeiro classificador de risco. Realiza consultas, exames clínicos, prescreve medicamentos e orienta os pacientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-plantonista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1954, 'Médico Pneumologista', 'Faz atendimento, diagnósticos, trata e acompanha pacientes com doenças pulmonares e respiratórias contraídas de diversas formas, indicando o melhor tratamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-pneumologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1955, 'Médico Proctologista', 'Realiza atendimento, diagnóstico e tratamento de doenças e infecções do intestino grosso, reto e ânus.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-proctologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1956, 'Médico Psiquiatra', 'Realiza atendimento no diagnóstico, prevenção e tratamento de doenças psiquiátricas, como dependência química, transtornos afetivos e comportamentais, entre outros e desenvolve ações para promoção da saúde mental.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-psiquiatra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1957, 'Médico Radiologista', 'Médico responsável pelo diagnóstico por imagem utilizando-se de tecnologias baseadas na física do raio-x, ressonância magnética e som. Utiliza-se destas ferrramentas para o diangóstico e terapias através de procedimentos guiados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-radiologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1958, 'Médico Regulador', 'Atende as situações de urgência / emergência. Realiza triagem das ocorrências e identifica presença de risco de morte. Decidi quais recursos são necessários e mais adequados ao atendimento destes pacientes e prestar o atendimento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-regulador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1959, 'Médico Reumatologista', 'Trata e diagnostica doenças relacionadas aos tecidos conjuntivos ou articulações, que sejam degenerativas (algumas são inflamatórias), como as artrites, escleroses, fibromialgia, gota, osteoartrite, osteoporose ou lupus.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-reumatologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1960, 'Médico Traumatologista', 'Investiga, diagnostica, trata e acompanha enfermidades relacionadas com fraturas e lesões ósseas e tendinosas provocadas por eventos traumáticos no aparelho músculo - esquelético ou locomotor, composto por: braços, mãos, pés, pernas, coluna, bacia, músculos, tendões e ligamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-traumatologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1961, 'Médico Ultrassonografista', 'Realiza exames de ultrassom geral, opera equipamentos, esclarece dúvidas dos pacientes e elabora laudos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-ultrassonografista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1962, 'Médico Urologista', 'Trata do trato urinário de homens e mulheres e do sistema reprodutor dos homens (testículos, ducto deferente, vesículas seminais, próstata e pênis).
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1963, 'Médico Visitador', 'Visitas em pacientes que estão sob cuidados de internação domiciliar, através de visitas semanais para acompanhamento do plano terapêutico, entre demais atividade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medico-visitador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1964, 'Medidor de Obras', 'Mede obras para instalação de materiais, como pisos, escadas, bancadas, entre outros, e realiza leitura e interpretação de plantas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/medidor-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1965, 'Meio Oficial Ajustador Mecânico', 'Auxilia nas atividades de ajustagem mecânica, acompanha a fabricação, reparo e manutenção de peças e montagem de conjuntos mecânicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-ajustador-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1966, 'Meio Oficial Carpinteiro', 'Auxilia no preparo do canteiro de obras, confecciona formas, forro de laje, andaimes e demais estruturas de madeira.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-carpinteiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1967, 'Meio Oficial de Caldeiraria', 'Confecciona e repara peças em chapas de metal como aço, ferro galvanizado, cobre, alumínio, entre outros. Fabrica ou conserta caldeiras, tanques, reservatórios e recipientes. Recorta e modela barras perfiladas de materiais ferrosos e não ferrosos para fabricar esquadrias e peças similares.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-de-caldeiraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2045, 'Nutricionista do Esporte', 'Elabora, coordena e faz acompanhamento alimentar de atletas e praticantes de atividade física, criando dietas adequadas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1968, 'Meio Oficial de Ferramentaria', 'Auxilia na construção, manutenção e montagem de ferramentas, matrizes, gabaritos e dispositivos, analisando junto ao Ferramenteiro projetos e desenhos. Confere, ajusta e mede peças que retornaram à bancada, a fim de efetuar a montagem final.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-de-ferramentaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1969, 'Meio Oficial de Impressão Flexográfica', 'Auxilia na operação de máquina impressora flexográfica, de acordo com ordem de serviço. Regula e controla máquina e realiza manutenção corretiva e preventiva, quando necessário.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-de-impressao-flexografica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1970, 'Meio Oficial de Manutenção', 'Cuida da manutenção, limpeza, e conservação do local de trabalho. Guarda e controla todo material, aparelhos e equipamentos sob sua responsabilidade. Auxilia na conservação de edificações e equipamentos em geral.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1971, 'Meio Oficial de Manutenção Predial', 'Auxilia na manutenção predial de áreas elétrica, mecânica, civil, hidráulica, alvenaria e pintura, realizando troca, conserto e instalação de peças, componentes e equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1972, 'Meio Oficial de Pintura', 'Auxilia no preparo, lixamento, pintura e acabamento de superfícies.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-de-pintura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1973, 'Meio Oficial de Refrigeração', 'Auxilia na instalação e manutenção corretiva e preventiva de equipamentos de refrigeração e climatização. Troca peças e limpa aparelhos, de acordo com normas de segurança e qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-de-refrigeracao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1974, 'Meio Oficial Eletricista', 'Faz a montagem ou reforma de sistemas elétricos, painéis e outros comandos, visando o perfeito funcionamento dos equipamentos. Presta auxílio ao eletricista de manutenção na realização de tarefas. Orienta os usuários sobre as condições de funcionamento e operação dos equipamentos elétricos. Otimiza o desempenho desses equipamentos e evita paradas por quebra ou defeito. Determina o serviço de elétrica que precisa ser feito externamente e fazer avaliação do trabalho após sua execução.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-eletricista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1975, 'Meio Oficial Fresador', 'Auxilia na operação de máquina fresadora na usinagem de peças de aço e ferro e realiza leitura de desenhos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-fresador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1976, 'Meio Oficial Fresador CNC', 'Auxilia na operação de máquina fresadora CNC na usinagem de peças de aço e ferro e realiza leitura de desenhos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-fresador-cnc/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1977, 'Meio Oficial Funileiro', 'Auxilia na fabricação e reparação de funilaria em veículos, caldeiras, tanques e outros recipientes de chapas de aço.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-funileiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1978, 'Meio Oficial Hidráulico', 'Auxilia na manutenção e instalação de rede hidráulica e realiza leitura e interpretação de desenhos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-hidraulico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1979, 'Meio Oficial Marceneiro', 'Auxilia nos serviços de manutenção, montagem e acabamento de peças em madeira e realiza leitura e interpretação de desenhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-marceneiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1980, 'Meio Oficial Mecânico', 'Auxilia na montagem e desmontagem, instalação e manutenção corretiva e preventiva de máquinas e equipamentos mecânicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1981, 'Meio Oficial Mecânico de Manutenção', 'Auxilia na manutenção mecânica corretiva e preventiva de máquinas, equipamentos e dispositivos, diagnostica estado de conservação e funcionamento e realiza troca, ajuste, reparo e testes das peças.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-mecanico-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1982, 'Meio Oficial Montador', 'Auxilia na montagens de peças, acessórios e equipamentos, de acordo com especificações técnicas nos desenhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-montador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1983, 'Meio Oficial Serralheiro', 'Auxilia na construção e montagem de estruturas, armações, quadros, portas, entre outros materiais de ferro ou alumínio. Realiza leitura e interpretação de desenhos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-serralheiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1984, 'Meio Oficial Soldador', 'Atua no setor de soldagem da empresa, presta auxílio ao soldador na soldagem de peças, manutenção, soldagem eletrodo, serra elétrica, pintura e desenvolve atividades pertinentes a função.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-soldador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1985, 'Meio Oficial Torneiro Mecânico', 'Auxilia no preparo, regulagem e operação de máquinas para usinagem de peças, controla parâmetros e qualidade das peças.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meio-oficial-torneiro-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1986, 'Mensageiro', 'Executa atividades de escritório e efetua pagamentos em bancos e estabelecimentos comerciais, controla arquivos administrativos, recebe e distribui correspondências, documentos e encomendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mensageiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1987, 'Mensageiro de Hotel', 'Acompanha chegada e saída dos hóspedes, organiza bagagens e orienta quanto as áreas públicas do hotel. Controla as solicitações de clientes, entrega correspondências e realiza pedido de táxi, transportes turísticos e reserva em restaurantes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mensageiro-de-hotel/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1988, 'Merendeira', 'Prepara a merenda e os alimentos. Atua com higiene dos alimentos preparados, procede a limpeza da cozinha e dos equipamentos. Auxilia na distribuição da merenda e dos alimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/merendeira/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1989, 'Mestre Cervejeiro', 'Acompanha todas as etapas do processo de fabricação de cervejas, seleciona e avalia as matérias-primas, analisa amostras e realiza testes de degustação para garantir a qualidade do produto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mestre-cervejeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1990, 'Mestre de Cabotagem', 'Realiza serviços em convés, inspeciona equipamentos e monitora carga e descarga, de acordo com os requerimentos da marinha.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mestre-de-cabotagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1991, 'Mestre de Obras', 'Lidera equipes de trabalhadores da construção civil em canteiros, controla equipamentos e materiais, inspeciona qualidade de matérias-primas utilizadas e administra cronograma de obras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mestre-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1992, 'Meteorologista', 'Atua em áreas ligadas a previsão do tempo, elaborando textos, relatórios e materiais que serão veiculados na TV, internet ou rádio.  Presta consultoria personalizada para fazendeiros e empresas, que dependem da previsão do tempo e do clima na hora de planejar o plantio dos alimentos. Trabalha com de instrumentação meteorológica, desenvolve e faz a manutenção de instrumentos como barômetros, termômetros, sensores automáticos, etc. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/meteorologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2124, 'Operador de Serra', 'Opera serras automáticas e manuais para realizar cortes em madeira e chapas de acrílico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/operador-de-serra/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1993, 'Metrologista', 'Faz a calibração interna e externa de equipamentos e trabalho preventivos no maquinário da empresa. Realiza a emissão de relatórios de análises, efetua a interpretação de desenhos, dimensionamento de peças, ferramentais e maquinário.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/metrologista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1994, 'Microbiologista', 'Controle de qualidade em meios de cultura, como: promoção de crescimento, esterilidade e PH, validação de métodos analíticos, e elaboração de protocolos de validação.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1995, 'Modelista', 'Desenvolve moldes de calçados e roupas de acordo com tabelas/amostras estabelecidas pela empresa, acompanha confecção de peças piloto e analisa e aprova amostras. Estuda estilos e tendências de moda e identifica processos, matérias-primas, máquinas e equipamentos necessários para a produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/modelista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1996, 'Modelo', 'Posa para fotógrafos e artistas plásticos, apresenta produtos em trabalhos publicitários, participa de desfile em passarelas, dentre outras atividades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/modelo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1997, 'Modelo de Prova', 'Realiza corpo de prova de roupas para demonstração e verificação de modelagem e caimento. Experimenta peças piloto de coleção, a fim de verificar possíveis defeitos e ajustes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/modelo-de-prova/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1998, 'Moldador', 'Realiza de moldagem plásticos e metais para fabricação de produtos, prepara matérias-primas e acompanha processo de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/moldador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (1999, 'Moldureiro', 'Realiza montagem de molduras de quadros, espelhos, porta-retratos e telas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/moldureiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2000, 'Moleiro', 'Realiza manutenção corretiva e preventiva de suporte de suspensão, amortecedores e molas de veículos, diagnosticando falhas de funcionamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/moleiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2001, 'Monitor de Alunos', 'Desenvolve atividades pedagógicas de acordo com planejamento conjunto, cuida da higiene pessoal das crianças, oferece e acompanha a alimentação das crianças, zela pelos cuidados gerais e segurança das crianças. Elabora planos semanais de atividades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/monitor-de-alunos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2002, 'Monitor de Informática', 'Realiza a monitoria aos alunos em laboratório de informática, apoia ao uso de tecnologia da informação nos departamentos e de equipamentos audiovisual. Ajuda na manutenção de website e ensina sobre gerenciamento de rede, hardware e software.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/monitor-de-informatica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2003, 'Monitor de Qualidade', 'Monitora as equipes de atendimento, avalia e acompanha as ligações, pontua e faz marcação em planilhas de monitoramento, fornece informações para aplicação de feedback para os supervisores, acompanha as mudanças e atualizações ocorridas nas ferramentas de apoio. Consolida o resultado das monitorias de qualidade e aplica feedback.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/monitor-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2004, 'Montador', 'Realiza montagem e desmontagem de estruturas, móveis, painéis, máquinas e demais equipamentos, conforme projeto, normas e procedimentos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2005, 'Montador de Calçados', 'Prepara máquinas e peças para costurar, fabricar e montar calçados. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-calcados/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2006, 'Montador de Elevador', 'Realiza pequenas ligações elétricas e montagem dos conjuntos de partes e peças, desce linhas de prumadas e distorce alinhamento de poços, sobe máquinas para a casa de máquinas ou ponto da caixa de corrida, sobe guias e fixa na caixa de corridas, monta estrutura de cabina e contra peso, traciona elevadores, fixa limitador de velocidade e testa aparelho de segurança, coloca portas de pavimento e gerencia um ajuda de instalação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-elevador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2007, 'Montador de Equipamentos', 'Monta, testa e inspeciona placas, aparelhos e, ou equipamentos eletroeletrônicos. Instala painel de comando de rampa, esteira rolante e elevadores. Preenche relatórios e fichas dos equipamentos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-equipamentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2008, 'Montador de Esquadrias', 'Realiza montagem, instalação, manutenção e acabamento de esquadrias de alumínio, madeiras, entre outros materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-esquadrias/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2009, 'Montador de Estruturas Metálicas', 'Realiza montagem e desmontagem de estruturas metálicas, interpreta desenhos técnicos e faz soldagem de peças.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-estruturas-metalicas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2010, 'Montador de Máquinas', 'Atua na montagem e ajustes de máquinas e equipamentos, faz a leitura, interpretação de desenhos técnicos e mecânicos, além de reparo e troca de peças.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-maquinas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2011, 'Montador de Móveis', 'Atua com montagem de móveis, realiza o atendimento ao cliente e mantém a organização do local.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-moveis/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2012, 'Montador de Painéis', 'Identifica os componentes e fios do painel, verifica e se responsabiliza por todo o material recebido para a montagem. Monta o layout do painel de acordo com o projeto eletromecânico, faz o barramento. Preenche corretamente as planilhas e quadros de acompanhamento da montagem, executa testes e ensaios nos painéis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-paineis/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2013, 'Montador de Veículos', 'Realiza montagem e desmontagem de veículos em linha de produção e para processos de pintura, funilaria, polimento e higienização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-de-veiculos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2014, 'Montador Elétrico', 'Monta equipamentos, ajusta e faz a manutenção de peças ou conjuntos parciais componentes de equipamentos mecânicos. Faz leitura e interpretação de esquemas elétricos e layouts, eletrônica industrial, ferramental específico, técnicas eletroeletrônicas do tipo PLC, conversores de frequência, automação, materiais e componentes eletroeletrônicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-eletrico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2015, 'Montador Eletrônico', 'Realiza montagem de placas, circuitos, cabeamento e demais componentes eletrônicos, de acordo com desenho técnico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-eletronico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2016, 'Montador Hidráulico', 'Interpreta projetos para montagem de infraestruturas hidráulicas, conecta tubulações e instala sistemas de refrigeração e climatização. Inspeciona materiais e aplica teste nos equipamentos, a fim de identificar e corrigir falhas e vazamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/montador-hidraulico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2017, 'Mordomo', 'Identifica e antecipa as necessidades dos hospedes. Proporciona alimentos e serviços de bebidas conforme o exigido. Ajuda com quaisquer planos ou arranjos que o hospede pretende fazer durante a sua estadia. Supervisiona e garante a Segurança dos hospedes banhistas. Garante que os clientes se sintam seguros na área da piscina. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/mordomo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2019, 'Motorista', 'Faz transporte de pessoas ou materiais para o destino
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2020, 'Motorista Administrativo', 'Dirige veículos para o transporte de funcionários de empresas, retira e entrega documentos e malotes em fornecedores e clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-administrativo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2021, 'Motorista Categoria B', 'Realiza o transporte de pessoas ou materiais para o destino estabelecido. Inspeciona as condições do veículo, analisando a parte elétrica, pneus e abastecimento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-categoria-b/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2022, 'Motorista Categoria C', 'Transporta materiais para diversos itinerários, possui conhecimento nas leis de trânsito e normas de segurança. Elabora relatórios de viagem e faz conferência de materiais para assegurar a correta entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-categoria-c/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2023, 'Motorista Categoria D', 'Transporta materiais para diversos itinerários, de acordo com as leis de trânsito e normas de segurança. Elabora relatórios de viagem e faz conferência de materiais para assegurar a correta entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-categoria-d/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2024, 'Motorista Categoria E', 'Transporta materiais para diversos itinerários, possui conhecimento nas leis de trânsito e normas de segurança. Elabora relatórios de viagem e faz conferência de materiais para assegurar a correta entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-categoria-e/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2025, 'Motorista de Ambulância', 'Conduz ambulância no transporte de emergência, zelando pelos pacientes e equipe médica. Faz a transferência de pacientes com ambulância simples e UTI seguindo as rotas, assim como, a manutenção e organização do veículo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-de-ambulancia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2026, 'Motorista de Caminhão', 'Transporta materiais e produtos para diversos itinerários. Elabora relatórios de viagem e rota. Efetua a prestação de contas das despesas efetuadas com o veículo. Zela pela conservação e segurança dos veículos, providenciando limpeza, ajustes e pequenos reparos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2027, 'Motorista de Carro Forte', 'Realiza o transporte de valores para bancos e comércio, possui conhecimento nas leis de trânsito e normas de segurança. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-de-carro-forte/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2028, 'Motorista de Guincho', 'Conduz e opera caminhão do tipo guincho para remoção de veículos, máquinas ou cargas pesadas. Realiza inspeções e reparos nos caminhões, vistoria cargas e verifica documentação de veículos e de cargas. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-de-guincho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2029, 'Motorista de Ônibus', 'Atua na condução de transporte coletivo, seguindo as normas de trânsito e realizando as rotas estipuladas pela empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-de-onibus/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2030, 'Motorista Entregador', 'Faz o transporte materiais e produtos para diversos itinerários. Elabora rotas de viagem e faz conferência de materiais para assegurar a correta entrega.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-entregador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2031, 'Motorista Escolar', 'Dirige veículos escolares, zela pelo cuidado aos passageiros, cumpre os horários estabelecidos e faz a limpeza e conservação do veículo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-escolar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2032, 'Motorista Executivo', 'Dirige veículos de passeio para o transporte de diretores e visitantes em viagens ou reuniões. Comparece em locais de embarque e desembarque em aeroportos, retira bagagem e busca encomendas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-executivo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2033, 'Motorista Operador de Betoneira', 'Dirige caminhão betoneira para entrega de concreto em obras e comanda rotação do tambor de mistura, de acordo com programação estabelecida.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/motorista-operador-de-betoneira/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2034, 'Museólogo', 'Administra, organiza e acondiciona as peças em museus e exposições, classifica peças de valor histórico e cultural e planeja exposições e intercâmbio de acervos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/museologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2035, 'Músico', 'Atua com composição de musicas, regência, interpretação de musicas eruditas, clássicas e populares. 
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2036, 'Musicoterapeuta', 'Faz uso da música para a reabilitação física, mental e social de indivíduos ou grupos. Pesquisa a relação do homem com os sons para criar métodos terapêuticos que visem a restabelecer o equilíbrio físico, psicológico e social do indivíduo. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/musicoterapeuta/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2037, 'Naturólogo', 'Atua com recursos naturais e de seu uso na promoção, manutenção e na recuperação da saúde. Faz pesquisas, ensina, orienta e utiliza terapias naturais na prevenção e no tratamento de doenças ou distúrbios físicos ou na melhoria da qualidade de vida.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/naturologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2038, 'Neuropsicólogo', 'Atua com diagnóstico, acompanhamento, tratamento e pesquisa das relações existentes entre o cérebro e as manifestações do comportamento em indivíduos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/neuropsicologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2039, 'Nivelador', 'Realiza marcação de níveis de terreno em obras, com instrumentos topográficos e geotécnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/nivelador/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2040, 'Nutricionista', 'Planeja e administra programas de alimentação e nutrição em empresas, escolas, hospitais, entre outros estabelecimentos. Define o cardápio das refeições, sugere pratos que supram a necessidade dos pacientes e clientes. Orienta e prescreve dieta individual ou em grupo.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2041, 'Nutricionista Clínico', 'Prescreve dietas a pacientes de hospitais, clínicas, instituição de recuperação, ambulatórios e consultórios. Elabora dietas de emagrecimento e adapta alimentação aos tratamentos para qualquer tipo de patologia.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2042, 'Nutricionista Comercial', 'Divulgação técnica de produtos para nutrição, promoção técnica e vendas. Faz treinamento técnico na área de nutrição, visitas, elabora relatórios de performance, promoção e participa de feiras e congressos e as demais atividades para a promoção de produtos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2043, 'Nutricionista de Produção', 'Elabora lista de compras, planeja o cardápio, supervisiona a produção de refeições, instalações físicas, aplica treinamentos com os funcionários, define POP (procedimentos operacionais padronizados), manual de boas práticas e métodos de controle de qualidade de alimentos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/nutricionista-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2044, 'Nutricionista de Qualidade', 'Responde pelo controle de qualidade dos produtos. Treinamento de funcionários, fiscalização, avaliação da produção, transporte, fichas técnicas, medições de temperatura de equipamentos e alimentos. Assegura os procedimentos de garantia da qualidade. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/nutricionista-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2046, 'Oceanógrafo', 'Pesquisa os seres animais e vegetais, o ambiente e os processos marinhos. Coleta, analisa e interpreta informações sobre as condições físicas, químicas, biológicas e geológicas dos ambientes aquáticos. Analisa a composição da água de rios, lagunas e estuários e atua em projetos de saneamento de áreas costeiras, monitorando e gerenciando obras e instalações para garantir a preservação ambiental.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/oceanografo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2047, 'Office-boy', 'Executa serviços da rotina administrativa, envolvendo recepção e distribuição de correspondências e documentos, confecção de cópias e serviços externos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/office-boy/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2048, 'Oficial de Manutenção', 'Realiza manutenção corretiva e preventiva e inspeção de máquinas e equipamentos e identifica necessidades de reparo para preservar as condições de funcionamento. Interpreta desenhos e diagramas de instalações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/oficial-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2049, 'Oficial de Manutenção Predial', 'Realiza instalações elétricas, hidráulicas e sanitárias. Atua com ar condicionado. Mantém os níveis de estoque dos kits de manutenção geral. Resolve possíveis falhas em equipamentos, realiza mudanças de material, mobiliário, máquinas e equipamentos de acordo com os procedimentos de segurança. Recebe as mercadorias e checa se estão em conformidade com o procedimento local.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/oficial-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2050, 'Oficial de Redes', 'Faz serviços de manutenção e reparos em cabos aéreos e subterrâneos das redes de acesso e backbone e em cabos de fibra óptica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/oficial-de-redes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2051, 'Oficial de Refrigeração', 'Presta assistência técnica na instalação e manutenção preventiva e corretiva de aparelhos de refrigeração e climatização, de acordo com normas de segurança e qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/oficial-de-refrigeracao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2052, 'Oficial Eletricista', 'Realiza a manutenção preventiva e corretiva das instalações e quadros distribuidores de baixa tensão e comandos elétricos, troca tomadas, disjuntores e verifica barramentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/oficial-eletricista/');
