INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2165, 'Pintor de Obras', 'Realiza pintura em paredes internas e externas. Prepara as superfícies antes de pintá-las, como limpeza, aplicação de massa fina ou corrida e lixamento. Aplica papel de parede e gesso para acabamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/pintor-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2166, 'Pintor Eletrostático', 'Realiza pintura eletrostática a pó de peças de ferro, alumínio e outros metais e auxilia no preparo de superfícies.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/pintor-eletrostatico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2167, 'Pizzaiolo', 'Monta e prepara massas para pizzas doces e salgadas. Verifica a qualidade dos alimentos, observando normas técnicas de higiene e segurança.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/pizzaiolo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2168, 'Planejador de Produção', 'Planeja as atividades de produção, garante a padronização de produtos e o particionamento do processo de fabricação, mantém o bom funcionamento da linha de produção, forma um fluxo de materiais que respeite restrições relacionadas a fatores tais como, custos de produção, disponibilidade de recursos e materiais e características tecnológicas do processo produtivo considerado, prevê prazos de entrega e controla os custos de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/planejador-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2169, 'Planejador de Projetos', 'Responsável pelo planejamento de projeto, cronogramas, seguimento de prazos deste cronograma, adaptação deste cronograma em função de mudanças de necessidades datas ou novas aplicações, confecção deste cronograma em softwares de planejamento de projeto, coordenação de grupos transversais, visitas técnicas, vistoria para planejamento de serviços e cotação de valores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/planejador-de-projetos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2170, 'Podólogo', 'Cuida e atua na investigação, prevenção, prognóstico da saúde e estética dos pés, tratando de problemas nas unhas, como encravação, infecção, calos, fissuras, bolhas e úlceras micóticas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/podologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2171, 'Polidor', 'Realiza polimento em metais, plásticos e veículos, por meio de processos manuais, semiautomáticos e automáticos, controlando a qualidade do serviço.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/polidor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2172, 'Polidor de Metais', 'Faz o polimento e afiação utilizando processos manuais, semi-automáticos e automáticos, para obter o controle da qualidade do serviço.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/polidor-de-metais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2173, 'Polidor de Veículos', 'Faz o polimento e cristalização de veículos após a pintura ou lavagem, utilizando processos manuais, semiautomáticos e automáticos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/polidor-de-veiculos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2174, 'Porteiro', 'Fiscaliza e guarda o patrimônio, realizando o controle da entrada e saída de pessoas e veículos nas dependências por meio de circuito, comunicando qualquer anormalidade e tomando as providências cabíveis, garantindo a segurança do local.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2175, 'Prensista', 'Prepara matrizes e a linha de produção para forjar peças
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2176, 'Preparador de Autos', 'Acompanha toda a rotina de preparação de veículos, acabamento, polimento, instalação de acessórios, reparos e acompanha as ordens de serviços para que sejam cumpridas até a entrega do veículo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-de-autos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2177, 'Preparador de CNC', 'Prepara e ajusta ferramentas e máquinas de usinagem CNC e atualiza layouts de ferramentas e ocorrências de manutenção das máquinas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-de-cnc/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2178, 'Preparador de Máquina Injetora', 'Prepara máquina injetora, promovendo ajustes e regulagens durante o processo. Auxilia nas atividades de manutenção preventiva e corretiva, limpeza e conservação das máquinas e equipamentos, evitando paradas devido a problemas mecânicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-de-maquina-injetora/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2179, 'Preparador de Máquinas', 'Prepara, ajusta e libera máquinas para produção. Utiliza instrumentos de medição, executa troca de ferramentas e efetua ajustes para garantir a precisão das peças. Lê e interpreta desenhos mecânico. Regula as máquinas instalando ferramentas e outros dispositivos especiais. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-de-maquinas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2180, 'Preparador de Pintura', 'Atua em toda rotina de programação de tintas, lixamento, acabamento, massa e preparação de pintura, entre outras atividades referentes ao cargo. Seleciona materiais a serem trabalhados, executa inspeção visual dos itens, identifica possíveis irregularidades e prepara superfícies para receber pintura.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-de-pintura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2181, 'Preparador de Tintas', 'Desenvolve cores em laboratório e as implementa para produção de artigos têxteis, do vestuário, couros e papéis. Controla a qualidade dos processos de estamparia, impressão e tingimento, bem como de artigos e produtos, quanto à conformidade da cor.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-de-tintas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2182, 'Preparador de Torno', 'Prepara tornos automáticos para usinagem de peças conforme especificações em desenhos ou ordens de serviço. Estuda programação de máquinas, estabelece plano de preparação, realiza ajustes necessários e instrui operadores no controle, regulagem e manipulação dos comandos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-de-torno/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2183, 'Preparador Físico', 'Avalia o preparo físico de alunos, desenvolve atividades físicas e ensina técnicas desportivas para melhorar o desempenho físico das pessoas e realiza treinamentos especializados com atletas de diferentes esportes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/preparador-fisico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2184, 'Processista', 'Trabalha no setor de produção na área de usinagem com tornos automáticos, CNC e centro de usinagem. Verifica desenhos técnicos mecânicos e processos produtivos. Define processo de fabricação do produto ou componente da forma mais otimizada possível focando redução de custo. Usa ao máximo os recursos dos equipamentos, orçamenta itens novos, considerando a previsão de peças por hora, consumo de pastilhas entre outros insumos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/processista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2185, 'Produtor Artístico', 'Produz eventos de entretenimento e dirige programas de televisão. Realiza pesquisa de mercado, define conceitos de identidade visual e musical e desenvolve estratégias de marketing para divulgação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-artistico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2186, 'Produtor Cultural', 'Planeja, elabora e realiza projetos artísticos e culturais, como espetáculos teatrais e de dança, musicais e exposição de artes. Define orçamentos e cronogramas e capta recursos para o evento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-cultural/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2187, 'Produtor de Casting', 'Recruta e escolhe modelos para desfiles, catálogos e demais eventos, e seleciona elenco para filmes. Realiza produção de casting e conduz testes de vídeo e foto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-de-casting/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2188, 'Produtor de Eventos', 'Realiza planejamento e organização de eventos, contrata fornecedores, negocia prazos e pagamentos e acompanha os processos de compras de materiais e serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-de-eventos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2189, 'Produtor de Moda', 'Pesquisa modelos, roupas e acessórios para realização de fotografia, filmes e desfiles, a fim de atender o briefing do evento. Participa na definição de looks a serem fotografados e planeja os figurinos necessários para personagens em filmes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-de-moda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2190, 'Produtor de Televisão', 'Planeja e controla produção de programas de televisão, envolvendo o desenvolvimento de ideias, definição e contratação de elenco e equipe técnica e supervisão de filmagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-de-televisao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2191, 'Produtor de Vídeo', 'Planeja produções de conteúdos, elabora roteiros e marcações para melhores tomadas de cena, manipula equipamentos, capta, edita e finaliza imagens, vídeos e áudios institucionais ou promocionais para TV, internet e redes sociais. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-de-video/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2192, 'Produtor Editorial', 'Elabora e acompanha a edição de livros, revistas e demais produtos de comunicação. Define o conteúdo, tipologia, tamanho das letras, qualidade de papel, paginação e ilustração. Planeja o lançamento de publicações e estuda tendências e necessidades do mercado, a fim de estabelecer metas de abordagem.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-editorial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2193, 'Produtor Gráfico', 'Elabora orçamentos em produção gráfica, PDV (ponto de venda), produção de eventos e mídia. Realiza orçamentos, contrata fornecedores e viabiliza projetos com foco em excelência na relação custo x benefício. Monitora a produção gráfica e instalação dos projetos. Confere pré-impressão, impressão e pós-impressão. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/produtor-grafico/');
