INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2420, 'Projetista', 'Desenvolve projetos de equipamentos industriais, ferramentas e produtos, analisa medidas, define matéria-prima e detalha desenhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2421, 'Projetista 3D', 'Desenvolve projetos de ferramentais e dispositivos utilizados na produção, acompanhando testes de try out de novos ferramentais e sua liberação. Acompanha a construção dos mesmos para atestar sua exatidão, seguindo o projeto inicial desenvolvido pela área de Engenharia.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-3d/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2422, 'Projetista Cadista', 'Desenvolve projetos, especifica equipamentos e materiais usados para a produção e elabora esboços e desenhos definitivos dos projetos, de acordo com as normas e especificações técnicas. Modifica, redesenha e atualiza os desenhos existentes. Define etapas do processo de fabricação, confere e avalia os projetos a serem fabricados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-cadista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2423, 'Projetista Civil', 'Elabora desenhos de projetos, relativos a instalações, equipamentos e máquinas, através de especificações técnicas, instrumentos e softwares disponíveis. Projeta e desenha planos de obras, que inclui a definição de caminhos e plataformas, cálculos de terraplenagem, drenagem e cálculo de materiais e infraestruturas. Realiza o desenvolvimento de planos detalhados, cortes e perfis longitudinais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-civil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2424, 'Projetista de Ar Condicionado', 'Desenvolve projetos de equipamentos de refrigeração e climatização, analisa viabilidade, elabora esboços e desenhos, calcula medidas, define matéria-prima e etapas do processo de fabricação, de acordo com normas e especificações técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-de-ar-condicionado/');
