INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2433, 'Projetista de Produto', 'Desenvolve projetos de novos produtos, elabora esboços e desenhos, calcula medidas e define matéria-prima, de acordo com normas e especificações técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-de-produto/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2434, 'Projetista de Telecomunicações', 'Desenvolve projetos de produtos relacionados a tecnologia, projeta, especifica equipamentos e protótipos. Planeja e executa pesquisas para o desenvolvimento de novos produtos. Elabora e aprova documentação pertinente ao trabalho desenvolvido.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-de-telecomunicacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2435, 'Projetista Elétrico', 'Faz leitura, interpretação e entendimento de diagramas, esquemáticos elétricos, layout e memorial descritivos de instalações. Participa de reuniões com clientes internos e externos para definições e resolução de dúvidas técnicas. Elabora lista de materiais elétricos a serem empregados na obra.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-eletrico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2436, 'Projetista Eletrônico', 'Desenvolve projetos de sistemas eletrônicos, elabora esboços, esquemas e placas de circuitos, de acordo com as normas técnicas. Modifica, redesenha e atualiza os desenhos existentes para atender aspectos estéticos, funcionais e especificações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-eletronico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2437, 'Projetista Hidráulico', 'Atua com realização de projetos em água fria ou quente, pluviais e esgoto sanitário. Faz leitura e interpretação de desenhos e manipulação de AutoCAD.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-hidraulico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2438, 'Projetista Mecânico', 'Desenvolve projetos para fabricação de máquinas, ferramentas e equipamentos mecânicos, de acordo com as normas e especificações técnicas. Define etapas dos processos, analisa viabilidade de produtos e montagem e realiza levantamento de custos e materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-mecanico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2439, 'Projetista Orçamentista', 'Desenvolve projetos e especifica equipamentos e materiais, de acordo com desenhos técnicos. Realiza levantamento de custos de suprimentos, mão de obra e equipamentos para elaboração de orçamentos. Modifica, redesenha e atualiza os desenhos existentes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/projetista-orcamentista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2440, 'Promotor de Crédito', 'Realiza a captação de clientes para venda de crédito financeiro. Realiza simulações, negocia e elabora propostas comerciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/promotor-de-credito/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2441, 'Promotor de Eventos', 'Atua com ações promocionais, ativação, destaque da marca, prepara e implementa exposições. Implementa a exposição da empresa em locais de grande concentração de pessoas. Separa e realiza a distribuição de brindes e materiais promocionais. Produz e envia relatórios com apresentação de resultados das atividades, capta percepções ou depoimentos dos clientes, consumidores e trade. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/promotor-de-eventos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2442, 'Promotor de Financiamento', 'Atua com representação do banco da montadora em concessionária, envia propostas de financiamento para análise de crédito, elabora relatório e formaliza contrato.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/promotor-de-financiamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2443, 'Promotor de Merchandising', 'Participa e atua na montagem de lojas novas no mercado ou região. Acompanha as instalações de comunicação visual. Negocia a participação das lojas em campanhas de lançamentos de produto da empresa. Garante a melhor apresentação da marca e seus produtos nas lojas. Mapeia os pontos-de-venda e gera relatórios periódicos de manutenção do espaço, com fotos do local e diagnósticos de possíveis atualizações e reposições. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/promotor-de-merchandising/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2444, 'Promotor de Vendas', 'Promove produtos e serviços e organiza exposição e rotatividade em pontos de venda de acordo com layout estabelecido pela empresa. Aborda clientes, esclarece dúvidas e distribui panfletos ou amostra grátis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/promotor-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2445, 'Promotor Técnico', 'Realiza promoção técnica de produtos e serviços, especifica e orienta quanto a utilização, elabora propostas comerciais e presta suporte técnico aos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/promotor-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2446, 'Propagandista', 'Visita, comercializa e divulga medicamentos, equipamentos médicos, cirúrgicos e demais produtos farmacêuticos em clínicas médicas e dentárias, consultórios, hospitais, laboratórios, entre outros. Participa de palestras, seminários e eventos para promoção de lançamentos. Elabora relatórios de acompanhamento e controle das metas e resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/propagandista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2447, 'Protético', 'Atua em conjunto com o cirurgião-dentista para melhorar a mastigação e a estética do paciente por meio do desenvolvimento de próteses dentárias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/protetico/');
