INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2448, 'Psicólogo', 'Diagnostica, previne e trata doenças mentais distúrbios emocionais e de personalidade. Estuda, pesquisa e avalia o desenvolvimento emocional e os processos mentais e sociais dos indivíduos, grupos e instituições, elucidando conflitos durante tratamento.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/psicologo/');
