INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2450, 'Psicólogo do Trânsito', 'Trata problemas relacionados ao trânsito, realiza avaliação psicológica em condutores e futuros motoristas e desenvolve ações socioeducativas com pedestres e condutores infratores, entre outras atividades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/psicologo-do-transito/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2451, 'Psicólogo Escolar', 'Responsável pelo estudo, pesquisa e avaliação do desenvolvimento emocional e os processos mentais e sociais em instituições escolares, com a finalidade de análise, tratamento, orientação e educação.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2452, 'Psicólogo Hospitalar', 'Atende pacientes e seus familiares, mediando a relação entre equipe de saúde, para suporte no adoecimento e redução do sofrimento no momento da hospitalização.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/psicologo-hospitalar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2453, 'Psicólogo Organizacional', 'Seleciona funcionários para empresas. Orienta carreiras, treina e forma profissionais, promove relações sociais saudáveis entre os colaboradores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/psicologo-organizacional/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2454, 'Psicólogo Social', 'Assessoria a órgãos públicos e/ ou particulares, realizando a organização de objetivos políticos ou comunitários na elaboração e implementação de programas de mudança de caráter social ou técnico, visando o auxílio em situações planejadas ou resolução de possíveis conflitos.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2455, 'Psicomotricista', 'Acompanha o desenvolvimento motor de crianças e avalia, trata e estuda transtornos psicomotores, a fim de elaborar o plano terapêutico.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/psicomotricista/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2456, 'Psicopedagogo', 'Identifica as dificuldades e transtornos que impedem o indivíduo de internalizar o conteúdo ensinado em seu contexto de aprendizagem, analisando o comportamento e a forma de aprendizagem. Desenvolve pesquisas e estudos científicos relacionados ao processo de aprendizagem e seus problemas, direcionado à prevenção de problemas de aprendizagem.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2457, 'Publicitário', 'Planeja e organiza campanhas publicitárias, dirigindo e coordenando a redação dos textos e a elaboração dos trabalhos gráficos, plásticos e outros de expressão artística, para promover pela imagem, pela palavra ou pelo som as qualidades e a utilização de mercadorias, equipamentos e serviços da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/publicitario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2458, 'Químico', 'Realiza ensaios, análises químicas e físico-químicas e seleciona metodologias, materiais, reagentes e critérios de amostragem para emitir laudo de análises e elaborar relatórios técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/quimico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2459, 'Químico Industrial', 'Planeja, organiza, padroniza as operações industriais pertinentes a fabricação dos produtos. Responsabiliza-se tecnicamente pelo processo produtivo diante dos CRQs. Desenvolve novos produtos, buscando sempre o emprego de novas matérias primas e tecnologias, com o objetivo de melhorar a competitividade no mercado. Orienta uma equipe específica e técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/quimico-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2460, 'Químico Responsável', 'Supervisiona laboratório de controle de qualidade ou controle de processos químicos industriais, orientando equipe no manuseio de instrumentos, a fim de propiciar bom estado de conservação dos elementos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/quimico-responsavel/');
