INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2519, 'Supervisor Agrícola', 'Coordena equipes, executa planejamento anual de plantio e operações agrícolas.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2520, 'Supervisor Comercial', 'Supervisiona a rotina da equipe, cria o planejamento de vendas,  avalia os profissionais de vendas de produtos e serviços e apresenta resultados. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2521, 'Supervisor Contábil', 'Elabora relatórios gerenciais, análise de balanços, planejamento tributário e preenchimento declarações, como DIPJ, DCTF, DACON e DIRF. Realiza classificação contábil, estudos e análises de viabilidade econômica, conciliação e análise de fechamento de custos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2522, 'Supervisor de Alimentos e Bebidas', 'Supervisiona as áreas de alimentos e bebidas, acompanha a organização do salão, atendimento dos garçons e o tempo de preparo e qualidade dos pratos. Verificar os equipamentos que necessitam de manutenção, realiza compra de matéria-prima e controla o estoque de produtos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-alimentos-e-bebidas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2523, 'Supervisor de Almoxarifado', 'Supervisiona as rotinas de recebimento, estocagem, cadastramento e liberação de materiais. Avalia a necessidade de reposição, encaminhando solicitações de compra e realiza inventário de estoque.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-almoxarifado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2524, 'Supervisor de Arquivo', 'Supervisionar as atividades de arquivo da empresa, desenvolve e implementa procedimentos de organização e classificação de documentos, garantindo o seu adequado armazenamento e conservação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-arquivo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2525, 'Supervisor de Assistência Técnica', 'Supervisiona as atividades de assistência técnica, orienta e distribui trabalhos às equipes de suporte, acompanha atendimento aos clientes e fornecedores e controla entrada e saída de produtos, visando a qualidade no atendimento e cumprimento de prazos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-assistencia-tecnica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2526, 'Supervisor de Atendimento ao Cliente', 'Supervisiona as atividades de atendimento aos usuários dos serviços da empresa, analisa estatísticas do departamento de atendimento. Recebe, organiza e distribui solicitações de clientes aos membros da equipe.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-atendimento-ao-cliente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2527, 'Supervisor de Auditoria', 'Elabora métricas, relatórios de monitoria de performance e  de qualidade. Apresenta cases para melhorias, supervisiona equipe e se reportar a gerência.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-auditoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2528, 'Supervisor de Automação', 'Supervisiona as atividades de automação industrial, acompanha construção e modificação de máquinas, dispositivo e sistemas de produção e controla manutenção elétrica ou mecânica de equipamentos, a fim de manter condições adequadas de funcionamento e maior disponibilidade operacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-automacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2529, 'Supervisor de Back Office', 'Supervisiona equipe de Back Office, controla absenteísmo, TMA, controle de pausas e relatórios. Desenvolve a equipe para resolver as dificuldades dos usuários/clientes. Aplica feedback, acompanha indicadores de qualidade e resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-back-office/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2530, 'Supervisor de Caixa', 'Supervisiona abertura e fechamento de caixa, confere comandas e numerários e acompanha atendimento ao cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-caixa/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2531, 'Supervisor de Caldeiraria', 'Supervisiona operários na construção, montagem e instalações industriais referentes à caldeiraria como: tanques, reservatórios, silos e depósitos. Acompanha a execução dos trabalhos, instruindo e ministrando conhecimentos práticos, a fim de solucionar possíveis problemas. Consulta tabelas, manuais e fórmulas técnicas e utiliza variado número de equipamentos manuais, mecânicos, automáticos e semi-automáticos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-caldeiraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2532, 'Supervisor de Call Center', 'Acompanha volumes de trabalho, produtividade e gráfico. Apoia os treinamentos a alinha os procedimentos, esclarece dúvidas de produtos, sistemas e procedimentos da operação. Assume ligações de solicitações que não foram atendidas, acompanha relatórios dos indicadores, aplica feedback de qualidade, solicita réplicas e também feedbacks pontuais de oportunidades. Acompanha, confere e corrige as marcações de cartão de ponto.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-call-center/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2533, 'Supervisor de Campo', 'Supervisiona, recruta, e treina equipe de vendas em campo, planeja e define as estratégias no negócio e acompanha os indicadores de metas, com objetivo de alcançar os resultados estabelecidos. Pode também atuar na área técnica, acompanhando atividades de manutenção e instalação em telecomunicações, elétrica, mecânica e outros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-campo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2534, 'Supervisor de Cobrança', 'Supervisiona as ações de negociação e recuperação de débitos junto aos clientes. Analisa inadimplências de modo a sugerir ações de redução e estratégias de melhoria no processo. Faz  a gestão e análise dos relatórios gerenciais, realiza o acompanhamento de produtividade da equipe e desenvolve estratégias para alcançar melhores resultados. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2535, 'Supervisor de Comércio Exterior', 'Supervisiona os processos de importação e exportação, acompanha pedidos, administra contratos e negocia com fornecedores e clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-comercio-exterior/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2536, 'Supervisor de Compras', 'Administra fornecedores, negocia políticas comerciais, realiza busca de melhorias e lidera equipe.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2537, 'Supervisor de Condomínio', 'Atua com administração de condomínio em geral. Acompanha orçamentos, compras, contratações de serviços, frequência, registro de faltas e atrasos de funcionários e realização de obra, reformas. Emite relatórios semanais, quinzenais e mensais, executa o planejamento orçamentário mensal e anual e realiza vistorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-condominio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2538, 'Supervisor de Contas', 'Atua no relacionamento com contas, desenvolve negócios e controla as visitas realizadas. Analisa oportunidades de mercado e desempenho da concorrência, avalia posicionamento da marca e acompanha o processo de vendas, a fim de cumprir as metas estabelecidas e alcançar melhores resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-contas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2539, 'Supervisor de Contas a Pagar', 'Desenvolve toda a gestão da área de contas a pagar, analisa os documentos e relatórios da área financeira e assegura o cumprimento das obrigações. Contata e relaciona-se com os bancos, para solucionar pendências relacionadas a pagamentos. Atua com borderôs, elabora e emite relatórios para controle, a fim de subsidiar as decisões gerenciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-contas-a-pagar/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2540, 'Supervisor de Contas a Receber', 'Supervisiona a área de contas a receber, prepara o fluxo de caixa, verifica as notas fiscais e controla a posição de débitos. Elabora relatórios e verifica a documentação relativa aos pagamentos. Acompanha a carteira de inadimplência e cheques devolvidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-contas-a-receber/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2541, 'Supervisor de Contratos', 'Supervisiona contratos de produtos e serviços e acompanha vigência e cumprimento junto aos fornecedores, garantindo o escopo de acordo com os  termos e as condições estabelecidas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-contratos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2542, 'Supervisor de Controladoria', 'Elabora budget e forecast. Avalia e analisa mensalmente em conjunto com o diretor financeiro os itens recebíveis, pagáveis, estoque e capital de giro. Analisa mensalmente os balancetes e demonstrativos de resultados. Elabora e consolida relatórios gerenciais para a diretoria no Brasil e no exterior.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-controladoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2543, 'Supervisor de Controle de Qualidade', 'Implementa e realiza a manutenção do sistema de gestão de qualidade (SGQ). Coordena a implantação da Metodologia 5S dentro da empresa. Executar programas de treinamento do SGQ e programas de qualidade (5S). Realiza ciclos de auditorias internas, desenvolve e coordena os indicadores de qualidade. Análise e tratamento de reclamações e de não conformidades identificadas pelos clientes. Participa de reuniões diárias e mensais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-controle-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2544, 'Supervisor de Costura', 'Supervisiona equipe na preparação de peças, costura e aplicação de aviamentos e etiquetas na produção em série de peças de vestuários, conforme moldes e especificações definidas. Identifica as necessidades de equipamentos e máquinas e implementa melhorias no fluxo de trabalho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-costura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2545, 'Supervisor de Crédito', 'Irá garantir os processos operacionais do crediário, garante o cumprimento das metas estabelecidas e aumento do cadastramento da base de clientes, realiza a gestão de risco de crédito, mobilizar e treinar sua equipe para o melhor atendimento ao cliente gestão comercial, diagnostica e propõe alternativas que melhorem resultados, define planejamento de horários, escalas e banco de horas da equipe.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-credito/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2546, 'Supervisor de Crédito e Cobrança', 'Supervisiona os serviços de crédito e cobrança a fim de recuperar a inadimplência, analisa tráfego e mapeamento nas operações de crediário e controla a régua de cobrança.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-credito-e-cobranca/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2547, 'Supervisor de Crédito Imobiliário', 'Supervisiona a equipe de financiamento imobiliário, acompanha processo de liberação de crédito e solicitação de documentação necessária e desenvolve relacionamento com bancos parceiros.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-credito-imobiliario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2548, 'Supervisor de Custos', 'Supervisiona o fechamento mensal de custos, participa nas projeções e elaboração de orçamentos anuais, analisa ativos fixos e inventários e acompanha resultados da situação econômico-financeira da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-custos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2549, 'Supervisor de Departamento Pessoal', 'Supervisiona administração de pessoal, controlando folha de pagamento, recolhimento de tributos, admissões e rescisões contratuais e férias de colaboradores, a fim de cumprir com a legislação vigente e normas da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-departamento-pessoal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2550, 'Supervisor de E-commerce', 'Supervisona o volume e rentabilidade de vendas, faz análise de giro de mercadorias. Realiza o e-marketing, briefing e cronograma da área e administra as campanhas do Google.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-e-commerce/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2551, 'Supervisor de Elétrica', 'Atua com estruturação de processos de análise de risco das instalações. Realiza proposta de adequação e melhorias, estruturação de acervo técnico, procedimentos operacionais e manutenção de infraestrutura elétrica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-eletrica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2571, 'Supervisor de Lavanderia', 'Supervisiona a equipe de lavanderia e controla entrada, saída e lavagem de roupas do hotel e de hóspedes, a fim de garantir a qualidade dos serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-lavanderia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2572, 'Supervisor de Limpeza', 'Controla, divide e executa as tarefas de conservação e limpeza, faz escalas e planeja o cronograma das atividades. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-limpeza/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2552, 'Supervisor de Engenharia', 'Planeja e acompanha os serviços de manutenção diária, elabora cronograma de todas as atividades, acompanhando sua execução para o cumprimento das metas de produção. Participa da elaboração de projetos, controla a utilização e estado das máquinas e equipamentos. Prepara relatórios gerenciais sobre a atividade de produção, com dados sobre carga de fabricação, metas, desempenho econômico e faturamento. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-engenharia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2553, 'Supervisor de Estamparia', 'Dirige as linhas de estamparia de acordo com a programação diária, acompanha prazos, produtividade e qualidade. Controla os recursos necessários para executar as atividades, como equipamentos, ferramentas e materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-estamparia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2554, 'Supervisor de Eventos', 'Supervisiona a produção de eventos, negocia com fornecedores, acompanha montagem e desmontagem e controla os custos. Analisa concorrência e identifica novas oportunidades de negócios para aumentar as vendas e fidelizar clientes.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2555, 'Supervisor de Expedição', 'Supervisiona a equipe de expedição, acompanhando o cadastramento de entrada e saída de produtos no sistema. Controla o estoque, processos de embarque e transportes para entrega ao cliente final. Verifica e controla notas fiscais e faturamento, faz análise de pedidos e distribui cargas. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-expedicao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2556, 'Supervisor de Facilities', 'Supervisiona equipes na realização de serviços de manutenção predial, recepção, limpeza, telefonia, segurança patrimonial e compras. Acompanha o desempenho dos colaboradores e aplica feedback para constante melhoria na prestação de serviços.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-facilities/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2557, 'Supervisor de Farmácia', 'Garante a organização, coordenação e distribuição das funções. Orienta tecnicamente todas as áreas que compõe o setor de farmácia. Manter o controle de estoque, garantindo a correta recepção, armazenagem e distribuição de medicamentos e correlatos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-farmacia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2558, 'Supervisor de Faturamento', 'Supervisiona a área de faturamento, controla os dados de produtos a serem expedidos, como tipo, quantidade e valor e acompanha legislação vigente para calcular os impostos a serem lançados nas notas fiscais de venda ou transferência.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-faturamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2559, 'Supervisor de Ferramentaria', 'Coordena a área de ferramentaria. Distribui atividades de manutenção, de acordo com os pedidos de reparos, acompanha o setor de usinagem da ferramentaria. Desenha e construi as peças de acordo com a necessidade das áreas. Programa e opera o centro de usinagem através de programa específico. Realiza orçamento junto aos fornecedores de peças que não podem ser produzidas internamente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-ferramentaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2560, 'Supervisor de Filial', 'Supervisiona as atividades de vendas da filial e distribui as tarefas para a equipe, visando assegurar a realização dentro dos padrões requeridos. Acompanha o faturamento e logística e elabora relatórios para a matriz com os resultados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-filial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2561, 'Supervisor de Folha de Pagamento', 'Supervisiona lançamentos em folha de pagamento, controla o cálculo de tributos e pagamento de obrigações trabalhistas e previdenciárias e elabora relatórios referentes a despesas de pessoal, previsões de pagamento de salários, férias, rescisões e custos com benefícios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-folha-de-pagamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2562, 'Supervisor de Frota', 'Coordena e supervisiona os processos relativos a administração e disponibilização dos veículos da frota. Acompanha e presta suporte as operações nos pátios instalados nas dependências das plantas. Realiza a implementação de novos processos, recebimento e preparação dos carros novos e controle de veículos, abastecimento, lavagem, movimentação e utilização da frota.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-frota/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2563, 'Supervisor de Fundição', 'Supervisiona as atividades do setor de fundição, acompanha a execução de moldes e vazamento de fornos, controla qualidade do metal e analisa necessidade de recursos técnicos e humanos para cumprir os cronogramas estabelecidos, custos e qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-fundicao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2564, 'Supervisor de Garantia da Qualidade', 'Supervisiona os processos de garantia da qualidade, acompanha auditoria dos requisitos de qualidade, implementa instruções de trabalho e promove treinamento aos colaboradores, para garantir a aplicação dos padrões da empresa e boas práticas de fabricação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-garantia-da-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2565, 'Supervisor de Hotelaria', 'Supervisiona os serviços prestados aos hóspedes, lidera equipe de hotelaria e aplica treinamento a novos colaboradores. Negocia com fornecedores e acompanha inventários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-hotelaria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2566, 'Supervisor de Infraestrutura', 'Planeja e supervisiona as atividades de manutenção preventiva e corretiva em máquinas, equipamentos, utilidades, rede de energia e instalações predial. Pesquisa e identifica novas tecnologias aplicáveis para o ambiente da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-infraestrutura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2567, 'Supervisor de Injeção Plástica', 'Supervisiona operação de máquina injetora, desenvolvendo melhorias nos processos de fabricação de plásticos, acompanha o cumprimento de programas e metas e controla regulagem de máquinas e moldes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-injecao-plastica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2568, 'Supervisor de Instalação', 'Supervisiona equipe na instalação de sistemas, orienta sobre adequações industriais e prediais, equipamentos e máquinas, elabora escalas, acompanha qualidade dos serviços realizados e controla compra de materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-instalacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2569, 'Supervisor de Inteligência de Mercado', 'Supervisiona equipe nas rotinas de análise competitiva, tendências e de perfil de concorrentes, monitoria de performance e estudo de mercado, com objetivo de prestar suporte de informações as áreas de Marketing e Vendas para lançamento de produtos e serviços e estimativa de negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-inteligencia-de-mercado/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2570, 'Supervisor de Laboratório', 'Distribui e controla a realização de atividades de laboratório de pesquisa e desenvolvimento e controle de qualidade, analisa registros de ocorrências técnicas operacionais e avalia condições de equipamentos e acessórios. Planeja as calibrações nos equipamentos, elabora planos e propostas e definindo metas. Garante o controle de qualidade do processo, laboratório, formulação do produto, garante que a execução dos procedimentos sejam cumpridos conforme norma vigente. Realiza ações preventivas e corretivas dos processos internos ligados a qualidade.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-laboratorio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2573, 'Supervisor de Locação', 'Supervisiona o atendimento a clientes e acompanha todo o processo de cadastramento, negociação e de abertura e fechamento de contratos de locação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-locacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2574, 'Supervisor de Logística', 'Administra e planeja as atividades dos setores de expedição e logística. Supervisiona o processo de descarga, conferência, movimentação, armazenagem, controle de vencimento e acondicionamento no estoque. Acompanha as trocas, devoluções, avarias, emissão de documentos fiscais e faz gestão de pessoas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-logistica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2575, 'Supervisor de Loja', 'Supervisiona a equipe, acompanha rotinas de vendas, define escalas e horários, organiza loja e confere entrada e saída de mercadorias, com objetivo de cumprir as regras estabelecidas pela empresa. Identifica a necessidade de programas de treinamento e participa no planejamento de estratégias de promoção e propaganda.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2576, 'Supervisor de Manutenção', 'Acompanha as atividades de manutenção corretiva e preventiva de instalações, equipamentos e máquinas. Elabora procedimentos de trabalho e plano de manutenção para identificar as deficiências operacionais e funcionais e atende as necessidades de infraestrutura.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-manutencao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (605, 'Auxiliar de Impressão', 'Auxilia nos processos de impressão e abastece máquinas com suprimentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-de-impressao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2577, 'Supervisor de Manutenção Automotiva', 'Supervisiona as atividades de manutenção preventiva e corretiva de veículos, administra recursos materiais, financeiros e mão de obra e acompanha ordens de serviços, identificando melhorias no processo e necessidade de substituição de peças e ferramentas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-manutencao-automotiva/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2578, 'Supervisor de Manutenção Elétrica', 'Supervisiona profissionais especializados na execução de consertos, reparos, montagens, construções, instalações, adaptações e modificações nas máquinas, equipamentos industriais, chaves elétricas, manuais, mecânicas e automáticas, painéis de comando à distância e instrumentação geral da empresa. Interpreta e ministra conhecimentos de desenhos técnicos, cálculos de oficina, normas e aplicação de materiais, relativos à medidas de precisão. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-manutencao-eletrica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2579, 'Supervisor de Manutenção Eletromecânica', 'Supervisiona as atividades de manutenção eletromecânica preventiva e corretiva, de acordo com metas e normas de segurança, saúde e qualidade, administra recursos materiais, técnicos e financeiros e revisa planos de inspeções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-manutencao-eletromecanica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2580, 'Supervisor de Manutenção Industrial', 'Executa manutenções preventivas e corretivas nas tubulações e nos equipamentos mecânicos. Contribui com a conservação do patrimônio e da segurança operacional da filial, realiza inspeções e vistorias periódicas, monitora os sistemas de proteção e emergência e atuando em situações necessárias. Acompanha os serviços de manutenção realizados por terceiros e disponibiliza informações relativas aos equipamentos e as áreas produtivas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-manutencao-industrial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2581, 'Supervisor de Manutenção Mecânica', 'Supervisiona as atividades de manutenção mecânica preventiva e corretiva, de acordo com metas e normas de segurança, saúde e qualidade, administra recursos materiais, técnicos e financeiros e revisa planos de inspeções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-manutencao-mecanica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2582, 'Supervisor de Manutenção Predial', 'Supervisiona equipe nas atividades de infraestrutura, envolvendo instalação e manutenção predial. Elabora os procedimentos de trabalho para manutenção corretiva e preventiva. Acompanha o recebimento e a inspeção de materiais para manutenção e a qualidade das instalações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-manutencao-predial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2583, 'Supervisor de Marketing', 'Atua com planejamento anual de metas, monitoramento do orçamento de marketing, comunicação com cliente e confecção de campanhas e comunicados internos. Conduz assessoria de imprensa e eventos, responde pelo endomarketing e mídia social, com envio de comunicados. Elabora e desenvolve a operacionalização do planejamento estratégico para se atingir o mercado e público alvo. Traz resultados e realiza relatórios gerenciais diários, semanais e mensais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-marketing/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2584, 'Supervisor de Meio Ambiente', 'Supervisiona equipe de meio ambiente composta de colaboradores e prestadores de serviço da área ambiental. Garante o atendimento aos clientes internos e externos visando. Atende as normas e procedimentos ambientais da empresa bem como a legislação ambiental vigente em nível federal, estadual e/ou municipal.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-meio-ambiente/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2585, 'Supervisor de Merchandising', 'Supervisiona a equipe de promotores em pontos de vendas no atendimento ao cliente e demonstração de produtos. Estuda e identifica o melhor posicionamento e exposição das mercadorias, organiza materiais de merchandising e analisa resultados para desenvolver estratégias no alcance das metas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-merchandising/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2586, 'Supervisor de Mineração', 'Supervisiona os processos de extração de minérios, desde perfuração e desmonte até classificação e lavagem. Inspeciona áreas de mineração, avalia riscos da atividade e gerencia recursos humanos, financeiros e materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-mineracao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2587, 'Supervisor de Monitoramento', 'Supervisiona as atividades de monitoramento de alarmes, sinais, veículos, residências e instalações por meio de equipamentos eletrônicos de segurança. Elabora escala de horários dos colaboradores, controla férias e avalia desempenho.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-monitoramento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2588, 'Supervisor de Monitoria', 'Elabora métricas, planilha de monitoria de performance e monitoramento de qualidade. Realiza monitoria de ligações, apresentação de resultados, gera relatórios, amostra para monitoria, calibração com supervisores/cliente, reunião de alinhamento de processos e ações motivacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-monitoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2589, 'Supervisor de Montagem', 'Supervisiona as atividades da área de produção como: controle de produção, manutenção e fabricação dos equipamentos e peças, a fim de assegurar o cumprimento das metas definidas, dentro dos padrões de qualidade, custos e segurança estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-montagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2590, 'Supervisor de Negócios', 'Identifica e prospecta oportunidades de negócios, estuda tendências, concorrência e viabilidade de investimento em novos mercados e participa na elaboração da estratégia comercial.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-negocios/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2591, 'Supervisor de Nutrição', 'Supervisão da unidade de nutrição clínica. Coordena atividades técnicas da unidade, orienta o trabalho do pessoal auxiliar, técnicos e nutricionistas. Acompanha o preparo e distribuição das refeições, recebimento e armazenagem dos gêneros alimentícios, para possibilitar melhor rendimento do serviço.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-nutricao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2592, 'Supervisor de Obras', 'Supervisiona e programa as atividades dos trabalhadores que trabalham com construção civil, controla a segurança para o desenvolvimento das atividades na construção de edificações e obras de engenharia. Monitora a obra, verifica qualidade, fluxo e movimentação de materiais e insumos utilizados. Elabora documentação técnica, relatórios de controle, administra o cronograma da obra, treinar equipes, contratar e recomenda promoção de trabalhadores. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-obras/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2593, 'Supervisor de Operações', 'Supervisiona equipe nas operações de produção e logística, controla a expedição, recebimento, armazenamento e distribuição de mercadorias. Acompanha desempenho dos colaboradores e aplica treinamentos, a fim de garantir a melhoria contínua do fluxo operacional.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-operacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2594, 'Supervisor de Ouvidoria', 'Supervisiona e acompanha as reclamações dos clientes, postadas pelos diversos canais da empresa. Identifica melhorias no processo e sugere mudanças nos fluxos operacionais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-ouvidoria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2595, 'Supervisor de Patrimônio', 'Administra patrimônio da empresa, por meio da gestão de estoque, catalogação e inventário de itens físicos, garantindo o controle das informações para auditorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-patrimonio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2596, 'Supervisor de PCP', 'Supervisiona o fluxo do processo de produção, estuda eficiência, apontamento de perdas, acompanha programação de máquinas de acordo com a demanda e administra movimentações de materiais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-pcp/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2597, 'Supervisor de Peças', 'Planeja o trabalho da equipe de vendas e analisa potencial de mercado. Controla os processos de compras de peças, estoque e inventário e acompanha entrada e saída de mercadorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-pecas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2598, 'Supervisor de Pintura', 'Atua com supervisão de pinturas dos equipamentos. Efetua testes de tintas em desenvolvimento. Identifica as melhorias nos processos de pintura e seus meios produtivos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-pintura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2599, 'Supervisor de Planejamento', 'Acompanha a elaboração das necessidades para o desenvolvimento de projetos. Faz o acompanhamento da contratação de terceiros. Acompanha o progresso de engenharia, suprimentos e demais áreas de suporte. Fiscaliza e interage com as equipes de gerenciamento. Informa e solicita à diretoria sobre recursos e despesas. Direciona conforme solicitações corretivas. Define e acompanha marcos de projeto / procedimentos, elabora relatórios gerenciais mensais e semanais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-planejamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2600, 'Supervisor de Pós-venda', 'Supervisiona o atendimento prestado aos clientes, soluciona divergências de pedidos e desenvolve melhorias no processo de pós-venda, com objetivo de fidelizar e assegurar os padrões estabelecidos pela empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-pos-venda/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2601, 'Supervisor de Prevenção de Perdas', 'Supervisiona os serviços de prevenção contra furtos, perdas e extravios de mercadorias, por meio de monitoramento eletrônico e define ações corretivas de ambientes vulneráveis. Acompanha treinamento de fiscalização de loja e controle de equipamentos de segurança e elabora relatórios periódicos para apresentar eventos ocorridos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-prevencao-de-perdas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2602, 'Supervisor de Processos', 'Implementa conceituação de processos, define processos de montagem e fluxo de processo, identificando gaps de processos e atividades sem valor agregado. Audita processos produtivo. Implanta KPI de processos. Desenvolve os métodos de otimização das atividades da área. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-processos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2603, 'Supervisor de Produção', 'Supervisiona as atividades de produção da fábrica, controla o volume a ser produzido, custos e qualidade e acompanha a eficiência da mão de obra. Elabora relatórios sobre o desempenho de produção e promove o uso adequado das instalações e equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-producao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2604, 'Supervisor de Produtos', 'Supervisiona e realiza pesquisa de mercado, faz análise comparativa dos produtos.  Acompanha projetos de desenvolvimento, cronogramas e lançamento de produtos. Atua com produção de propostas técnicas e comerciais, relacionados aos seus produtos. Avalia o ciclo de vida dos produtos e propõe ações de acordo com os resultados.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2605, 'Supervisor de Projeto Social', 'Supervisiona e realiza acompanhamento das atividades. Busca parcerias com organizações sociais, empresas e promove a integração entre elas para fidelizá-las. Apoia processos de seleção da equipe e planejamento da capacitação. Produz relatórios, apoia e acompanha a equipe na execução das atividades previstas na metodologia.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-projeto-social/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2606, 'Supervisor de Projetos', 'Supervisiona projetos, planeja sua execução e acompanha escopo estabelecido e o progresso das rotinas, a fim de cumprir metas, prazos e custos estabelecidos. Identifica os riscos para estudar formas de minimizar impactos e corrigir ações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-projetos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2607, 'Supervisor de Qualidade', 'Supervisiona as atividades de inspeção e controle de qualidade de produtos e matérias-primas, analisa e levanta procedimentos, elabora indicadores e acompanha a aplicação de ações corretivas e preventivas nos processos internos, a fim de garantir o cumprimento das normas de qualidade e políticas da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2608, 'Supervisor de Radiologia', 'Atua com elaboração do plano de radioproteção, implantação do serviço de radioproteção e gestão de equipes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-radiologia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2609, 'Supervisor de Recepção', 'Realiza o acompanhamento das atividades diárias, aplica treinamentos aos colaboradores e elabora escala de folga, a fim de cumprir as normas e garantir a satisfação dos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-recepcao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2610, 'Supervisor de Recrutamento e Seleção', 'Supervisiona a equipe de recrutamento e seleção, controle de indicadores de seleção, elaboração de relatórios e implantação de projetos de aprendizes, trainee e inclusão de pessoas com deficiência.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2611, 'Supervisor de Recursos Humanos', 'Supervisiona as atividades de recursos humanos envolvendo recrutamento e seleção, administração salarial, treinamento, folha de pagamento, benefícios e registros de ponto eletrônico.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2612, 'Supervisor de Refrigeração', 'Supervisiona equipe na manutenção corretiva e preventiva e instalação de equipamentos de refrigeração e ar condicionado e controla peças em estoque e programa de compras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-refrigeracao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2613, 'Supervisor de Relacionamento', 'Analisa indicadores e produtividade da equipe, identificando as causas dos indicadores negativos, para tomada de ações corretivas, de forma a fazer cumprir as metas traçadas para o departamento. Avalia situações de conflito, registra e documenta todas as anomalias do atendimento para tomada de decisões para reversão de resultados negativos e otimização de processos. Acompanha os resultados pós-treinamentos, verificando se houve alteração na performance dos profissionais e definindo junto a gerência novas ações em casos negativos. Participa do processo seletivo e da contratação de profissionais para a área.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-relacionamento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2615, 'Supervisor de Restaurante', 'Supervisiona as rotinas do restaurante, acompanha abertura e fechamento de caixa, realiza inventário e controla equipamentos, utensílios e mercadorias. Lidera equipes, controla folgas e escalas dos funcionários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-restaurante/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2616, 'Supervisor de SAC', 'Supervisiona as atividades de atendimento aos usuários. Analisa estatísticas do departamento, faz o acompanhamento das monitorias realizadas e aplica feedback.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2617, 'Supervisor de Segurança do Trabalho', 'Supervisiona o desenvolvimento e a implantação de políticas de saúde e segurança no trabalho, realiza auditoria e avalia riscos de ambientes organizacionais. Identifica as variáveis de controle de doenças ocupacionais, acidentes e qualidade de vida, elaborando ações educativas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-seguranca-do-trabalho/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2618, 'Supervisor de Segurança Patrimonial', 'Supervisiona e orienta a execução do serviço de vigilância para encontrar e corrigir anormalidades ou solucionar problemas. Prepara escalas de trabalho e treinamentos para a equipe, a fim de aprimorar sua capacitação técnica.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2619, 'Supervisor de Seguros', 'Supervisiona equipe no fechamento e renovação de seguros, elabora processos de implantação de novos contratos, desenvolve estratégias de vendas na identificação de novos negócios e acompanha resultados e mensuração da qualidade dos serviços prestados.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-seguros/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2620, 'Supervisor de Serviços Gerais', 'Atua na compra, distribuição e controle de materiais e produtos de limpeza disponibilizados para os funcionários em suas tarefas. Passa as orientações, treina e acompanha a execução dos serviço.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-servicos-gerais/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2621, 'Supervisor de Suprimentos', 'Acompanha todas as rotinas de compra e orçamentos para projetos. Recebe solicitações de compras. Solicita cotações junto a fornecedores, efetua compras diretas, acompanha cotações para inclusão de projetos nas esferas públicas e particulares. Negocia diretamente com os fornecedores, emite ordem de compra para aprovação da superintendência e analisa contratos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-suprimentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2622, 'Supervisor de Tecnologia da Informação', 'Supervisiona as atividades da área de Tecnologia da Informação, envolvendo a elaboração de projetos de implantação, de acordo com as necessidades do negócio, redesenho de processos, desenvolvimento e integração de sistemas e suporte de rede.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-tecnologia-da-informacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2623, 'Supervisor de Tesouraria', 'Supervisiona as atividades de contas a pagar e tesouraria, prepara as previsões de caixa, negocia despesas bancárias e verifica a exatidão de toda a documentação relativa a pagamentos. Controla os contratos de prestação de serviços, prepara o fluxo de caixa diário e semanal e elabora documentação para procedimentos de cartório.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-tesouraria/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2624, 'Supervisor de Trade Marketing', 'Supervisiona equipe de vendas em marketing, acompanha resultados, motivações e capacitação técnica da equipe, desdobrando objetivos, análise e planejamento de mercado. Desenvolve, executa e acompanha ações promocionais.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2625, 'Supervisor de Transportes', 'Supervisiona toda a logística de transporte, atua com contratação e cotação de frete, verifica  custos logísticos e elabora planilhas e relatórios. Analisa e propõe soluções para os problemas da área, coleta dados, divulga e implementa melhorias nos indicadores de desempenho e monitora o desempenho dos motoristas. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-transportes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2626, 'Supervisor de Treinamento e Desenvolvimento', 'Lidera a equipe e assegura o planejamento das atividades de treinamento e desenvolvimento. Coordena pesquisas de clima organizacional. Elabora e assegura o cumprimento do planejamento orçamentário. Implementa treinamentos e programas de desenvolvimento. Prepara e ministra reuniões.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-treinamento-e-desenvolvimento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2627, 'Supervisor de Usinagem', 'Supervisiona atividades de usinagem na transformação de peças e metais, realiza leitura e interpretação de desenhos e acompanha métodos e processos produtivos e qualidade dos produtos, visando assegurar os objetivos da empresa.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-usinagem/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2628, 'Supervisor de Vendas', 'Supervisiona as equipes de vendas, acompanha os pedidos dos clientes e identifica suas necessidades, para definir a melhor forma de atendimento. Acompanha negociação de preços e prazos de entrega e estabelece metas para cumprimento dos objetivos da área comercial.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-de-vendas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2629, 'Supervisor Financeiro', 'Elabora orçamentos e demonstrativos financeiros e econômicos, projeta receitas, coleta dados da empresa, compara valores estimados com realizados, analisa objetivos e metas definidas. Administra recursos financeiros e desenvolve relacionamento com instituições financeiras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-financeiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2630, 'Supervisor Florestal', 'Supervisiona e fiscaliza atividades florestais, como construção de viveiros, produção de mudas, colheita e manejo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-florestal/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2632, 'Supervisor Pedagógico', 'Planeja, supervisiona e avalia o desenvolvimento dos projetos de educação profissional. Orienta, acompanha e avalia o trabalho dos instrutores. Distribui o material pedagógico para professores, controle da entrada e saída de professores, faz o recolhimento de cadernos de roteiro dos professores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-pedagogico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (606, 'Auxiliar de Informática', 'Auxilia na montagem e manutenção de computadores, instala softwares e presta suporte aos usuários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/auxiliar-de-informatica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2633, 'Supervisor Técnico', 'Supervisiona toda a equipe técnica na execução dos serviços, para que se cumpra os procedimentos internos e normas de conduta. Participa de reuniões de planejamento e segurança e elabora relatórios de serviços. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2634, 'Supervisor Tributário', 'Supervisiona a legislação fiscal da empresa para correta apuração de tributos e preenchimento das obrigações acessórias, garantindo melhor planejamento na redução de riscos e da carga tributária.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/supervisor-tributario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2635, 'Suporte Técnico', 'Presta suporte técnico para instalação, configuração e manutenção de software e hardware, realiza testes de funcionamento e acompanha o desempenho dos recursos técnicos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/suporte-tecnico/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2636, 'Sushiman', 'Prepara shushis e sashimis, além de vários tipos de refeições típicas do Japão. Realiza limpeza e conservação de peixes e frutos do mar, verificando a qualidade dos alimentos. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/sushiman/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2637, 'Tapeceiro', 'Faz tapetes de tecidos manualmente, compondo ou não figuras de todo tipo, deixando a imagem esboçada pelos mesmos fios da textura utilizada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tapeceiro/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2638, 'Tapeceiro de Veículos', 'Realiza montagem e desmontagem de estofamento de veículos, substituindo e recuperando revestimentos internos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tapeceiro-de-veiculos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2639, 'Tecelão', 'Prepara trama, urdimento, engomação e remeteção de fios e fabrica tecidos planos e de malha, opera urdideira, engomadeira e teares retilíneos e circulares. Controla o cozimento da goma, temperatura, viscosidade e solidez da engomagem dos fios, corrigindo defeitos de operação das máquinas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecelao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2640, 'Técnico Administrativo', 'Realiza atividades de apoio administrativo, organiza arquivo, elabora documentos e planilhas e estabelece contato com clientes e fornecedores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-administrativo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2641, 'Técnico Agrícola', 'Auxilia os produtores rurais oferecendo ajuda técnica para desenvolverem sua produção. Prepara o solo, plantio, combate a pragas e colheita. Faz análise para verificar possíveis pontos que devem ser melhorados na produção e aplica soluções pontuais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-agricola/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2642, 'Técnico Bancário', 'Processa operações de crédito, investimento e serviços bancários. Controla as operações de concessão de crédito, investimento e serviços a pessoas físicas ou jurídicas, a fim de cumprir e faz cumprir as normas e regras internas e de órgãos regulamentadores. Atende aos demais setores do banco, como seus clientes internos e a órgãos governamentais. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-bancario/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2643, 'Técnico Comercial', 'Analisa concorrência e mercado para identificar as necessidades dos clientes, participa na promoção da venda e elabora propostas comerciais. Acompanha o atendimento, venda, dúvidas e reclamações dos clientes, a fim de fidelizar e propor melhorias.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-comercial/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2644, 'Técnico Contábil', 'Realiza análise e conciliação de contas, classifica e contabiliza as despesas, receitas e movimentação financeira. Elabora quadros demonstrativos, relatórios e tabelas, compilando dados contábeis. Participa da elaboração de balancetes e balanços, aplicando normas contábeis.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-contabil/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2645, 'Técnico da Qualidade', 'Desenvolve manuais, procedimentos e instruções de trabalho, participa no planejamento das auditorias e elabora plano de ações preventivas e corretivas para cumprir os padrões de qualidade estabelecidos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-da-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2646, 'Técnico de Arquivo', 'Realiza classificação, registro e organização de documentos, de acordo com padrão da empresa e administra entrada e saída de arquivos para garantir sua conservação e controle das informações.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-arquivo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2647, 'Técnico de Assistência Técnica', 'Realiza reparo e manutenção preventiva e corretiva nos equipamentos, de acordo com as normas e procedimentos da empresa. Presta atendimento aos clientes para solução de problemas complexos, garantindo o suporte técnico para solução. ', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-assistencia-tecnica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2648, 'Técnico de Atendimento', 'Atende clientes, analisa resultados e relatórios. Recebe, confere e edita planilhas, apoia e monitora a operação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-atendimento/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2649, 'Técnico de Campo', 'Desenvolve atividades em campo no diagnóstico de defeitos, instalação e manutenção corretiva e preventiva de equipamentos e sistemas. Identifica problemas e necessidades dos clientes, a fim de melhorar a performance dos equipamentos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-campo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2650, 'Técnico de Celular', 'Faz consertos, manutenção de celulares, troca, reparo de peças, atualização de sistemas e desbloqueio de aparelhos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-celular/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2651, 'Técnico de Controle de Qualidade', 'Analisa qualidade de produtos acabados, de acordo com política definida pela empresa, acompanha indicadores de desempenho e elabora procedimentos, a fim de garantir conformidade com os padrões técnicos e identificar deficiências dos processos.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-controle-de-qualidade/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2652, 'Técnico de Equipamentos', 'Realiza manutenção corretiva e preventiva de equipamentos, identifica e analisa falhas de funcionamento, a fim de garantir o perfeito estado de utilização e conservação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-equipamentos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2653, 'Técnico de Equipamentos Médicos', 'Realiza manutenção corretiva e preventiva de equipamentos médicos e laboratoriais, a fim de garantir o perfeito estado de utilização e conservação.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-equipamentos-medicos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2654, 'Técnico de Eventos', 'Presta apoio técnico e logístico no planejamento, organização e realização de eventos e cerimoniais, formula orçamentos e capta patrocinadores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-eventos/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2655, 'Técnico de Gasoterapia', 'Instala e revisa equipamentos gasoterápicos e controla o uso e consumo de gases medicinais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-gasoterapia/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2656, 'Técnico de Gesso', 'Confecciona e retira aparelhos e talas gessadas como goteiras e enfaixamentos com o uso de material convencional e sintético.  Presta suporte ao médico ortopedista na instalação de trações esqueléticas e nas manobras de redução manual.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-gesso/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2657, 'Técnico de Help Desk', 'Auxilia nas atividades de infraestrutura de TI, mantendo a disponibilidade dos sistemas e atendendo às políticas de segurança da empresa. Presta suporte ao ambiente interno, instalação e configuração de sistemas operacionais, redes e impressoras e administra e monitora servidores.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-help-desk/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2659, 'Técnico de Implantação', 'Implanta sistemas de tecnologia da informação e telefonia,  de acordo com as necessidades dos clientes e presta suporte para solucionar dúvidas ou problemas de usuários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-implantacao/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2660, 'Técnico de Impressora', 'Realiza instalação e manutenção corretiva e preventiva de impressoras e multifuncionais e presta suporte técnico aos usuários.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-impressora/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2661, 'Técnico de Infraestrutura', 'Elabora e implanta projetos de redes, instala, testa e realiza manutenções preventiva e corretiva de sistemas. Repara equipamentos e presta assistência técnica aos clientes.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-infraestrutura/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2662, 'Técnico de Instalação', 'Faz instalação e manutenção de equipamentos e sistemas, mantém contato com clientes e realiza visita técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-instalacao/');
