INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2665, 'Técnico de Manutenção de Computadores', 'Executa configurações de sistema operacional, realiza limpeza e conservação dos equipamentos. Faz testes preventivos operacionais e de segurança nos equipamentos e demais periféricos. Realiza atendimentos de manutenção em postos externos, faz emissão de relatórios técnicos de visitas e laudos de atendimento de manutenção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/tecnico-de-manutencao-de-computadores/');
