INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2820, 'Trocador de Moldes', 'Regula máquinas no setor de injeção, fazendo ajustes nos comandos e verificando a qualidade dos produtos. Responsável pela substituição dos moldes seguindo determinações da programação de produção.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/trocador-de-moldes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2821, 'Turismólogo', 'Planeja, organiza e promove viagens, eventos e atividades de lazer e negócios. Vende passagens, reserva hotéis e programa de passeios e excursões. Realiza levantamentos socioeconômicos e culturais na área de turismo.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/turismologo/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2822, 'Vendedor', 'Realiza atendimento a clientes, negocia preço, prazo, condições de pagamento e descontos da venda, orienta quanto às especificações dos produtos e/ou serviços. Controla os pedidos dos clientes, qualidade dos produtos e prazo de entrega estabelecido. Auxilia os clientes na escolha, demonstra funcionamento, oferece degustação ou distribui amostras.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2823, 'Vendedor de Consórcio', 'Realiza venda interna ou externa de consórcio de caminhões, automóveis, motocicletas e imóveis, elabora propostas comerciais e agenda visitas para demonstração do produto e fechamento de negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-consorcio/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2824, 'Vendedor de E-commerce', 'Vende produtos por e-commerce e atende clientes da loja virtual por telefone, chat ou e-mail para auxílio na finalização de pedidos ou em dúvidas gerais. Cadastra produtos no site e participa na elaboração de estratégias comerciais.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-e-commerce/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2825, 'Vendedor de Franquias', 'Prospecta e negocia com clientes a venda de franquias, estuda local para abertura do estabelecimento e acompanha a implantação e inauguração de novas unidades.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-franquias/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2826, 'Vendedor de Frete', 'Vende serviços de transporte de cargas nacionais ou internacionais nos modais aéreo, terrestre ou marítimo, administra carteira, elabora propostas comerciais e estuda a melhor logística para o cliente.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-frete/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2827, 'Vendedor de Informática', 'Vende produtos e soluções em informática, esclarece dados técnicos e avalia o perfil e a necessidade do cliente ou empresa para apresentar o equipamento ou serviço mais adequado.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-informatica/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2828, 'Vendedor de Loja', 'Vende mercadorias em estabelecimentos do comércio varejista ou atacadista. Faz demonstração e degustação e distribui amostras. Controla estoque e realiza inventário de produtos para reposição.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-loja/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2829, 'Vendedor de Máquinas', 'Realiza atendimento aos clientes para venda de máquinas, orienta quanto às especificações e demonstra funcionamento. Elabora orçamentos, acompanha pedidos e presta consultoria técnica.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-maquinas/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2830, 'Vendedor de Seguros', 'Prospecta e atende clientes para venda de seguros, realiza cálculos e orçamentos e acompanha o processo até a emissão da apólice.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-seguros/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2831, 'Vendedor de Telecomunicações', 'Realiza a venda de aparelhos, acessórios e serviços de telefonia e dados, esclarece informações técnicas e presta suporte aos clientes na definição da solução adequada.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-telecomunicacoes/');
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2832, 'Vendedor de Veículos', 'Atende clientes, identifica as necessidades e orienta com informações referentes ao veículo. Realiza o fechamento da venda e acompanha as práticas comerciais da concorrência, a fim de identificar oportunidade de novos negócios.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-de-veiculos/');
