INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2836, 'Vendedor Técnico', 'Realiza atendimento aos clientes para orientar quanto às especificações técnicas dos produtos. Presta suporte e aplica treinamento quanto a funcionalidade. Acompanha os pedidos dos clientes, a fim de garantir a qualidade do produto dentro das especificações técnicas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/vendedor-tecnico/');
