INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (985, 'Coordenador Industrial', 'Coordena as atividades industriais, distribui e controla a realização de atividades de manutenção, analisa registros de ocorrências técnicas e operacionais e avalia as condições das máquinas e equipamentos. Planeja manutenções, elabora planos e propostas. Coordena equipes de trabalho, administra recursos humanos e aquisição de recursos materiais para manutenções.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/coordenador-industrial');
