INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2844, 'Vigilante Líder', 'Orienta os colaboradores, verifica situações e mantém controle de entrada e saída de pessoas. Cuida da segurança do patrimônio e dos frequentadores do local e realiza rondas de segurança internas e externas, cobertura dos postos de vigilância no caso de substituição, ausências ou necessidades de apoio. Verifica escalas, acompanha e passa normas da área.
INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2845, 'Visitador de Navios', 'Elabora relatórios das condições da embarcação, tripulação e carga e controla documentos para liberação da atração do navio perante as autoridades. Coordena a entrega de provisões de bordo ou peças de reparo, troca de tripulação e demais problemas.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/visitador-de-navios/');
