INSERT INTO public.vaga (vag_id, vag_nome, vag_descricao, vag_criadoem, vag_fonte) VALUES (2850, 'Web Designer', 'Executa o tratamento de imagens para inclusão no site, criação de banners para o site e para campanhas de trade marketing. Cria graficamente o visual da página das aplicações com imagens e textos., desenvolve a navegação da página. Cria banners, folders, cartões e gifs animados com identidade visual.', '2017-08-26 08:58:24.440193', 'https://www.catho.com.br/profissoes/web-designer/');
